  状态_In      就诊登记记录.状态%Type := 0,
  医疗类别_In  就诊登记记录.医疗类别%Type := Null,
  帐户余额_In  就诊登记记录.帐户余额%Type := 0,
  病种id_In    就诊登记记录.病种id%Type := Null,
  病种名称_In  就诊登记记录.病种名称%Type := Null,
