    Values
      (险类_In, 病人id_In, 主页id_New, 就诊时间_In, 状态_In, 医疗类别_In, 帐户余额_In, 病种id_In, 病种名称_In, 并发症_In, Ic卡信息_In, His流水号_In,
       Yb流水号_In, 备注_In);
  End If;
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_就诊登记记录_Update;
/

--79521:马政,2014-12-05,卫材批准文号修改
Create Or Replace Procedure Zl_材料外购_Insert
(
  No_In         In 药品收发记录.No%Type,
  序号_In       In 药品收发记录.序号%Type,
  库房id_In     In 药品收发记录.库房id%Type,
  供药单位id_In In 药品收发记录.供药单位id%Type,
  材料id_In     In 药品收发记录.药品id%Type,
  产地_In       In 药品收发记录.产地%Type := Null,
  批号_In       In 药品收发记录.批号%Type := Null,
  生产日期_In   In 药品收发记录.生产日期%Type := Null,
  效期_In       In 药品收发记录.效期%Type := Null,
  灭菌日期_In   In 药品收发记录.灭菌日期%Type := Null,
  灭菌效期_In   In 药品收发记录.灭菌效期%Type := Null,
  实际数量_In   In 药品收发记录.实际数量%Type := Null,
  成本价_In     In 药品收发记录.成本价%Type := Null,
  成本金额_In   In 药品收发记录.成本金额%Type := Null,
  扣率_In       In 药品收发记录.扣率%Type := Null,
  零售价_In     In 药品收发记录.零售价%Type := Null,
  零售金额_In   In 药品收发记录.零售金额%Type := Null,
  差价_In       In 药品收发记录.差价%Type := Null,
  零售差价_In   In 药品收发记录.差价%Type := Null,
  摘要_In       In 药品收发记录.摘要%Type := Null,
  注册证号_In   In 药品收发记录.注册证号%Type := Null,
  填制人_In     In 药品收发记录.填制人%Type := Null,
  随货单号_In   In 应付记录.随货单号%Type := Null,
  发票号_In     In 应付记录.发票号%Type := Null,
  发票日期_In   In 应付记录.发票日期%Type := Null,
  发票金额_In   In 应付记录.发票金额%Type := Null,
  填制日期_In   In 药品收发记录.填制日期%Type := Null,
  核查人_In     In 药品收发记录.配药人%Type := Null,
  核查日期_In   In 药品收发记录.配药日期%Type := Null,
  批次_In       In 药品收发记录.批次%Type := 0,
  退货_In       In Number := 1,
  高值材料_In   In Varchar2 := Null,
  商品条码_In   In 药品收发记录.商品条码%Type := Null,
  内部条码_In   In 药品收发记录.内部条码%Type := Null,
  费用id_In     In 药品收发记录.费用id%Type := 0,
  发票代码_In   In 应付记录.发票代码%Type := Null,
  财务审核_In   In Number := 0,
  批准文号_In   In 药品收发记录.批准文号%Type := Null
) Is
  Err_Item Exception;
  v_Err_Msg Varchar2(200);

  v_No         应付记录.No%Type; --应付记录的NO 
  v_商品名     收费项目目录.名称%Type; --通用名称 
  v_规格       收费项目目录.规格%Type;
  v_产地       收费项目目录.规格%Type;
  v_单位       收费项目目录.计算单位%Type;
  v_Lngid      药品收发记录.Id%Type; --收发ID 
  n_应付id     应付记录.Id%Type; --应付记录的ID 
  n_入出类别id 药品收发记录.入出类别id%Type; --入出类别ID 
  n_入出系数   药品收发记录.入出系数%Type; --入出系数 
  n_批次       药品收发记录.批次%Type := Null; --批次 
  n_库房分批   Integer; --是否分批核算    1:分批；0：不分批 
  n_在用分批   Integer; --是否在用分批       1:分批；0：不分批 
  v_可用数量   药品库存.可用数量%Type;
Begin

  If Not 批准文号_In Is Null And Not 产地_In Is Null Then
    Update 药品生产商对照 Set 批准文号 = 批准文号_In Where 药品id = 材料id_In And 厂家名称 = 产地_In;
    If Sql%RowCount = 0 Then
      Insert Into 药品生产商对照 (药品id, 厂家名称, 批准文号) Values (材料id_In, 产地_In, 批准文号_In);
    End If;
  End If;

  --取该材料的名称 
  v_产地 := '';
  Select 名称, 规格, 计算单位 Into v_商品名, v_规格, v_单位 From 收费项目目录 Where ID = 材料id_In;

  If v_规格 Is Not Null Then
    If Instr(v_规格, '|') <> 0 Then
      v_产地 := Substr(v_规格, Instr(v_规格, '|'));
      v_规格 := Substr(v_规格, Instr(v_规格, '|') - 1);
    End If;
  End If;

  Select 药品收发记录_Id.Nextval Into v_Lngid From Dual;

  Select Nvl(库房分批, 0), Nvl(在用分批, 0) Into n_库房分批, n_在用分批 From 材料特性 Where 材料id = 材料id_In;

  --财务审核直接用传过来的批次
  If 财务审核_In = 0 Then
    If 费用id_In > 0 And 批次_In > 0 Then
      n_批次 := 批次_In;
    Else
      If n_在用分批 = 0 Then
        If n_库房分批 = 1 Then
          Begin
            Select Distinct 0
            Into n_库房分批
            From 部门性质说明
            Where ((工作性质 Like '发料部门') Or (工作性质 Like '制剂室')) And 部门id = 库房id_In;
          Exception
            When Others Then
              n_库房分批 := 1;
          End;
        
          If n_库房分批 = 1 Then
            n_批次 := v_Lngid;
          End If;
        End If;
      Else
        n_批次 := v_Lngid;
      End If;
    End If;
  Else
    n_批次 := 批次_In;
  End If;

  Select b.Id, b.系数
  Into n_入出类别id, n_入出系数
  From 药品单据性质 A, 药品入出类别 B
  Where a.类别id = b.Id And a.单据 = 30 And Rownum < 2;

  Insert Into 药品收发记录
    (ID, 记录状态, 单据, NO, 序号, 库房id, 供药单位id, 入出类别id, 入出系数, 药品id, 批次, 产地, 批号, 生产日期, 效期, 灭菌日期, 灭菌效期, 填写数量, 实际数量, 成本价, 成本金额,
     扣率, 零售价, 零售金额, 差价, 摘要, 填制人, 填制日期, 发药方式, 配药人, 配药日期, 注册证号, 用法, 商品条码, 内部条码, 费用id, 批准文号)
  Values
    (v_Lngid, 1, 15, No_In, 序号_In, 库房id_In, 供药单位id_In, n_入出类别id, n_入出系数, 材料id_In, Decode(退货_In, -1, 批次_In, n_批次), 产地_In,
     批号_In, 生产日期_In, 效期_In, 灭菌日期_In, 灭菌效期_In, 退货_In * 实际数量_In, 退货_In * 实际数量_In, 成本价_In, 退货_In * 成本金额_In, 扣率_In, 零售价_In,
     退货_In * 零售金额_In, 退货_In * 差价_In, 摘要_In, 填制人_In, 填制日期_In, Decode(退货_In, -1, 1, 0), 核查人_In, 核查日期_In, 注册证号_In, 零售差价_In,
     商品条码_In, 内部条码_In, 费用id_In, 批准文号_In);

  --高值材料信息 
  If Length(高值材料_In) > 0 Then
    Insert Into 收发记录补充信息
      (收发id, 科室, 病人姓名, 住院号, 床号)
    Values
      (v_Lngid, Substr(高值材料_In, 1, Instr(高值材料_In, ',', 1, 1) - 1),
       Substr(高值材料_In, Instr(高值材料_In, ',', 1, 1) + 1, Instr(高值材料_In, ',', 1, 2) - Instr(高值材料_In, ',', 1, 1) - 1),
       Substr(高值材料_In, Instr(高值材料_In, ',', 1, 2) + 1, Instr(高值材料_In, ',', 1, 3) - Instr(高值材料_In, ',', 1, 2) - 1),
       Substr(高值材料_In, Instr(高值材料_In, ',', 1, 3) + 1, Length(高值材料_In)));
  End If;

  If 发票号_In Is Not Null Or 随货单号_In Is Not Null Then
  
    Select 应付记录_Id.Nextval Into n_应付id From Dual;
  
    --如果是第一笔明细,则产生应付记录的NO 
    Begin
      Select NO
      Into v_No
      From 应付记录
      Where 系统标识 = 5 And 记录性质 = 0 And 记录状态 = 1 And 入库单据号 = No_In And Rownum < 2;
    Exception
      When Others Then
        v_No := Nextno(67);
    End;
  
    Insert Into 应付记录
      (ID, 记录性质, 记录状态, 项目id, 序号, 单位id, NO, 系统标识, 收发id, 入库单据号, 单据金额, 随货单号, 发票号, 发票日期, 发票金额, 品名, 规格, 产地, 批号, 计量单位, 数量,
       采购价, 采购金额, 填制人, 填制日期, 审核人, 审核日期, 摘要, 库房id, 发票代码)
    Values
      (n_应付id, 0, 1, 材料id_In, 序号_In, 供药单位id_In, v_No, 5, v_Lngid, No_In, 退货_In * 零售金额_In, 随货单号_In, 发票号_In, 发票日期_In,
       退货_In * Decode(发票号_In, Null, 成本金额_In, 发票金额_In), v_商品名, v_规格, v_产地, 批号_In, v_单位, 退货_In * 实际数量_In, 成本价_In,
       退货_In * 成本金额_In, 填制人_In, 填制日期_In, Null, Null, 摘要_In, 库房id_In, 发票代码_In);
  End If;

  --退货时下可用数量 
  If 退货_In = -1 Then
    --检查库存 
    Begin
      Select Nvl(可用数量, 0)
      Into v_可用数量
      From 药品库存
      Where 库房id = 库房id_In And 药品id = 材料id_In And Nvl(批次, 0) = Nvl(批次_In, 0) And 性质 = 1;
    Exception
      When Others Then
        v_可用数量 := 0;
    End;
  
    If v_可用数量 - 实际数量_In < 0 Then
      v_Err_Msg := '[ZLSOFT]第' || 序号_In || '行的可用数量不够,请检查[ZLSOFT]';
      Raise Err_Item;
    End If;
    Update 药品库存
    Set 可用数量 = Nvl(可用数量, 0) - 实际数量_In
    Where 库房id = 库房id_In And 药品id = 材料id_In And Nvl(批次, 0) = 批次_In And 性质 = 1;
    Delete From 药品库存
    Where 库房id = 库房id_In And 药品id = 材料id_In And Nvl(可用数量, 0) = 0 And Nvl(实际数量, 0) = 0 And Nvl(实际金额, 0) = 0 And
          Nvl(实际差价, 0) = 0;
  End If;

Exception
  When Err_Item Then
    Raise_Application_Error(-20101, v_Err_Msg);
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_材料外购_Insert;
/

--79521:马政,2014-12-02,卫材流通业务环节加入批准文号
Create Or Replace Procedure Zl_材料外购_Verify
(
  No_In       In 药品收发记录.No%Type := Null,
  审核人_In   In 药品收发记录.审核人%Type := Null,
  审核日期_In In 药品收发记录.审核日期%Type := Sysdate
) Is
  Err_Item Exception;
  v_Err_Msg Varchar2(500);

  n_单位id      药品收发记录.供药单位id%Type;
  n_发票金额    应付记录.发票金额%Type;
  n_库存金额    药品库存.实际金额%Type;
  n_库存差价    药品库存.实际差价%Type;
  n_库存数量    药品库存.实际数量%Type;
  n_实价卫材    收费项目目录.是否变价%Type;
  n_Batch_Count Integer; --原不分批现在分批的材料的数量
  v_条码前缀    Varchar2(20);
  v_内部条码    药品库存.内部条码%Type;
  v_移库no      药品收发记录.No%Type;
  v_对方库房id  药品收发记录.库房id%Type := 0;
  v_入类别id    药品收发记录.入出类别id%Type := 0;
  v_出类别id    药品收发记录.入出类别id%Type := 0;
  n_平均成本价  药品库存.平均成本价%Type;
Begin
  v_条码前缀 := Nvl(Zl_Getsysparameter(159), '');

  Update 药品收发记录
  Set 审核人 = Nvl(审核人_In, 审核人), 审核日期 = 审核日期_In
  Where NO = No_In And 单据 = 15 And 记录状态 = 1 And 审核人 Is Null;

  If Sql%RowCount = 0 Then
    v_Err_Msg := '[ZLSOFT]该单据已经被他人审核或删除，不能进行审核！[ZLSOFT]';
    Raise Err_Item;
  End If;
  --主要针对原不分批现在分批的材料，不能对其审核
  Select Count(*)
  Into n_Batch_Count
  From 药品收发记录 A, 材料特性 B
  Where a.药品id = b.材料id And a.No = No_In And a.单据 = 15 And a.记录状态 = 1 And Nvl(a.批次, 0) = 0 And
        ((Nvl(b.库房分批, 0) = 1 And
        a.库房id Not In (Select 部门id From 部门性质说明 Where (工作性质 Like '发料部门') Or (工作性质 Like '制剂室'))) Or Nvl(b.在用分批, 0) = 1);

  If n_Batch_Count > 0 Then
    v_Err_Msg := '[ZLSOFT]该单据中包含有一条原来不分批，现在分批的材料，不能审核！[ZLSOFT]';
    Raise Err_Item;
  End If;

  --原分批现不分批的材料,在审核时，要处理他
  Update 药品收发记录
  Set 批次 = 0
  Where ID In
        (Select ID
         From 药品收发记录 A, 材料特性 B
         Where a.药品id = b.材料id And a.No = No_In And a.单据 = 15 And a.记录状态 = 1 And Nvl(a.批次, 0) > 0 And
               (Nvl(b.库房分批, 0) = 0 Or
               (Nvl(b.在用分批, 0) = 0 And
               a.库房id In (Select 部门id From 部门性质说明 Where (工作性质 Like '发料部门') Or (工作性质 Like '制剂室')))));

  For v_收发 In (Select a.Id, a.实际数量, a.发药方式, a.零售价, a.零售金额, a.差价, a.库房id, a.药品id, a.批次, a.供药单位id, a.成本价, a.批号, a.效期,
                      a.灭菌效期, a.生产日期, a.产地, a.入出类别id, a.注册证号, a.扣率, a.商品条码, a.内部条码, Nvl(b.是否条码管理, 0) As 条码管理, a.批准文号
               From 药品收发记录 A, 材料特性 B
               Where a.药品id = b.材料id And a.No = No_In And a.单据 = 15 And a.记录状态 = 1
               Order By a.药品id) Loop
    v_内部条码 := Null;
    If v_收发.条码管理 = 1 Then
      If v_收发.内部条码 Is Null Then
        If Not v_条码前缀 Is Null Then
          v_内部条码 := v_条码前缀 || Nextno(126);
        Else
          v_内部条码 := Nextno(126);
        End If;
      Else
        v_内部条码 := v_收发.内部条码;
      End If;
    End If;
  
    --更改材料库存表的相应数据
    Select 是否变价 Into n_实价卫材 From 收费项目目录 Where ID = v_收发.药品id;
  
    Update 药品库存
    Set 可用数量 = Nvl(可用数量, 0) + Nvl(v_收发.实际数量, 0) * Decode(v_收发.发药方式, 1, 0, 1), 实际数量 = Nvl(实际数量, 0) + Nvl(v_收发.实际数量, 0),
        实际金额 = Nvl(实际金额, 0) + Nvl(v_收发.零售金额, 0), 实际差价 = Nvl(实际差价, 0) + Nvl(v_收发.差价, 0),
        上次供应商id = Nvl(v_收发.供药单位id, 上次供应商id), 上次采购价 = Nvl(v_收发.成本价, 上次采购价), 上次批号 = Nvl(v_收发.批号, 上次批号),
        上次产地 = Nvl(v_收发.产地, 上次产地), 灭菌效期 = Nvl(v_收发.灭菌效期, 灭菌效期), 上次生产日期 = Nvl(v_收发.生产日期, 上次生产日期), 效期 = Nvl(v_收发.效期, 效期),
        零售价 = Decode(Nvl(v_收发.批次, 0), 0, Null, Decode(n_实价卫材, 1, v_收发.零售价, Null)), 上次扣率 = Nvl(v_收发.扣率, 上次扣率),
        商品条码 = v_收发.商品条码, 内部条码 = v_内部条码, 批准文号 = v_收发.批准文号
    Where 库房id = v_收发.库房id And 药品id = v_收发.药品id And Nvl(批次, 0) = Nvl(v_收发.批次, 0) And 性质 = 1;
  
    If Sql%NotFound Then
      Insert Into 药品库存
        (库房id, 药品id, 批次, 性质, 可用数量, 实际数量, 实际金额, 实际差价, 上次供应商id, 上次采购价, 上次批号, 上次生产日期, 上次产地, 灭菌效期, 效期, 零售价, 上次扣率, 商品条码,
         内部条码, 平均成本价, 批准文号)
      Values
        (v_收发.库房id, v_收发.药品id, v_收发.批次, 1, v_收发.实际数量 * Decode(v_收发.发药方式, 1, 0, 1), v_收发.实际数量, v_收发.零售金额, v_收发.差价,
         v_收发.供药单位id, v_收发.成本价, v_收发.批号, v_收发.生产日期, v_收发.产地, v_收发.灭菌效期, v_收发.效期,
         Decode(Nvl(v_收发.批次, 0), 0, Null, Decode(n_实价卫材, 1, v_收发.零售价, Null)), v_收发.扣率, v_收发.商品条码, v_内部条码, v_收发.成本价,
         v_收发.批准文号);
    End If;
  
    If v_收发.内部条码 Is Null And Not v_内部条码 Is Null Then
      Update 药品收发记录 Set 内部条码 = v_内部条码 Where ID = v_收发.Id;
    End If;
  
    --清除数量金额为零的记录
    Delete From 药品库存
    Where 库房id = v_收发.库房id And 药品id = v_收发.药品id And Nvl(可用数量, 0) = 0 And Nvl(实际数量, 0) = 0 And Nvl(实际金额, 0) = 0 And
          Nvl(实际差价, 0) = 0;
  
    --更改材料收发汇总表的相应数据
    --更新该材料的成本价
    Begin
      Select Sum(Nvl(实际金额, 0)), Sum(Nvl(实际差价, 0)), Sum(Nvl(实际数量, 0))
      Into n_库存金额, n_库存差价, n_库存数量
      From 药品库存
      Where 性质 = 1 And 药品id = v_收发.药品id;
    Exception
      When Others Then
        n_库存数量 := 0;
    End;
  
    --更新该药品的成本价
    Update 材料特性
    Set 成本价 = v_收发.成本价, 上次售价 = v_收发.零售价, 上次供应商id = v_收发.供药单位id, 上次产地 = v_收发.产地
    Where 材料id = v_收发.药品id;
  
    --更改材料特性中的注册证号:如果发现材料特性表中的注册证号没填，则直接反写给材料特性表中的注册证号
    If Nvl(v_收发.注册证号, ' ') <> ' ' Then
      Update 材料特性 Set 注册证号 = v_收发.注册证号 Where 材料id = v_收发.药品id And 注册证号 Is Null;
    End If;
  
    --重新计算库存表中的平均成本价
    Update 药品库存
    Set 平均成本价 = Decode(Nvl(批次, 0), 0, Decode((实际金额 - 实际差价) / 实际数量, 0, 上次采购价, (实际金额 - 实际差价) / 实际数量), 上次采购价)
    Where 药品id = v_收发.药品id And Nvl(批次, 0) = Nvl(v_收发.批次, 0) And 库房id = v_收发.库房id And 性质 = 1 And Nvl(实际数量, 0) <> 0;
    If Sql%NotFound Then
      Select 成本价 Into n_平均成本价 From 材料特性 Where 材料id = v_收发.药品id;
      Update 药品库存
      Set 平均成本价 = n_平均成本价
      Where 药品id = v_收发.药品id And 库房id = v_收发.库房id And Nvl(批次, 0) = Nvl(v_收发.批次, 0) And 性质 = 1;
    End If;
  End Loop;

  --对应付余额表进行处理
  --此处用一个块，主要是解决没有对应发票号的记录
  Begin
    Update 应付记录
    Set 审核人 = 审核人_In, 审核日期 = 审核日期_In
    Where 入库单据号 = No_In And 系统标识 = 5 And 记录性质 = 0 And 记录状态 = 1;
  
    Select b.单位id, Sum(发票金额)
    Into n_单位id, n_发票金额
    From 药品收发记录 A, 应付记录 B
    Where a.Id = b.收发id And a.No = No_In And a.单据 = 15 And b.系统标识 = 5
    Group By b.单位id;
  
    If Nvl(n_单位id, 0) <> 0 Then
      Update 应付余额 Set 金额 = Nvl(金额, 0) + Nvl(n_发票金额, 0) Where 单位id = n_单位id And 性质 = 1;
    
      If Sql%NotFound Then
        Insert Into 应付余额 (单位id, 性质, 金额) Values (n_单位id, 1, n_发票金额);
      End If;
    End If;
  Exception
    When No_Data_Found Then
      Null;
  End;

  --如果是自动产生的备货卫材入库单，则产生移库单
  For v_Data In (Select ID, 序号, 实际数量, 发药方式, 零售价, 零售金额, 差价, 库房id, 药品id, 批次, 供药单位id, 成本价, 成本金额, 批号, 效期, 灭菌效期, 生产日期, 产地,
                        入出类别id, 注册证号, 扣率, 摘要, 商品条码, 内部条码, 费用id, 审核人, 审核日期
                 From 药品收发记录
                 Where NO = No_In And 单据 = 15 And 记录状态 = 1 And 审核日期 Is Not Null And 费用id > 0
                 Order By 序号) Loop
    If v_对方库房id = 0 Then
      Begin
        Select Distinct 库房id Into v_对方库房id From 药品收发记录 Where 单据 In (24, 25) And 费用id = v_Data.费用id;
      Exception
        When Others Then
          v_对方库房id := 0;
      End;
    End If;
  
    If v_对方库房id > 0 Then
      If v_移库no Is Null Then
        v_移库no := Nextno(72, v_Data.库房id);
      End If;
    
      Zl_材料移库_Insert(v_移库no, v_Data.序号 * 2 - 1, v_Data.库房id, v_对方库房id, v_Data.药品id, v_Data.批次, v_Data.实际数量, v_Data.实际数量,
                     v_Data.成本价, v_Data.成本金额, v_Data.零售价, v_Data.零售金额, v_Data.差价, v_Data.审核人, v_Data.产地, v_Data.批号,
                     v_Data.效期, v_Data.灭菌效期, v_Data.摘要, v_Data.审核日期);
    End If;
  End Loop;

  --对新产生的移库单进行备料和审核
  If Not v_移库no Is Null Then
    Zl_材料移库_Prepare(v_移库no, 审核人_In);
    Zl_材料移库_Prepare(v_移库no);
  
    Select b.Id As 类别id
    Into v_入类别id
    From 药品单据性质 A, 药品入出类别 B
    Where a.类别id = b.Id And a.单据 = 34 And 系数 = 1 And Rownum < 2;
  
    Select b.Id As 类别id
    Into v_出类别id
    From 药品单据性质 A, 药品入出类别 B
    Where a.类别id = b.Id And a.单据 = 34 And 系数 = -1 And Rownum < 2;
  
    For v_Data In (Select 序号, 库房id, 对方部门id, 药品id, 产地, Nvl(批次, 0) As 批次, 填写数量, 实际数量, 成本价, 成本金额, 零售金额, 差价, NO, 填制人, 批号, 效期,
                          灭菌效期, 填制日期
                   From 药品收发记录
                   Where 单据 = 19 And NO = v_移库no And 审核日期 Is Null And 入出系数 = -1
                   Order By 序号) Loop
    
      Zl_材料移库_Verify(v_Data.序号, v_Data.库房id, v_Data.对方部门id, v_Data.药品id, v_Data.产地, v_Data.批次, v_Data.填写数量, v_Data.实际数量,
                     v_Data.成本价, v_Data.成本金额, v_Data.零售金额, v_Data.差价, v_出类别id, v_入类别id, v_Data.No, v_Data.填制人, v_Data.批号,
                     v_Data.效期, v_Data.灭菌效期, v_Data.填制日期);
    End Loop;
  End If;
Exception
  When Err_Item Then
    Raise_Application_Error(-20101, v_Err_Msg);
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_材料外购_Verify;
/

--79521:马政,2014-12-02,卫材流通业务环节加入批准文号
Create Or Replace Procedure Zl_材料外购_Strike
(
  行次_In       In Integer,
  原记录状态_In In 药品收发记录.记录状态%Type,
  No_In         In 药品收发记录.No%Type,
  序号_In       In 药品收发记录.序号%Type,
  材料id_In     In 药品收发记录.药品id%Type,
  冲销数量_In   In 药品收发记录.实际数量%Type,
  填制人_In     In 药品收发记录.填制人%Type,
  填制日期_In   In 药品收发记录.填制日期%Type,
  随货单号_In   In 应付记录.随货单号%Type := Null,
  发票号_In     In 应付记录.发票号%Type := Null,
  发票日期_In   In 应付记录.发票日期%Type := Null,
  发票金额_In   In 应付记录.发票金额%Type := Null,
  全部冲销_In   In 药品收发记录.实际数量%Type := 0, --用于财务审核
  财务审核_In   In Number := 0, --财务审核标志:1-财务审核,0-冲销
  摘要_In       In 药品收发记录.摘要%Type := Null,
  发票代码_In   In 应付记录.发票代码%Type := Null
) Is
  Err_Item Exception;
  v_Err_Msg Varchar2(100);

  v_产地     药品收发记录.产地%Type;
  v_批号     药品收发记录.批号%Type;
  v_摘要     药品收发记录.摘要%Type;
  v_核查人   药品收发记录.配药人%Type;
  v_注册证号 药品收发记录.注册证号%Type;
  d_效期     药品收发记录.效期%Type;
  d_灭菌效期 药品收发记录.灭菌效期%Type;
  d_灭菌日期 药品收发记录.灭菌日期%Type;
  d_生产日期 药品收发记录.生产日期%Type;
  d_核查日期 药品收发记录.配药日期%Type;
  v_商品条码 药品收发记录.商品条码%Type;
  v_内部条码 药品收发记录.内部条码%Type;

  n_应付id     应付记录.Id%Type;
  n_库房id     药品收发记录.库房id%Type;
  n_供药单位id 药品收发记录.供药单位id%Type;
  n_入出类别id 药品收发记录.入出类别id%Type;
  n_批次       药品收发记录.批次%Type;
  n_成本价     药品收发记录.成本价%Type;
  n_成本金额   药品收发记录.成本金额%Type;
  n_扣率       药品收发记录.扣率%Type;
  n_零售价     药品收发记录.零售价%Type;
  n_零售金额   药品收发记录.零售金额%Type;
  n_差价       药品收发记录.差价%Type;
  n_零售差价   药品收发记录.差价%Type;

  n_剩余数量     药品收发记录.实际数量%Type;
  n_剩余成本金额 药品收发记录.成本金额%Type;
  n_剩余零售金额 药品收发记录.零售金额%Type;
  n_剩余差价金额 药品收发记录.差价%Type;
  n_平均成本价   药品库存.平均成本价%Type;
  v_批准文号     药品收发记录.批准文号%Type;

  n_发药方式 药品收发记录.发药方式%Type;
  n_入出系数 药品收发记录.入出系数%Type;
  n_冲销数量 药品收发记录.实际数量%Type;
  --对冲销数量进行检查
  n_库存数   药品库存.实际数量%Type;
  n_收发id   药品收发记录.Id%Type;
  n_记录状态 药品收发记录.记录状态%Type;
  n_实价卫材 收费项目目录.是否变价%Type;

  n_库房分批   Integer;
  n_在用分批   Integer;
  n_分批属性   Integer;
  n_库房       Integer;
  n_分批       Number;
  n_小数       Number(2);
  n_发票金额   Number(16, 5);
  n_Batchcount Integer; --原不分批现在分批的材料的数量

Begin

  Select Nvl(精度, 2) Into n_小数 From 药品卫材精度 Where 性质 = 0 And 类别 = 2 And 内容 = 4 And 单位 = 5;

  If 行次_In = 1 Then
    Update 药品收发记录
    Set 记录状态 = Decode(原记录状态_In, 1, 3, 原记录状态_In + 3), 费用id = Decode(财务审核_In, 1, 1, Null)
    Where NO = No_In And 单据 = 15 And 记录状态 = 原记录状态_In;
  
    If Sql%RowCount = 0 Then
      v_Err_Msg := '[ZLSOFT]该单据已经被他人冲销！[ZLSOFT]';
    
      Raise Err_Item;
    End If;
  End If;

  --主要针对原不分批现在分批的材料，不能对其审核
  Select Count(*)
  Into n_Batchcount
  From 药品收发记录 A, 材料特性 B
  Where a.药品id = b.材料id And a.No = No_In And a.单据 = 15 And Mod(a.记录状态, 3) = 0 And Nvl(a.批次, 0) = 0 And
        a.药品id + 0 = 材料id_In And
        ((Nvl(b.在用分批, 0) = 1 And
        a.库房id Not In (Select 部门id From 部门性质说明 Where (工作性质 Like '发料部门') Or (工作性质 Like '制剂室'))) Or Nvl(b.在用分批, 0) = 1);

  If n_Batchcount > 0 Then
    v_Err_Msg := '[ZLSOFT]该单据中第' || 序号_In || '行的材料原来不分批，现在分批的材料，不能冲销！[ZLSOFT]';
    Raise Err_Item;
  End If;

  Select Sum(a.实际数量) As 剩余数量, Sum(a.成本金额) As 剩余成本金额, Sum(a.零售金额) As 剩余零售金额,
         Sum(To_Number(To_Char(Nvl(用法, '0'), '999999999990.9999999'), '999999999990.9999999')) As 剩余差价金额, a.库房id,
         a.供药单位id, a.入出类别id, a.入出系数, Nvl(a.批次, 0), a.产地, a.批号, a.效期, a.灭菌效期, a.灭菌日期, a.生产日期, a.成本价, a.扣率, a.零售价, a.注册证号,
         b.库房分批, b.在用分批, Max(a.发药方式), Max(a.配药人), Max(配药日期), a.商品条码, a.内部条码, a.批准文号
  Into n_剩余数量, n_剩余成本金额, n_剩余零售金额, n_剩余差价金额, n_库房id, n_供药单位id, n_入出类别id, n_入出系数, n_批次, v_产地, v_批号, d_效期, d_灭菌效期, d_灭菌日期,
       d_生产日期, n_成本价, n_扣率, n_零售价, v_注册证号, n_库房分批, n_在用分批, n_发药方式, v_核查人, d_核查日期, v_商品条码, v_内部条码, v_批准文号
  From 药品收发记录 A, 材料特性 B
  Where a.No = No_In And a.药品id = b.材料id And a.单据 = 15 And a.药品id + 0 = 材料id_In And a.序号 = 序号_In
  Group By a.库房id, a.供药单位id, a.入出类别id, a.入出系数, Nvl(a.批次, 0), a.产地, a.批号, a.效期, a.灭菌效期, a.灭菌日期, a.生产日期, a.成本价, a.扣率,
           a.零售价, a.注册证号, b.库房分批, b.在用分批, a.商品条码, a.内部条码,a.批准文号;

  --判断该部门是库房还是发料部门
  Begin
    Select Distinct 0
    Into n_库房
    From 部门性质说明
    Where (工作性质 = '发料部门' Or 工作性质 = '制剂室') And 部门id = n_库房id;
  Exception
    When Others Then
      n_库房 := 1;
  End;

  --根据部门性质,判断分批特性
  If n_库房 = 0 Then
    n_分批属性 := n_在用分批;
  
  Else
    n_分批属性 := n_库房分批;
  End If;

  --n_分批:(原分批,现分批为批次;否则为零)原不分批,现分批,本过程不考虑
  n_分批 := 0;
  If n_分批属性 = 1 And n_批次 <> 0 Then
    n_分批 := n_批次;
  End If;

  --全部冲销或者财务审核时，冲销数量等于剩余数量；其他情况冲销数量等于传入的冲销数量 
  If 全部冲销_In = 1 Or 财务审核_In = 1 Then
    n_冲销数量 := n_剩余数量;
  Else
    n_冲销数量 := 冲销数量_In;
  End If;

  --冲销数量大于剩余数量，不允许
  If Abs(n_剩余数量) < Abs(n_冲销数量) Then
    v_Err_Msg := '[ZLSOFT]该单据中第' || 序号_In || '行的材料冲销的数量大于了剩余的数据，不能冲销！[ZLSOFT]';
    Raise Err_Item;
  End If;

  Select 是否变价 Into n_实价卫材 From 收费项目目录 Where ID = 材料id_In;

  n_成本金额 := Round(n_冲销数量 / n_剩余数量 * n_剩余成本金额, n_小数);
  n_零售金额 := Round(n_冲销数量 / n_剩余数量 * n_剩余零售金额, n_小数);
  n_差价     := Round(n_零售金额 - n_成本金额, n_小数);
  If 全部冲销_In = 1 Or 财务审核_In = 1 Then
    n_零售差价 := n_剩余差价金额;
  Else
    n_零售差价 := Round(n_冲销数量 / n_剩余数量 * n_剩余差价金额, n_小数);
  End If;

  Select 药品收发记录_Id.Nextval Into n_收发id From Dual;

  Insert Into 药品收发记录
    (ID, 记录状态, 单据, NO, 序号, 库房id, 供药单位id, 入出类别id, 入出系数, 药品id, 批次, 产地, 批号, 生产日期, 效期, 灭菌效期, 灭菌日期, 填写数量, 实际数量, 成本价, 成本金额,
     扣率, 零售价, 零售金额, 差价, 摘要, 注册证号, 填制人, 填制日期, 审核人, 审核日期, 配药人, 配药日期, 发药方式, 用法, 费用id, 商品条码, 内部条码, 批准文号)
  Values
    (n_收发id, Decode(原记录状态_In, 1, 2, 原记录状态_In + 2), 15, No_In, 序号_In, n_库房id, n_供药单位id, n_入出类别id, 1, 材料id_In, n_批次, v_产地,
     v_批号, d_生产日期, d_效期, d_灭菌效期, d_灭菌日期, -n_冲销数量, -n_冲销数量, n_成本价, -n_成本金额, n_扣率, n_零售价, -n_零售金额, -n_差价, 摘要_In, v_注册证号,
     填制人_In, 填制日期_In, 填制人_In, 填制日期_In, v_核查人, d_核查日期, n_发药方式, -n_零售差价, Decode(财务审核_In, 1, 1, Null), v_商品条码, v_内部条码,
     v_批准文号);

  --对于冲销的单据也应该对应付余额表进行处理
  --只对填了发票号的记录进行处理
  n_发票金额 := Nvl(发票金额_In, 0);
  If (Nvl(发票号_In, ' ') <> ' ' Or 随货单号_In Is Not Null) And Nvl(n_发票金额, 0) <> 0 Then
    --对于财务审核的，要将剩余的发票金额全部冲销
    If 全部冲销_In = 1 Then
    
      Select Sum(b.发票金额)
      Into n_发票金额
      From (Select ID From 药品收发记录 Where 单据 = 15 And NO = No_In And 序号 = 序号_In) A, 应付记录 B
      Where a.Id = b.收发id And b.系统标识 = 5 And b.记录性质 <> -1;
    
    End If;
  
    Update 应付余额 Set 金额 = Nvl(金额, 0) - Nvl(n_发票金额, 0) Where 单位id = n_供药单位id And 性质 = 1;
  
    If Sql%NotFound Then
      Insert Into 应付余额 (单位id, 性质, 金额) Values (n_供药单位id, 1, -nvl(n_发票金额, 0));
    
    End If;
    Delete 应付余额 Where 单位id = n_供药单位id And Nvl(金额, 0) = 0;
  
  End If;

  Update 药品库存
  Set 可用数量 = Nvl(可用数量, 0) - n_冲销数量, 实际数量 = Nvl(实际数量, 0) - n_冲销数量, 实际金额 = Nvl(实际金额, 0) - n_零售金额,
      实际差价 = Nvl(实际差价, 0) - n_差价, 上次供应商id = n_供药单位id, 上次采购价 = n_成本价, 上次批号 = v_批号, 上次产地 = v_产地, 灭菌效期 = d_灭菌效期,
      上次生产日期 = d_生产日期, 效期 = d_效期, 批准文号 = v_批准文号
  Where 库房id = n_库房id And 药品id = 材料id_In And Nvl(批次, 0) = Nvl(n_分批, 0) And 性质 = 1;

  If Sql%NotFound Then
    Insert Into 药品库存
      (库房id, 药品id, 批次, 性质, 可用数量, 实际数量, 实际金额, 实际差价, 上次供应商id, 上次采购价, 上次批号, 上次产地, 效期, 灭菌效期, 上次生产日期, 零售价, 平均成本价, 批准文号)
    Values
      (n_库房id, 材料id_In, n_分批, 1, -n_冲销数量, -n_冲销数量, -n_零售金额, -n_差价, n_供药单位id, n_成本价, v_批号, v_产地, d_效期, d_灭菌效期, d_生产日期,
       Decode(n_实价卫材, 0, Null, Decode(Nvl(n_分批, 0), 0, Null, n_零售价)), n_成本价, v_批准文号);
  End If;

  --清除数量金额为零的记录
  Delete From 药品库存
  Where 库房id = n_库房id And 药品id = 材料id_In And Nvl(可用数量, 0) = 0 And Nvl(实际数量, 0) = 0 And Nvl(实际金额, 0) = 0 And
        Nvl(实际差价, 0) = 0;

  --重新计算库存表中的平均成本价
  Update 药品库存
  Set 平均成本价 = Decode(Nvl(批次, 0), 0, Decode((实际金额 - 实际差价) / 实际数量, 0, 上次采购价, (实际金额 - 实际差价) / 实际数量), 上次采购价)
  Where 性质 = 1 And 库房id = n_库房id And 药品id = 材料id_In And Nvl(批次, 0) = Nvl(n_分批, 0) And Nvl(实际数量, 0) <> 0;
  If Sql%NotFound Then
    Select 成本价 Into n_平均成本价 From 材料特性 Where 材料id = 材料id_In;
    Update 药品库存
    Set 平均成本价 = n_平均成本价
    Where 性质 = 1 And 库房id = n_库房id And 药品id = 材料id_In And Nvl(批次, 0) = Nvl(n_分批, 0) And Nvl(平均成本价, 0) <> n_成本价;
  End If;

  --产生应付记录的冲销记录(先判断应付记录中是否已存在该记录对应的冲销记录,是则更新;否则新增)
  Select 应付记录_Id.Nextval Into n_应付id From Dual;

  Begin
    Select Max(记录状态) + 3
    Into n_记录状态
    From 应付记录
    Where (系统标识, 记录性质, NO, 项目id, 序号) In
          (Select 系统标识, 记录性质, NO, 项目id, 序号
           From 应付记录
           Where 收发id =
                 (Select ID From 药品收发记录 Where NO = No_In And 单据 = 15 And 序号 = 序号_In And Mod(记录状态, 3) = 0) And 系统标识 = 5 And
                 记录性质 = 0) And 记录状态 <> 1 And Mod(记录状态, 3) <> 0;
  
  Exception
    When Others Then
      n_记录状态 := 2;
  End;
  If n_记录状态 Is Null Then
    n_记录状态 := 2;
  End If;
  If Mod(n_记录状态, 3) <> 2 Then
    n_记录状态 := n_记录状态 + 1;
  End If;
  If Mod(n_记录状态, 3) <> 2 Then
    n_记录状态 := n_记录状态 + 1;
  End If;

  Insert Into 应付记录
    (ID, 记录性质, 记录状态, 项目id, 序号, 单位id, NO, 系统标识, 收发id, 入库单据号, 单据金额, 随货单号, 发票号, 发票日期, 发票金额, 品名, 规格, 产地, 批号, 计量单位, 数量, 采购价,
     采购金额, 填制人, 填制日期, 审核人, 审核日期, 摘要, 库房id, 发票代码)
    Select n_应付id, 记录性质, n_记录状态, 材料id_In, 序号_In, 单位id, NO, 5, n_收发id, 入库单据号, -1 * n_零售金额, 随货单号, 发票号, 发票日期, -n_发票金额, 品名,
           规格, 产地, 批号, 计量单位, -1 * n_冲销数量, 采购价, -1 * 采购价 * n_冲销数量, 填制人_In, 填制日期_In, 填制人_In, 填制日期_In, 摘要, 库房id, 发票代码_In
    From 应付记录
    Where 收发id = (Select ID From 药品收发记录 Where NO = No_In And 单据 = 15 And 序号 = 序号_In And Mod(记录状态, 3) = 0) And 系统标识 = 5 And
          记录性质 = 0;

  Update 应付记录
  Set 记录状态 = 3
  Where 收发id = (Select ID From 药品收发记录 Where NO = No_In And 单据 = 15 And 序号 = 序号_In And Mod(记录状态, 3) = 0) And 系统标识 = 5 And
        记录性质 = 0;

  --处理调价后冲销（财务审核时不需要处理）        
  If 财务审核_In = 0 Then
    Zl_材料收发记录_调价修正(n_收发id);
  End If;
Exception
  When Err_Item Then
    Raise_Application_Error(-20101, v_Err_Msg);
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_材料外购_Strike;
/

--79521:马政,2014-12-05,卫材批准文号修改
Create Or Replace Procedure Zl_材料其他入库_Insert
(
  No_In         In 药品收发记录.No%Type,
  序号_In       In 药品收发记录.序号%Type,
  库房id_In     In 药品收发记录.库房id%Type,
  入出类别id_In In 药品收发记录.入出类别id%Type,
  材料id_In     In 药品收发记录.药品id%Type,
  实际数量_In   In 药品收发记录.实际数量%Type,
  成本价_In     In 药品收发记录.成本价%Type,
  成本金额_In   In 药品收发记录.成本金额%Type,
  零售价_In     In 药品收发记录.零售价%Type,
  零售金额_In   In 药品收发记录.零售金额%Type,
  差价_In       In 药品收发记录.差价%Type,
  零售差价_In   In 药品收发记录.差价%Type,
  填制人_In     In 药品收发记录.填制人%Type,
  填制日期_In   In 药品收发记录.填制日期%Type,
  摘要_In       In 药品收发记录.摘要%Type := Null,
  产地_In       In 药品收发记录.产地%Type := Null,
  批号_In       In 药品收发记录.批号%Type := Null,
  生产日期_In   In 药品收发记录.生产日期%Type := Null,
  效期_In       In 药品收发记录.效期%Type := Null,
  灭菌日期_In   In 药品收发记录.灭菌日期%Type := Null,
  灭菌效期_In   In 药品收发记录.灭菌效期%Type := Null,
  商品条码_In   In 药品收发记录.商品条码%Type := Null,
  批准文号_In   In 药品收发记录.批准文号%Type := Null
) Is
  n_Id       药品收发记录.Id%Type; --收发ID
  n_入出系数 药品收发记录.入出系数%Type;
  n_批次     药品收发记录.批次%Type := Null; --批次
  n_库房分批 Integer; --是否分批核算    1:分批;0：不分批
  n_在用分批 Integer; --是否分批核算    1:分批;0：不分批
Begin
  If Not 批准文号_In Is Null And Not 产地_In Is Null Then
    Update 药品生产商对照 Set 批准文号 = 批准文号_In Where 药品id = 材料id_In And 厂家名称 = 产地_In;
    If Sql%RowCount = 0 Then
      Insert Into 药品生产商对照 (药品id, 厂家名称, 批准文号) Values (材料id_In, 产地_In, 批准文号_In);
    End If;
  End If;

  n_入出系数 := 1;

  Select 药品收发记录_Id.Nextval Into n_Id From Dual;

  Select Nvl(库房分批, 0), Nvl(在用分批, 0) Into n_库房分批, n_在用分批 From 材料特性 Where 材料id = 材料id_In;

  If n_在用分批 = 0 Then
    If n_库房分批 = 1 Then
      Begin
        Select Distinct 0
        Into n_库房分批
        From 部门性质说明
        Where (工作性质 = '发料部门' Or 工作性质 Like '制剂室') And 部门id = 库房id_In;
      Exception
        When Others Then
          n_库房分批 := 1;
      End;
    
      If n_库房分批 = 1 Then
        n_批次 := n_Id;
      End If;
    End If;
  Else
    n_批次 := n_Id;
  End If;
  Insert Into 药品收发记录
    (ID, 记录状态, 单据, NO, 序号, 库房id, 入出类别id, 入出系数, 药品id, 批次, 产地, 批号, 效期, 灭菌日期, 灭菌效期, 填写数量, 实际数量, 成本价, 成本金额, 零售价, 零售金额, 差价,
     摘要, 填制人, 填制日期, 生产日期, 用法, 商品条码, 批准文号)
  Values
    (n_Id, 1, 17, No_In, 序号_In, 库房id_In, 入出类别id_In, n_入出系数, 材料id_In, n_批次, 产地_In, 批号_In, 效期_In, 灭菌日期_In, 灭菌效期_In,
     实际数量_In, 实际数量_In, 成本价_In, 成本金额_In, 零售价_In, 零售金额_In, 差价_In, 摘要_In, 填制人_In, 填制日期_In, 生产日期_In, 零售差价_In, 商品条码_In,
     批准文号_In);
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_材料其他入库_Insert;
/

--79521:马政,2014-12-02,卫材流通业务环节加入批准文号
Create Or Replace Procedure Zl_材料其他入库_Verify
(
  No_In     In 药品收发记录.No%Type := Null,
  审核人_In In 药品收发记录.审核人%Type := Null
) Is
  Err_Item Exception;
  v_Err_Msg Varchar2(500);

  n_实价卫材    收费项目目录.是否变价%Type;
  n_Batch_Count Integer; --原不分批现在分批的材料的数量
  v_条码前缀    Varchar2(20);
  v_内部条码    药品库存.内部条码%Type;
  n_平均成本价  药品库存.平均成本价%Type;
Begin
  v_条码前缀 := Nvl(Zl_Getsysparameter(159), '');

  Update 药品收发记录
  Set 审核人 = 审核人_In, 审核日期 = Sysdate
  Where NO = No_In And 单据 = 17 And 记录状态 = 1 And 审核人 Is Null;

  If Sql%RowCount = 0 Then
    v_Err_Msg := '[ZLSOFT]该单据已经被他人审核！[ZLSOFT]';
    Raise Err_Item;
  End If;

  --主要针对原不分批现在分批的材料，不能对其审核
  Select Count(*)
  Into n_Batch_Count
  From 药品收发记录 A, 材料特性 B
  Where a.药品id = b.材料id And a.No = No_In And a.单据 = 17 And a.记录状态 = 1 And Nvl(a.批次, 0) = 0 And
        ((Nvl(b.库房分批, 0) = 1 And
        a.库房id Not In (Select 部门id From 部门性质说明 Where (工作性质 Like '发料部门') Or (工作性质 Like '制剂室'))) Or Nvl(b.在用分批, 0) = 1);

  If n_Batch_Count > 0 Then
    v_Err_Msg := '[ZLSOFT]该单据中包含有一条原来不分批，现在分批的材料，不能审核！[ZLSOFT';
    Raise Err_Item;
  End If;

  --原分批现不分批的材料,在审核时，要处理他
  Update 药品收发记录
  Set 批次 = 0
  Where ID =
        (Select ID
         From 药品收发记录 A, 材料特性 B
         Where b.材料id = a.药品id And a.No = No_In And a.单据 = 17 And a.记录状态 = 1 And Nvl(a.批次, 0) > 0 And
               (Nvl(b.库房分批, 0) = 0 Or
               (Nvl(b.在用分批, 0) = 0 And
               a.库房id In (Select 部门id From 部门性质说明 Where (工作性质 Like '发料部门') Or (工作性质 Like '制剂室')))));

  For c_收发 In (Select a.Id, a.实际数量, a.零售价, a.零售金额, a.差价, a.库房id, a.药品id, a.批次, a.成本价, a.批号, a.效期, a.灭菌效期, a.灭菌日期, a.产地,
                      a.入出类别id, a.生产日期, a.商品条码, a.内部条码, Nvl(b.是否条码管理, 0) As 条码管理, a.批准文号
               From 药品收发记录 A, 材料特性 B
               Where a.药品id = b.材料id And a.No = No_In And a.单据 = 17 And a.记录状态 = 1
               Order By a.药品id) Loop
  
    v_内部条码 := Null;
    If c_收发.条码管理 = 1 Then
      If Not v_条码前缀 Is Null Then
        v_内部条码 := v_条码前缀 || Nextno(126);
      Else
        v_内部条码 := Nextno(126);
      End If;
    End If;
  
    --更改药品库存表的相应数据
    Select 是否变价 Into n_实价卫材 From 收费项目目录 Where ID = c_收发.药品id;
  
    Update 药品库存
    Set 可用数量 = Nvl(可用数量, 0) + Nvl(c_收发.实际数量, 0), 实际数量 = Nvl(实际数量, 0) + Nvl(c_收发.实际数量, 0),
        实际金额 = Nvl(实际金额, 0) + Nvl(c_收发.零售金额, 0), 实际差价 = Nvl(实际差价, 0) + Nvl(c_收发.差价, 0), 上次采购价 = Nvl(c_收发.成本价, 上次采购价),
        上次批号 = Nvl(c_收发.批号, 上次批号), 上次生产日期 = Nvl(c_收发.生产日期, 上次生产日期), 上次产地 = Nvl(c_收发.产地, 上次产地), 效期 = Nvl(c_收发.效期, 效期),
        灭菌效期 = Nvl(c_收发.灭菌效期, 灭菌效期), 零售价 = Decode(Nvl(c_收发.批次, 0), 0, Null, Decode(n_实价卫材, 1, c_收发.零售价, Null)),
        商品条码 = c_收发.商品条码, 内部条码 = v_内部条码, 批准文号 = c_收发.批准文号
    Where 库房id = c_收发.库房id And 药品id = c_收发.药品id And Nvl(批次, 0) = Nvl(c_收发.批次, 0) And 性质 = 1;
  
    If Sql%NotFound Then
      Insert Into 药品库存
        (库房id, 药品id, 批次, 性质, 可用数量, 实际数量, 实际金额, 实际差价, 上次采购价, 上次批号, 上次生产日期, 上次产地, 效期, 灭菌效期, 零售价, 商品条码, 内部条码, 平均成本价, 批准文号)
      Values
        (c_收发.库房id, c_收发.药品id, c_收发.批次, 1, c_收发.实际数量, c_收发.实际数量, c_收发.零售金额, c_收发.差价, c_收发.成本价, c_收发.批号, c_收发.生产日期,
         c_收发.产地, c_收发.效期, c_收发.灭菌效期, Decode(Nvl(c_收发.批次, 0), 0, Null, Decode(n_实价卫材, 1, c_收发.零售价, Null)), c_收发.商品条码,
         v_内部条码, c_收发.成本价, c_收发.批准文号);
    End If;
  
    Delete From 药品库存
    Where 库房id = c_收发.库房id And 药品id = c_收发.药品id And Nvl(可用数量, 0) = 0 And Nvl(实际数量, 0) = 0 And Nvl(实际金额, 0) = 0 And
          Nvl(实际差价, 0) = 0;
  
    If Not v_内部条码 Is Null Then
      Update 药品收发记录 Set 内部条码 = v_内部条码 Where ID = c_收发.Id;
    End If;
  
    --更新该材料的成本价
    Update 材料特性 Set 成本价 = c_收发.成本价, 上次售价 = c_收发.零售价 Where 材料id = c_收发.药品id;
  
    --重新计算库存表中的平均成本价
    Update 药品库存
    Set 平均成本价 = Decode(Nvl(批次, 0), 0, Decode((实际金额 - 实际差价) / 实际数量, 0, 上次采购价, (实际金额 - 实际差价) / 实际数量), 上次采购价)
    Where 药品id = c_收发.药品id And Nvl(批次, 0) = Nvl(c_收发.批次, 0) And 库房id = c_收发.库房id And Nvl(实际数量, 0) <> 0 And 性质 = 1;
    If Sql%NotFound Then
      Select 成本价 Into n_平均成本价 From 材料特性 Where 材料id = c_收发.药品id;
      Update 药品库存
      Set 平均成本价 = n_平均成本价
      Where 药品id = c_收发.药品id And 库房id = c_收发.库房id And Nvl(批次, 0) = Nvl(c_收发.批次, 0) And 性质 = 1;
    End If;
  
  End Loop;
Exception
  When Err_Item Then
    Raise_Application_Error(-20101, v_Err_Msg);
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_材料其他入库_Verify;
/

--79521:马政,2014-12-02,卫材流通业务环节加入批准文号
Create Or Replace Procedure Zl_材料其他入库_Strike
(
  行次_In       In Integer,
  原记录状态_In In 药品收发记录.记录状态%Type,
  No_In         In 药品收发记录.No%Type,
  序号_In       In 药品收发记录.序号%Type,
  材料id_In     In 药品收发记录.药品id%Type,
  冲销数量_In   In 药品收发记录.实际数量%Type,
  填制人_In     In 药品收发记录.填制人%Type,
  填制日期_In   In 药品收发记录.填制日期%Type,
  全部冲销_In   In 药品收发记录.实际数量%Type := 0, --1-全部冲销,0-部分冲销,
  摘要_In       In 药品收发记录.摘要%Type := Null
) Is
  Err_Item Exception;
  v_Err_Msg Varchar2(500);

  v_产地 药品收发记录.产地%Type;
  v_批号 药品收发记录.批号%Type;

  n_库房id     药品收发记录.库房id%Type;
  n_入出类别id 药品收发记录.入出类别id%Type;
  n_批次       药品收发记录.批次%Type;
  n_成本价     药品收发记录.成本价%Type;
  n_成本金额   药品收发记录.成本金额%Type;
  n_扣率       药品收发记录.扣率%Type;
  n_零售价     药品收发记录.零售价%Type;
  n_零售金额   药品收发记录.零售金额%Type;
  n_差价       药品收发记录.差价%Type;
  n_零售差价   药品收发记录.差价%Type;
  v_商品条码   药品收发记录.商品条码%Type;
  v_内部条码   药品收发记录.内部条码%Type;
  v_批准文号   药品收发记录.批准文号%Type;

  n_入出系数     药品收发记录.入出系数%Type;
  n_收发id       药品收发记录.Id%Type;
  n_剩余数量     药品收发记录.实际数量%Type;
  n_剩余成本金额 药品收发记录.成本金额%Type;
  n_剩余零售金额 药品收发记录.零售金额%Type;
  n_剩余差价金额 药品收发记录.差价%Type;

  n_冲销数量 药品收发记录.实际数量%Type;
  n_实价卫材 收费项目目录.是否变价%Type;

  n_Batchcount Integer; --原不分批现在分批的材料的数量
  --对冲销数量进行检查
  n_库存数   药品库存.实际数量%Type;
  n_库房分批 Integer;
  n_在用分批 Integer;
  n_分批属性 Integer;
  n_库房     Integer;
  n_分批     Number;
  n_小数     Number(2);

  d_生产日期   药品收发记录.生产日期%Type;
  d_效期       药品收发记录.效期%Type;
  d_灭菌效期   药品收发记录.灭菌效期%Type;
  d_灭菌日期   药品收发记录.灭菌日期%Type;
  n_平均成本价 药品库存.平均成本价%Type;
Begin
  n_冲销数量 := 冲销数量_In;
  Select Nvl(精度, 2) Into n_小数 From 药品卫材精度 Where 性质 = 0 And 类别 = 2 And 内容 = 4 And 单位 = 5;

  If 行次_In = 1 Then
    Update 药品收发记录
    Set 记录状态 = Decode(原记录状态_In, 1, 3, 原记录状态_In + 3)
    Where NO = No_In And 单据 = 17 And 记录状态 = 原记录状态_In;
  
    If Sql%RowCount = 0 Then
      v_Err_Msg := '[ZLSOFT]该单据已经被他人冲销！[ZLSOFT]';
      Raise Err_Item;
    End If;
  End If;

  --主要针对原不分批现在分批的材料，不能对其审核
  Select Count(*)
  Into n_Batchcount
  From 药品收发记录 A, 材料特性 B
  Where a.药品id = b.材料id And a.No = No_In And a.单据 = 17 And Mod(a.记录状态, 3) = 0 And a.药品id + 0 = 材料id_In And
        Nvl(a.批次, 0) = 0 And
        ((Nvl(b.库房分批, 0) = 1 And
        a.库房id Not In (Select 部门id From 部门性质说明 Where (工作性质 Like '发料部门') Or (工作性质 Like '制剂室'))) Or Nvl(b.在用分批, 0) = 1);

  If n_Batchcount > 0 Then
    v_Err_Msg := '[ZLSOFT]该单据中包含有一条原来不分批，现在分批的材料，不能冲销！[ZLSOFT]';
    Raise Err_Item;
  End If;

  Select Sum(a.实际数量) As 剩余数量, Sum(a.成本金额) As 剩余成本金额, Sum(a.零售金额) As 剩余零售金额,
         Sum(To_Number(To_Char(Nvl(用法, '0'), '999999999990.9999999'), '999999999990.9999999')) As 剩余差价金额, a.库房id,
         a.入出类别id, a.入出系数, Nvl(a.批次, 0), a.产地, a.批号, a.生产日期, a.效期, a.灭菌效期, a.灭菌日期, a.成本价, a.扣率, a.零售价, b.库房分批, b.在用分批,
         a.商品条码, a.内部条码, a.批准文号
  Into n_剩余数量, n_剩余成本金额, n_剩余零售金额, n_剩余差价金额, n_库房id, n_入出类别id, n_入出系数, n_批次, v_产地, v_批号, d_生产日期, d_效期, d_灭菌效期, d_灭菌日期,
       n_成本价, n_扣率, n_零售价, n_库房分批, n_在用分批, v_商品条码, v_内部条码, v_批准文号
  From 药品收发记录 A, 材料特性 B
  Where a.No = No_In And a.单据 = 17 And a.药品id = b.材料id And a.药品id + 0 = 材料id_In And a.序号 = 序号_In
  Group By a.库房id, a.入出类别id, a.入出系数, Nvl(a.批次, 0), a.产地,a.批准文号, a.批号, a.生产日期, a.效期, a.灭菌效期, a.灭菌日期, a.成本价, a.扣率, a.零售价, b.库房分批,
           b.在用分批, a.商品条码, a.内部条码;

  --判断该部门是库房还是发料部门
  Begin
    Select Distinct 0
    Into n_库房
    From 部门性质说明
    Where ((工作性质 Like '发料部门') Or (工作性质 Like '制剂室')) And 部门id = n_库房id;
  Exception
    When Others Then
      n_库房 := 1;
  End;

  --根据部门性质,判断分批特性
  If n_库房 = 0 Then
    n_分批属性 := n_在用分批;
  Else
    n_分批属性 := n_库房分批;
  End If;

  --n_分批:(原分批,现分批为批次;否则为零)原不分批,现分批,本过程不考虑
  n_分批 := 0;
  If n_分批属性 = 1 And n_批次 <> 0 Then
    n_分批 := n_批次;
  End If;

  --取库存数
  Begin
    Select Nvl(实际数量, 0)
    Into n_库存数
    From 药品库存
    Where 库房id = n_库房id And 药品id = 材料id_In And Nvl(批次, 0) = n_分批 And 性质 = 1;
  Exception
    When Others Then
      n_库存数 := 0;
  End;
  If Nvl(n_剩余数量, 0) = 0 Then
    v_Err_Msg := '[ZLSOFT]该单据中第' || 序号_In || '行的材料已经被冲销完成,不能再冲！[ZLSOFT]';
    Raise Err_Item;
  End If;

  --如果库存数大于剩余数量,取剩余数量;否则取库存数
  If n_库存数 < n_剩余数量 Then
    If 全部冲销_In = 1 Then
      --不允许
      v_Err_Msg := '[ZLSOFT]该单据中第' || 序号_In || '行的材料冲销的数量大于了剩余的数据，不能冲销！[ZLSOFT]';
      Raise Err_Item;
    End If;
    n_剩余成本金额 := n_库存数 / n_剩余数量 * n_剩余成本金额;
    n_剩余零售金额 := n_库存数 / n_剩余数量 * n_剩余零售金额;
    n_剩余数量     := n_库存数;
    n_剩余差价金额 := n_库存数 / n_剩余数量 * n_剩余差价金额;
  End If;

  If 全部冲销_In = 1 Then
    n_冲销数量 := n_剩余数量;
  End If;

  --冲销数量大于剩余数量，不允许
  If Abs(n_剩余数量) < Abs(n_冲销数量) Then
    v_Err_Msg := '[ZLSOFT]该单据中第' || 序号_In || '行的材料冲销的数量大于了剩余的数据，不能冲销！[ZLSOFT]';
    Raise Err_Item;
  End If;

  Select 是否变价 Into n_实价卫材 From 收费项目目录 Where ID = 材料id_In;

  n_成本金额 := Round(n_冲销数量 / n_剩余数量 * n_剩余成本金额, n_小数);
  n_零售金额 := Round(n_冲销数量 / n_剩余数量 * n_剩余零售金额, n_小数);
  n_差价     := Round(n_零售金额 - n_成本金额, n_小数);
  n_零售差价 := Round(n_冲销数量 / n_剩余数量 * n_剩余差价金额, n_小数);

  Select 药品收发记录_Id.Nextval Into n_收发id From Dual;

  Insert Into 药品收发记录
    (ID, 记录状态, 单据, NO, 序号, 库房id, 入出类别id, 入出系数, 药品id, 批次, 产地, 批号, 生产日期, 效期, 灭菌日期, 灭菌效期, 填写数量, 实际数量, 成本价, 成本金额, 零售价, 零售金额,
     差价, 用法, 摘要, 填制人, 填制日期, 审核人, 审核日期, 商品条码, 内部条码, 批准文号)
  Values
    (n_收发id, Decode(原记录状态_In, 1, 2, 原记录状态_In + 2), 17, No_In, 序号_In, n_库房id, n_入出类别id, n_入出系数, 材料id_In, n_批次, v_产地,
     v_批号, d_生产日期, d_效期, d_灭菌日期, d_灭菌效期, -n_冲销数量, -n_冲销数量, n_成本价, -n_成本金额, n_零售价, -n_零售金额, -n_差价, -n_零售差价, 摘要_In, 填制人_In,
     填制日期_In, 填制人_In, 填制日期_In, v_商品条码, v_内部条码, v_批准文号);

  --更改药品库存表的相应数据

  Update 药品库存
  Set 可用数量 = Nvl(可用数量, 0) - Nvl(n_冲销数量, 0), 实际数量 = Nvl(实际数量, 0) - Nvl(n_冲销数量, 0), 实际金额 = Nvl(实际金额, 0) - Nvl(n_零售金额, 0),
      实际差价 = Nvl(实际差价, 0) - Nvl(n_差价, 0), 上次采购价 = Nvl(n_成本价, 上次采购价), 上次批号 = Nvl(v_批号, 上次批号), 上次产地 = Nvl(v_产地, 上次产地),
      上次生产日期 = Nvl(d_生产日期, 上次生产日期), 灭菌效期 = Nvl(d_灭菌效期, 灭菌效期), 效期 = Nvl(d_效期, 效期), 批准文号 = v_批准文号
  Where 库房id = n_库房id And 药品id = 材料id_In And Nvl(批次, 0) = Nvl(n_分批, 0) And 性质 = 1;

  If Sql%NotFound Then
    Insert Into 药品库存
      (库房id, 药品id, 批次, 性质, 可用数量, 实际数量, 实际金额, 实际差价, 上次采购价, 上次批号, 上次生产日期, 上次产地, 效期, 灭菌效期, 零售价, 平均成本价, 批准文号)
    Values
      (n_库房id, 材料id_In, n_分批, 1, -n_冲销数量, -n_冲销数量, -n_零售金额, -n_差价, n_成本价, v_批号, d_生产日期, v_产地, d_效期, d_灭菌效期,
       Decode(n_实价卫材, 0, Null, Decode(Nvl(n_分批, 0), 0, Null, n_零售价)), n_成本价, v_批准文号);
  End If;

  Delete From 药品库存
  Where 库房id = n_库房id And 药品id = 材料id_In And Nvl(可用数量, 0) = 0 And Nvl(实际数量, 0) = 0 And Nvl(实际金额, 0) = 0 And
        Nvl(实际差价, 0) = 0;

  Zl_材料收发记录_调价修正(n_收发id);

Exception
  When Err_Item Then
    Raise_Application_Error(-20101, v_Err_Msg);
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_材料其他入库_Strike;
/

--80059:胡俊勇,2014-12-01,标记皮试结果后医嘱自动执行
Create Or Replace Procedure Zl_病人医嘱记录_皮试
(
  --功能：填写医嘱皮试结果
  --说明：同时处理病人过敏记录
  --参数：标注_In=比如阳性："(+)",阴性："(-)",免试："免试"，等
  --      结果_IN=0-阴性,1-阳性，NULL=免试
  Id_In         病人医嘱记录.Id%Type,
  标注_In       病人医嘱记录.皮试结果%Type,
  结果_In       病人过敏记录.结果%Type,
  操作员姓名_In Varchar2 := Null,
  皮试时间_In   病人过敏记录.过敏时间%Type := Null,
  过敏反应_In   病人过敏药物.过敏反应%Type := Null
) Is
  --跟该过敏试验相关的所有药品信息项目
  Cursor c_Data Is
    Select Distinct c.病人id, Decode(c.挂号单, Null, c.主页id, d.Id) As 主页id, a.项目id, b.名称
    From 诊疗用法用量 A, 诊疗项目目录 B, 病人医嘱记录 C, 病人挂号记录 D
    Where Nvl(a.性质, 0) = 0 And a.用法id = c.诊疗项目id And a.项目id = b.Id And b.类别 In ('5', '6') And c.挂号单 = d.No(+) And
          c.Id = Id_In;

  v_挂号单   病人医嘱记录.挂号单%Type;
  v_状态     病人医嘱记录.医嘱状态%Type;
  v_医嘱内容 病人医嘱记录.医嘱内容%Type;

  v_Date     Date;
  d_操作时间 Date;
  v_Temp     Varchar2(255);
  v_人员编号 人员表.编号%Type;
  v_人员姓名 病人医嘱状态.操作人员%Type;

  v_Error Varchar2(255);
  Err_Custom Exception;
Begin
  --检查医嘱状态是否正确:并发操作
  Select 挂号单, 医嘱状态, 医嘱内容 Into v_挂号单, v_状态, v_医嘱内容 From 病人医嘱记录 Where ID = Id_In;
  If v_状态 = 4 Then
    v_Error := '医嘱"' || v_医嘱内容 || '"已经作废，不能登记过敏试验结果。';
    Raise Err_Custom;
  End If;
  If v_挂号单 Is Not Null And v_状态 = 1 And Not 结果_In Is Null Then
    v_Error := '医嘱"' || v_医嘱内容 || '"尚未发送，不能登记过敏试验结果。';
    Raise Err_Custom;
  End If;

  --当前操作人员
  If 操作员姓名_In Is Null Then
    v_Temp     := Zl_Identity;
    v_Temp     := Substr(v_Temp, Instr(v_Temp, ';') + 1);
    v_Temp     := Substr(v_Temp, Instr(v_Temp, ',') + 1);
    v_人员编号 := Substr(v_Temp, 1, Instr(v_Temp, ',') - 1);
    v_人员姓名 := Substr(v_Temp, Instr(v_Temp, ',') + 1);
  Else
    v_人员姓名 := 操作员姓名_In;
    Select 编号 Into v_人员编号 From 人员表 Where 姓名 = v_人员姓名;
  End If;

  If 皮试时间_In Is Null Then
    Select Sysdate Into v_Date From Dual;
    d_操作时间 := v_Date;
  Else
    v_Date := 皮试时间_In;
    Select Sysdate Into d_操作时间 From Dual;
  End If;

  --处理医嘱记录:清除免试一样记录
  Update 病人医嘱记录 Set 皮试结果 = 标注_In Where ID = Id_In;
  Insert Into 病人医嘱状态 (医嘱id, 操作类型, 操作人员, 操作时间) Values (Id_In, 10, v_人员姓名, d_操作时间);

  --登记病人过敏记录(即使以前有同类药的过敏结果登记)
  If Not 结果_In Is Null Then
    For r_Data In c_Data Loop
      Insert Into 病人过敏记录
        (ID, 病人id, 主页id, 记录来源, 药物id, 药物名, 结果, 过敏时间, 记录时间, 记录人, 过敏反应)
      Values
        (病人过敏记录_Id.Nextval, r_Data.病人id, r_Data.主页id, 2, r_Data.项目id, r_Data.名称, 结果_In, v_Date, d_操作时间, v_人员姓名, 过敏反应_In);
      If 结果_In = 1 Then
        Update 病人过敏药物
        Set 过敏反应 = 过敏反应_In, 过敏药物id = r_Data.项目id
        Where 病人id = r_Data.病人id And 过敏药物 = r_Data.名称;
        If Sql%RowCount = 0 Then
          Insert Into 病人过敏药物
            (病人id, 过敏药物id, 过敏药物, 过敏反应)
          Values
            (r_Data.病人id, r_Data.项目id, r_Data.名称, 过敏反应_In);
        End If;
      Else
        --如果没有过敏的记录就删除该药品的过敏记录
        Delete From 病人过敏药物 A
        Where a.病人id = r_Data.病人id And a.过敏药物 = r_Data.名称 And a.过敏药物id = r_Data.项目id And Not Exists
         (Select 1
               From 病人过敏记录 B
               Where b.病人id = a.病人id And b.药物id = a.过敏药物id And b.药物名 = a.过敏药物 And 结果 = 1);
      End If;
    End Loop;
    --标记皮试结果时将医嘱自动设为执行完成
    For X In (Select 执行状态, 发送号, 执行部门id From 病人医嘱发送 Where 医嘱id = Id_In) Loop
      If x.执行状态 <> 1 Then
        Zl_病人医嘱执行_Finish(Id_In, x.发送号, Null, 0, v_人员编号, v_人员姓名, x.执行部门id);
      End If;
    End Loop;
  End If;
Exception
  When Err_Custom Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Error || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_病人医嘱记录_皮试;
/

--80255:刘尔旋,2014-11-27,门诊转住院时退预交没填写预交类别的问题
Create Or Replace Procedure Zl_门诊转住院_收费转出
(
  结算序号_In   病人预交记录.结算序号%Type,
  No_In         住院费用记录.No%Type,
  操作员编号_In 住院费用记录.操作员编号%Type,
  操作员姓名_In 住院费用记录.操作员姓名%Type,
  退费时间_In   住院费用记录.发生时间%Type,
  门诊退费_In   Number := 0,
  入院科室id_In 住院费用记录.开单部门id%Type := Null,
  主页id_In     住院费用记录.主页id%Type := Null,
  结算方式_In   病人预交记录.结算方式%Type := Null,
  三方退费_In   Number := 0,
  结帐id_In     病人预交记录.结帐id%Type := Null
) As
  --门诊退费_In:0-门诊转住院立即销帐;1-门诊退费模式
  -- 门诊退费_In为1时:入院科室id_In和主页ID_IN可以不传入
  n_Count      Number(5);
  n_原结帐id   住院费用记录.结帐id%Type;
  n_实收金额   门诊费用记录.实收金额%Type;
  n_组id       财务缴款分组.Id%Type;
  n_病人id     病人信息.病人id%Type;
  v_预交no     病人预交记录.No%Type;
  n_预交金额   病人预交记录.冲预交%Type;
  n_打印id     票据使用明细.打印id%Type;
  n_开单部门id 住院费用记录.开单部门id%Type;
  v_开单人     门诊费用记录.开单人%Type;
  n_结帐id     门诊费用记录.结帐id%Type;
  n_返回值     病人余额.费用余额%Type;
  n_部分退费   Number(5);
  n_退费条数   Number(5);
  v_结算方式   结算方式.名称%Type;
  v_Nos        Varchar2(3000);
  n_退现       Number(5);
  v_结帐ids    Varchar2(3000);
  n_Tempid     病人预交记录.Id%Type;

  Err_Item Exception;
  v_Err_Msg Varchar2(200);

  Procedure Zl_Square_Update
  (
    原结帐id_In 病人预交记录.结帐id%Type,
    现结帐id_In 病人预交记录.结帐id%Type,
    缴款组id_In 病人预交记录.缴款组id%Type,
    退款时间_In 病人预交记录.收款时间%Type,
    结算序号_In 病人预交记录.结算序号%Type,
    结算内容_In Varchar2 := Null
  ) As
    n_记录状态 病人卡结算记录.记录状态%Type;
    n_预交id   病人预交记录.Id%Type;
    v_卡号     病人卡结算记录.卡号%Type;
    n_存在卡片 Number;
    d_停用日期 消费卡目录.停用日期%Type;
    n_最大序号 病人卡结算记录.序号%Type;
    n_序号     病人卡结算记录.序号%Type;
    n_余额     消费卡目录.余额%Type;
    n_接口编号 病人卡结算记录.接口编号%Type;
    d_回收时间 消费卡目录.回收时间%Type;
    n_Id       病人预交记录.Id%Type;
  Begin
    n_预交id := 0;
  
    --处理消费卡,结算卡在上面就已经处理了
    For v_校对 In (Select a.Id As 预交id, c.消费卡id, c.结算金额, c.接口编号, c.卡号, c.序号, c.Id
                 From 病人预交记录 A, 病人卡结算对照 B, 病人卡结算记录 C
                 Where a.Id = b.预交id And b.卡结算id = c.Id And a.记录性质 = 3 And a.记录状态 In (1, 3) And
                       Instr(Nvl(结算内容_In, '_LXH'), ',' || a.结算方式 || ',') = 0 And a.结帐id = 原结帐id_In) Loop
    
      If Nvl(v_校对.消费卡id, 0) <> 0 Then
        Select Max(记录状态)
        Into n_记录状态
        From 病人卡结算记录
        Where 接口编号 = v_校对.接口编号 And 消费卡id = Nvl(v_校对.消费卡id, 0) And 卡号 = v_校对.卡号 And Nvl(序号, 0) = Nvl(v_校对.序号, 0);
      Else
        Select Max(记录状态)
        Into n_记录状态
        From 病人卡结算记录
        Where 接口编号 = v_校对.接口编号 And 消费卡id Is Null And 卡号 = v_校对.卡号 And Nvl(序号, 0) = Nvl(v_校对.序号, 0);
      End If;
    
      If n_记录状态 = 1 Then
        n_记录状态 := 2;
      Else
        n_记录状态 := n_记录状态 + 2;
      End If;
      --多条时,只更新一条
      If n_预交id = 0 Then
        Select 病人预交记录_Id.Nextval Into n_预交id From Dual;
      
        Insert Into 病人预交记录
          (ID, 记录性质, NO, 记录状态, 病人id, 主页id, 摘要, 结算方式, 结算号码, 收款时间, 缴款单位, 单位开户行, 单位帐号, 操作员编号, 操作员姓名, 冲预交, 结帐id, 缴款组id,
           预交类别, 卡类别id, 结算卡序号, 卡号, 交易流水号, 交易说明, 合作单位, 校对标志, 结算序号)
          Select n_预交id, 记录性质, NO, 2, 病人id, 主页id, 摘要, 结算方式, 结算号码, 退款时间_In, 缴款单位, 单位开户行, 单位帐号, 操作员编号_In, 操作员姓名_In,
                 -1 * 冲预交, 现结帐id_In, 缴款组id_In, 预交类别, 卡类别id, Nvl(结算卡序号, v_校对.接口编号), 卡号, 交易流水号, 交易说明, 合作单位, 2, 结算序号_In
          From 病人预交记录 A
          Where ID = v_校对.预交id;
      End If;
    
      If Nvl(v_校对.消费卡id, 0) <> 0 Then
        --消费卡,直接退回卡数据中
        Begin
          Select 卡号, 1, 停用日期, (Select Max(序号) From 消费卡目录 B Where a.卡号 = b.卡号 And a.接口编号 = b.接口编号), 序号, 余额, 接口编号, 回收时间
          Into v_卡号, n_存在卡片, d_停用日期, n_最大序号, n_序号, n_余额, n_接口编号, d_回收时间
          From 消费卡目录 A
          Where ID = v_校对.消费卡id;
        Exception
          When Others Then
            n_存在卡片 := 0;
        End;
      
        --取消停用
        If n_存在卡片 = 0 Then
          v_Err_Msg := '卡号为"' || v_卡号 || '"的消费卡被他人删除，不能再启用该卡片,请检查！';
          Raise Err_Item;
        End If;
        If Nvl(n_序号, 0) < Nvl(n_最大序号, 0) Then
          v_Err_Msg := '不能启用历史发卡记录(卡号为"' || v_卡号 || '"),请检查！';
          Raise Err_Item;
        End If;
        If Nvl(d_停用日期, To_Date('3000-01-01', 'yyyy-mm-dd')) < To_Date('3000-01-01', 'yyyy-mm-dd') Then
          v_Err_Msg := '卡号为"' || v_卡号 || '"的消费卡已经被他人停用，不能再进行退费,请检查！';
          Raise Err_Item;
        End If;
      
        If d_回收时间 < To_Date('3000-01-01', 'yyyy-mm-dd') Then
          v_Err_Msg := '卡号为"' || v_卡号 || '"的消费卡已经回收，不能退费,请检查！';
          Raise Err_Item;
        End If;
        Update 消费卡目录 Set 余额 = Nvl(余额, 0) + v_校对.结算金额 Where ID = Nvl(v_校对.消费卡id, 0);
      End If;
    
      Select 病人卡结算记录_Id.Nextval Into n_Id From Dual;
      Insert Into 病人卡结算记录
        (ID, 接口编号, 消费卡id, 序号, 记录状态, 结算方式, 结算金额, 卡号, 交易流水号, 交易时间, 备注, 结算标志)
        Select n_Id, 接口编号, 消费卡id, 序号, n_记录状态, 结算方式, -1 * v_校对.结算金额, 卡号, 交易流水号, 交易时间, 备注,
               Decode(消费卡id, Null, 0, 0, 0, 1) As 标志
        From 病人卡结算记录
        Where ID = v_校对.Id;
      Insert Into 病人卡结算对照 (预交id, 卡结算id) Values (n_预交id, n_Id);
    
      If n_记录状态 <> 2 And n_记录状态 <> 1 Then
        Update 病人卡结算记录 Set 记录状态 = 3 Where ID = v_校对.Id;
      End If;
    End Loop;
  End;
Begin
  n_组id := Zl_Get组id(操作员姓名_In);

  For r_Nos In (Select Distinct a.No
                From 门诊费用记录 A, (Select Distinct 结帐id From 病人预交记录 Where 结算序号 = 结算序号_In) B
                Where Mod(a.记录性质, 10) = 1 And a.记录状态 In (1, 3) And a.结帐id = b.结帐id) Loop
    v_Nos := v_Nos || ',' || r_Nos.No;
  End Loop;
  v_Nos := Substr(v_Nos, 2);

  For r_结帐ids In (Select Distinct a.结帐id
                  From 门诊费用记录 A
                  Where a.No In (Select Column_Value From Table(f_Str2list(v_Nos))) And Mod(a.记录性质, 10) = 1 And
                        a.记录状态 <> 0) Loop
    v_结帐ids := v_结帐ids || ',' || r_结帐ids.结帐id;
  End Loop;
  v_结帐ids := Substr(v_结帐ids, 2);
  Select Count(a.No), Sum(a.实收金额)
  Into n_Count, n_实收金额
  From 门诊费用记录 A
  Where a.No In (Select Column_Value From Table(f_Str2list(v_Nos))) And Mod(a.记录性质, 10) = 1;
  If n_Count = 0 Or n_实收金额 = 0 Then
    v_Err_Msg := '本次结算不是收费或因并发原因他人操作了该结算,不能转为住院费用.';
    Raise Err_Item;
  End If;

  Select 结帐id, 病人id, 开单部门id, 开单人
  Into n_原结帐id, n_病人id, n_开单部门id, v_开单人
  From 门诊费用记录
  Where 结帐id = Abs(结算序号_In) And Mod(记录性质, 10) = 1 And 记录状态 In (1, 3) And Rownum < 2;

  Begin
    Select 1
    Into n_部分退费
    From 门诊费用记录 A
    Where Mod(a.记录性质, 10) = 1 And a.记录状态 = 2 And a.结帐id In (Select Column_Value From Table(f_Str2list(v_结帐ids))) And
          Rownum < 2;
  Exception
    When Others Then
      n_部分退费 := 0;
  End;

  Begin
    Select 0
    Into n_部分退费
    From 门诊费用记录 A
    Where 记录性质 = 11 And a.结帐id In (Select Column_Value From Table(f_Str2list(v_结帐ids))) And Rownum < 2;
  Exception
    When Others Then
      Null;
  End;
  Begin
    Select Count(Avg(1))
    Into n_退费条数
    From 病人预交记录 A
    Where a.记录性质 = 3 And a.记录状态 <> 0 And 结帐id In (Select Column_Value From Table(f_Str2list(v_结帐ids)))
    Group By a.结算方式;
  Exception
    When Others Then
      n_退费条数 := 0;
  End;
  --1.1作废费用记录
  If 结帐id_In Is Null Then
    Select 病人结帐记录_Id.Nextval Into n_结帐id From Dual;
  Else
    n_结帐id := 结帐id_In;
  End If;
  Insert Into 门诊费用记录
    (ID, NO, 实际票号, 记录性质, 记录状态, 序号, 从属父号, 价格父号, 病人id, 医嘱序号, 门诊标志, 姓名, 性别, 年龄, 标识号, 付款方式, 费别, 病人科室id, 收费类别, 收费细目id, 计算单位,
     付数, 发药窗口, 数次, 加班标志, 附加标志, 收入项目id, 收据费目, 记帐费用, 标准单价, 应收金额, 实收金额, 开单部门id, 开单人, 执行部门id, 划价人, 执行人, 执行状态, 执行时间, 操作员编号,
     操作员姓名, 发生时间, 登记时间, 结帐id, 结帐金额, 保险项目否, 保险大类id, 统筹金额, 摘要, 是否上传, 保险编码, 费用类型, 缴款组id)
    Select 病人费用记录_Id.Nextval, a.No, a.实际票号, a.记录性质, 2, a.序号, a.从属父号, a.价格父号, a.病人id, a.医嘱序号, a.门诊标志, a.姓名, a.性别, a.年龄,
           a.标识号, a.付款方式, a.费别, a.病人科室id, a.收费类别, a.收费细目id, a.计算单位, a.付数, a.发药窗口, -1 * a.数次, a.加班标志, a.附加标志, a.收入项目id,
           a.收据费目, a.记帐费用, a.标准单价, -1 * a.应收金额, -1 * a.实收金额, a.开单部门id, a.开单人, a.执行部门id, a.划价人, a.执行人, -1, a.执行时间,
           操作员编号_In, 操作员姓名_In, a.发生时间, 退费时间_In, n_结帐id, -1 * a.结帐金额, a.保险项目否, a.保险大类id, a.统筹金额, a.摘要,
           Decode(Nvl(a.附加标志, 0), 9, 1, 0), a.保险编码, a.费用类型, n_组id
    From 门诊费用记录 A
    Where a.No In (Select Column_Value From Table(f_Str2list(v_Nos))) And Mod(a.记录性质, 10) = 1 And a.记录状态 = 1;

  --Update 门诊费用记录 Set 记录状态 = 3 Where NO = No_In And 记录性质 = 1 And 记录状态 = 1;
  --1.2作废预交记录
  --作废冲预交部分
  If n_部分退费 = 0 Then
    For r_Prepay In (Select NO, 实际票号, 病人id, 主页id, 科室id, 结算方式, 结算号码, 缴款单位, 单位开户行, 单位帐号, 收款时间, -1 * Sum(冲预交) As 冲预交, 卡类别id,
                            结算卡序号, 卡号, 交易流水号, 交易说明, 合作单位
                     From 病人预交记录 A
                     Where 记录性质 In (1, 11) And a.结帐id In (Select Column_Value From Table(f_Str2list(v_结帐ids)))
                     Group By n_Tempid, NO, 实际票号, 病人id, 主页id, 科室id, 结算方式, 结算号码, 缴款单位, 单位开户行, 单位帐号, 收款时间, 卡类别id, 结算卡序号, 卡号,
                              交易流水号, 交易说明, 合作单位) Loop
      Select 病人预交记录_Id.Nextval Into n_Tempid From Dual;
      Insert Into 病人预交记录
        (ID, NO, 实际票号, 记录性质, 记录状态, 病人id, 主页id, 科室id, 金额, 结算方式, 结算号码, 摘要, 缴款单位, 单位开户行, 单位帐号, 收款时间, 操作员姓名, 操作员编号, 冲预交,
         结帐id, 卡类别id, 结算卡序号, 卡号, 交易流水号, 交易说明, 合作单位, 结算序号, 预交类别)
        Select n_Tempid, r_Prepay.No, r_Prepay.实际票号, 11, 1, r_Prepay.病人id, r_Prepay.主页id, r_Prepay.科室id, Null,
               r_Prepay.结算方式, r_Prepay.结算号码, Null, r_Prepay.缴款单位, r_Prepay.单位开户行, r_Prepay.单位帐号, r_Prepay.收款时间, 操作员姓名_In,
               操作员编号_In, r_Prepay.冲预交, n_结帐id, r_Prepay.卡类别id, r_Prepay.结算卡序号, r_Prepay.卡号, r_Prepay.交易流水号,
               r_Prepay.交易说明, r_Prepay.合作单位, -1 * n_结帐id, 1
        From Dual;
    End Loop;
  
    For v_预交 In (Select Nvl(预交类别, 2) As 预交类别, Nvl(Sum(Nvl(冲预交, 0)), 0) As 预交金额
                 From 病人预交记录 A
                 Where 记录性质 In (1, 11) And a.结帐id In (Select Column_Value From Table(f_Str2list(v_结帐ids))) And
                       a.结帐id <> n_结帐id
                 Group By Nvl(预交类别, 2)
                 Having Sum(Nvl(冲预交, 0)) <> 0) Loop
    
      Update 病人余额
      Set 预交余额 = Nvl(预交余额, 0) + Nvl(v_预交.预交金额, 0)
      Where 病人id = n_病人id And 类型 = Nvl(v_预交.预交类别, 2) And 性质 = 1
      Returning 预交余额 Into n_返回值;
      If Sql%RowCount = 0 Then
        Insert Into 病人余额 (病人id, 类型, 预交余额, 性质) Values (n_病人id, Nvl(v_预交.预交类别, 2), n_预交金额, 1);
        n_返回值 := n_预交金额;
      End If;
      If Nvl(n_返回值, 0) = 0 Then
        Delete From 病人余额 Where 病人id = n_病人id And 性质 = 1 And Nvl(预交余额, 0) = 0 And Nvl(费用余额, 0) = 0;
      End If;
    End Loop;
  Else
    If n_退费条数 = 0 And Nvl(门诊退费_In, 0) = 0 Then
      --只使用了预交，原样退回预交
      For r_Prepay In (Select NO, 实际票号, 病人id, 主页id, 科室id, Max(结算方式) As 结算方式, 结算号码, 缴款单位, 单位开户行, 单位帐号, 收款时间,
                              -1 * Sum(冲预交) As 冲预交, 卡类别id, 结算卡序号, 卡号, 交易流水号, 交易说明, 合作单位
                       From 病人预交记录 A
                       Where 记录性质 In (1, 11) And a.结帐id In (Select Column_Value From Table(f_Str2list(v_结帐ids)))
                       Group By n_Tempid, NO, 实际票号, 病人id, 主页id, 科室id, 结算号码, 缴款单位, 单位开户行, 单位帐号, 收款时间, 卡类别id, 结算卡序号, 卡号,
                                交易流水号, 交易说明, 合作单位) Loop
        Select 病人预交记录_Id.Nextval Into n_Tempid From Dual;
        Insert Into 病人预交记录
          (ID, NO, 实际票号, 记录性质, 记录状态, 病人id, 主页id, 科室id, 金额, 结算方式, 结算号码, 摘要, 缴款单位, 单位开户行, 单位帐号, 收款时间, 操作员姓名, 操作员编号, 冲预交,
           结帐id, 卡类别id, 结算卡序号, 卡号, 交易流水号, 交易说明, 合作单位, 结算序号, 预交类别)
          Select n_Tempid, r_Prepay.No, r_Prepay.实际票号, 11, 1, r_Prepay.病人id, r_Prepay.主页id, r_Prepay.科室id, Null,
                 r_Prepay.结算方式, r_Prepay.结算号码, Null, r_Prepay.缴款单位, r_Prepay.单位开户行, r_Prepay.单位帐号, r_Prepay.收款时间,
                 操作员姓名_In, 操作员编号_In, r_Prepay.冲预交, n_结帐id, r_Prepay.卡类别id, r_Prepay.结算卡序号, r_Prepay.卡号, r_Prepay.交易流水号,
                 r_Prepay.交易说明, r_Prepay.合作单位, -1 * n_结帐id, 1
          From Dual;
        Select -1 * 冲预交 Into n_预交金额 From 病人预交记录 Where ID = n_Tempid;
        Update 病人余额
        Set 预交余额 = Nvl(预交余额, 0) + Nvl(n_预交金额, 0)
        Where 病人id = n_病人id And 类型 = 1 And 性质 = 1
        Returning 预交余额 Into n_返回值;
        If Sql%RowCount = 0 Then
          Insert Into 病人余额 (病人id, 类型, 预交余额, 性质) Values (n_病人id, 1, n_预交金额, 1);
          n_返回值 := n_预交金额;
        End If;
        If Nvl(n_返回值, 0) = 0 Then
          Delete From 病人余额 Where 病人id = n_病人id And 性质 = 1 And Nvl(预交余额, 0) = 0 And Nvl(费用余额, 0) = 0;
        End If;
      End Loop;
    Else
      Select 名称 Into v_结算方式 From 结算方式 Where 性质 = 1;
      Select 病人预交记录_Id.Nextval Into n_Tempid From Dual;
      Insert Into 病人预交记录
        (ID, NO, 实际票号, 记录性质, 记录状态, 病人id, 主页id, 科室id, 金额, 结算方式, 结算号码, 摘要, 缴款单位, 单位开户行, 单位帐号, 收款时间, 操作员姓名, 操作员编号, 冲预交,
         结帐id, 卡类别id, 结算卡序号, 卡号, 交易流水号, 交易说明, 合作单位, 结算序号)
        Select n_Tempid, Max(NO), Max(实际票号), 3, 3, 病人id, 主页id, 科室id, Null, v_结算方式, Max(结算号码), '预交临时记录', Null, Null, Null,
               Max(收款时间), 操作员姓名_In, 操作员编号_In, Sum(冲预交), n_原结帐id, Null, Null, Null, Null, Null, Null, -1 * n_原结帐id
        From 病人预交记录 A
        Where 记录性质 In (1, 11) And a.结帐id In (Select Column_Value From Table(f_Str2list(v_结帐ids)))
        Group By n_Tempid, 3, 3, 病人id, 主页id, 科室id, Null, v_结算方式, '预交临时记录', 操作员姓名_In, 操作员编号_In, n_原结帐id;
    End If;
  End If;

  --作废门诊缴费及医保部分
  Insert Into 病人预交记录
    (ID, 记录性质, NO, 记录状态, 病人id, 主页id, 摘要, 结算方式, 结算号码, 收款时间, 缴款单位, 单位开户行, 单位帐号, 操作员编号, 操作员姓名, 冲预交, 结帐id, 缴款组id, 预交类别,
     卡类别id, 结算卡序号, 卡号, 交易流水号, 交易说明, 合作单位, 结算序号)
    Select 病人预交记录_Id.Nextval, 记录性质, NO, 2, 病人id, 主页id, 摘要, 结算方式, 结算号码, 退费时间_In, 缴款单位, 单位开户行, 单位帐号, 操作员编号_In, 操作员姓名_In, 0,
           n_结帐id, n_组id, 预交类别, 卡类别id, 结算卡序号, 卡号, 交易流水号, 交易说明, 合作单位, -1 * n_结帐id
    From 病人预交记录 A, 结算方式 B
    Where a.记录性质 = 3 And a.记录状态 = 1 And a.结帐id In (Select Column_Value From Table(f_Str2list(v_结帐ids))) And
          a.结算方式 = b.名称 And b.性质 <> 8;

  Update 病人预交记录
  Set 记录状态 = 3
  Where 记录性质 = 3 And 记录状态 = 1 And 结帐id In (Select Column_Value From Table(f_Str2list(v_结帐ids)));

  --2.票据收回
  --可能以前没有打印,无收回
  For r_Nos In (Select Distinct a.No
                From 门诊费用记录 A
                Where Mod(a.记录性质, 10) = 1 And a.记录状态 In (1, 3) And
                      a.结帐id In (Select Column_Value From Table(f_Str2list(v_结帐ids)))) Loop
  
    Select Nvl(Max(ID), 0) Into n_打印id From 票据打印内容 Where 数据性质 = 1 And NO = r_Nos.No;
    If n_打印id > 0 Then
      --多张单据循环调用时只能收回一次
      Select Count(打印id) Into n_Count From 票据使用明细 Where 票种 = 1 And 性质 = 2 And 打印id = n_打印id;
      If n_Count = 0 Then
        Insert Into 票据使用明细
          (ID, 票种, 号码, 性质, 原因, 领用id, 打印id, 使用时间, 使用人)
          Select 票据使用明细_Id.Nextval, 票种, 号码, 2, 2, 领用id, 打印id, 退费时间_In, 操作员姓名_In
          From 票据使用明细
          Where 打印id = n_打印id And 票种 = 1 And 性质 = 1;
      End If;
    End If;
  End Loop;

  --3.缴款数据处理(
  --   现有两种情况:
  --    1. 转出过程直接销帐的,则缴款数据不增加;
  --    2. 先转出,再到门诊退款退票,则需要进行缴款数据处理
  If Nvl(门诊退费_In, 0) = 1 Then
    For c_预交 In (Select a.结算方式, Sum(a.冲预交) As 冲预交, 2 As 预交类别, a.卡类别id, a.结算卡序号, a.卡号, Min(a.交易流水号) As 交易流水号,
                        Min(a.交易说明) As 交易说明, Min(a.合作单位) As 合作单位, b.性质
                 From 病人预交记录 A, 结算方式 B
                 Where a.记录性质 = 3 And a.记录状态 In (2, 3) And
                       a.结帐id In (Select Column_Value From Table(f_Str2list(v_结帐ids))) And a.结算方式 = b.名称 And
                       b.性质 In (1, 2, 3, 4, 7, 8)
                 Group By a.结算方式, 预交类别, a.卡类别id, a.结算卡序号, a.卡号, b.性质
                 Having Sum(a.冲预交) <> 0) Loop
      If c_预交.性质 In (3, 4, 8) Then
        v_结算方式 := c_预交.结算方式;
      Else
        If 结算方式_In Is Null Then
          Select 名称 Into v_结算方式 From 结算方式 Where 性质 = 1 And Rownum < 2;
        Else
          v_结算方式 := 结算方式_In;
        End If;
      End If;
      If c_预交.性质 = 7 Then
        Begin
          Select 1 Into n_退现 From 医疗卡类别 Where ID = c_预交.卡类别id And 是否退现 = 1;
        Exception
          When Others Then
            n_退现 := 0;
        End;
        If n_部分退费 = 0 Then
          If n_退现 = 0 And 三方退费_In = 0 Then
            v_Err_Msg := '存在无法退现的三方账户,无法进行退费!';
            Raise Err_Item;
          End If;
          If n_退现 = 1 Then
            If 结算方式_In Is Null Then
              Select 名称 Into v_结算方式 From 结算方式 Where 性质 = 1 And Rownum < 2;
            Else
              v_结算方式 := 结算方式_In;
            End If;
          End If;
          If 三方退费_In = 1 Then
            v_结算方式 := c_预交.结算方式;
          End If;
        Else
          If 三方退费_In = 1 Then
            v_结算方式 := c_预交.结算方式;
          End If;
          If n_退现 = 0 And 三方退费_In = 0 Then
            v_Err_Msg := '存在无法退现的三方账户,无法进行退费!';
            Raise Err_Item;
          End If;
        End If;
      End If;
      If c_预交.性质 = 8 Then
        Zl_Square_Update(n_原结帐id, n_结帐id, n_组id, 退费时间_In, -1 * n_结帐id);
      End If;
      Update 人员缴款余额
      Set 余额 = Nvl(余额, 0) - c_预交.冲预交
      Where 收款员 = 操作员姓名_In And 性质 = 1 And 结算方式 = v_结算方式
      Returning 余额 Into n_返回值;
      If Sql%RowCount = 0 Then
        Insert Into 人员缴款余额
          (收款员, 结算方式, 性质, 余额)
        Values
          (操作员姓名_In, v_结算方式, 1, -1 * c_预交.冲预交);
        n_返回值 := c_预交.冲预交;
      End If;
      If Nvl(n_返回值, 0) = 0 Then
        Delete From 人员缴款余额
        Where 收款员 = 操作员姓名_In And 性质 = 1 And 结算方式 = v_结算方式 And Nvl(余额, 0) = 0;
      End If;
      --部分退费情况，退原预交记录
      If c_预交.性质 <> 8 Then
        Update 病人预交记录
        Set 冲预交 = 冲预交 + (-1 * c_预交.冲预交)
        Where 记录性质 = 3 And 记录状态 = 2 And 结帐id = n_结帐id And 结算方式 = v_结算方式;
        If Sql%RowCount = 0 Then
          Insert Into 病人预交记录
            (ID, NO, 实际票号, 记录性质, 记录状态, 病人id, 主页id, 科室id, 冲预交, 结算方式, 结算号码, 收款时间, 缴款单位, 单位开户行, 单位帐号, 操作员编号, 操作员姓名, 摘要,
             缴款组id, 卡类别id, 结算卡序号, 卡号, 交易流水号, 交易说明, 合作单位, 结帐id, 结算序号, 校对标志)
          Values
            (病人预交记录_Id.Nextval, Null, Null, 3, 2, n_病人id, 主页id_In, 入院科室id_In, -1 * c_预交.冲预交, v_结算方式, Null, 退费时间_In,
             Null, Null, Null, 操作员编号_In, 操作员姓名_In, '', n_组id, c_预交.卡类别id, c_预交.结算卡序号, c_预交.卡号, c_预交.交易流水号, c_预交.交易说明,
             c_预交.合作单位, n_结帐id, -1 * n_结帐id, 0);
        End If;
      End If;
    End Loop;
  
    --更新费用审核记录
    Update 费用审核记录
    Set 记录状态 = 2
    Where 费用id In (Select a.Id
                   From 门诊费用记录 A
                   Where a.No In (Select Column_Value From Table(f_Str2list(v_Nos))) And Mod(a.记录性质, 10) = 1 And
                         a.记录状态 In (1, 3)) And 性质 = 1;
    --作废门诊记录
    For r_Nos In (Select Distinct NO
                  From 门诊费用记录
                  Where Mod(记录性质, 10) = 1 And 记录状态 In (1, 3) And
                        结帐id In (Select Column_Value From Table(f_Str2list(v_结帐ids)))) Loop
      Update 门诊费用记录 Set 记录状态 = 3 Where NO = r_Nos.No And Mod(记录性质, 10) = 1 And 记录状态 = 1;
    End Loop;
    For r_Clinic In (Select Min(a.记录性质) As 记录性质, a.No, a.序号, a.从属父号, a.价格父号, a.病人id, a.姓名, a.性别, a.年龄, a.病人科室id, a.费别,
                            a.收费类别, a.收费细目id, a.计算单位, a.保险项目否, a.保险大类id, a.保险编码, a.费用类型, a.发药窗口, a.付数, Sum(a.数次) As 数次,
                            a.加班标志, a.附加标志, a.婴儿费, a.收入项目id, a.收据费目, a.标准单价, Sum(a.应收金额) As 应收金额, Sum(a.实收金额) As 实收金额,
                            Sum(a.统筹金额) As 统筹金额, a.开单部门id, a.开单人, a.执行部门id, a.划价人, a.记帐单id, a.是否急诊, a.发生时间,
                            Min(a.实际票号) As 实际票号
                     From 门诊费用记录 A
                     Where a.No In (Select Column_Value From Table(f_Str2list(v_Nos))) And Mod(a.记录性质, 10) = 1 And
                           a.记录状态 In (2, 3) And a.附加标志 Not In (8, 9)
                     Group By a.No, a.序号, a.从属父号, a.价格父号, a.病人id, a.姓名, a.性别, a.年龄, a.病人科室id, a.费别, a.收费类别, a.收费细目id,
                              a.计算单位, a.保险项目否, a.保险大类id, a.保险编码, a.费用类型, a.发药窗口, a.付数, a.加班标志, a.附加标志, a.婴儿费, a.收入项目id,
                              a.收据费目, a.标准单价, a.开单部门id, a.开单人, a.执行部门id, a.划价人, a.记帐单id, a.是否急诊, a.发生时间
                     Having Sum(a.数次) <> 0) Loop
      Insert Into 门诊费用记录
        (ID, 记录性质, NO, 实际票号, 记录状态, 序号, 从属父号, 价格父号, 门诊标志, 病人id, 标识号, 姓名, 性别, 年龄, 病人科室id, 费别, 收费类别, 收费细目id, 计算单位, 保险项目否,
         保险大类id, 保险编码, 费用类型, 发药窗口, 付数, 数次, 加班标志, 附加标志, 婴儿费, 收入项目id, 收据费目, 标准单价, 应收金额, 实收金额, 统筹金额, 记帐费用, 开单部门id, 开单人,
         发生时间, 登记时间, 执行部门id, 划价人, 操作员编号, 操作员姓名, 记帐单id, 摘要, 是否急诊, 缴款组id, 结帐id, 结帐金额, 执行状态)
      Values
        (病人费用记录_Id.Nextval, r_Clinic.记录性质, r_Clinic.No, r_Clinic.实际票号, 2, r_Clinic.序号, r_Clinic.从属父号, r_Clinic.价格父号, 1,
         r_Clinic.病人id, '', r_Clinic.姓名, r_Clinic.性别, r_Clinic.年龄, r_Clinic.病人科室id, r_Clinic.费别, r_Clinic.收费类别,
         r_Clinic.收费细目id, r_Clinic.计算单位, r_Clinic.保险项目否, r_Clinic.保险大类id, r_Clinic.保险编码, r_Clinic.费用类型, r_Clinic.发药窗口,
         r_Clinic.付数, -1 * r_Clinic.数次, r_Clinic.加班标志, r_Clinic.附加标志, r_Clinic.婴儿费, r_Clinic.收入项目id, r_Clinic.收据费目,
         r_Clinic.标准单价, -1 * r_Clinic.应收金额, -1 * r_Clinic.实收金额, -1 * r_Clinic.统筹金额, 0, r_Clinic.开单部门id, r_Clinic.开单人,
         r_Clinic.发生时间, 退费时间_In, r_Clinic.执行部门id, r_Clinic.划价人, 操作员编号_In, 操作员姓名_In, r_Clinic.记帐单id, '', r_Clinic.是否急诊,
         n_组id, n_结帐id, -1 * r_Clinic.实收金额, -1);
    End Loop;
  Else
    --4.退款转预交(不产生票据,由操作员通过重打进行)
  
    For r_Pay In (Select a.结算方式, Sum(a.冲预交) As 冲预交, 2 As 预交类别, a.卡类别id, a.结算卡序号, a.卡号, Min(a.交易流水号) As 交易流水号,
                         Min(a.交易说明) As 交易说明, Min(a.合作单位) As 合作单位, b.性质
                  From 病人预交记录 A, 结算方式 B
                  Where a.记录性质 = 3 And a.记录状态 In (2, 3) And
                        a.结帐id In (Select Column_Value From Table(f_Str2list(v_结帐ids))) And a.结算方式 = b.名称 And
                        b.性质 In (1, 2, 3, 4, 7, 8)
                  Group By a.结算方式, 预交类别, a.卡类别id, a.结算卡序号, a.卡号, b.性质
                  Having Sum(a.冲预交) <> 0) Loop
      --4.1产生预交款单据 (不存在部分退费的情况)
      --所有单据,按规则生成预交款单据
      --因为收款后立即缴款,所以人员缴款余额无变化
      If n_部分退费 = 0 Or n_退费条数 = 1 Then
        v_结算方式 := r_Pay.结算方式;
      Else
        Select 名称 Into v_结算方式 From 结算方式 Where 性质 = 1 And Rownum < 2;
      End If;
      If r_Pay.性质 = 7 Then
        Begin
          Select 1 Into n_退现 From 医疗卡类别 Where ID = r_Pay.卡类别id And 是否退现 = 1;
        Exception
          When Others Then
            n_退现 := 0;
        End;
        If n_部分退费 = 0 Then
          If 三方退费_In = 1 Then
            v_结算方式 := r_Pay.结算方式;
          End If;
          If n_退现 = 0 And 三方退费_In = 0 Then
            v_结算方式 := r_Pay.结算方式;
          End If;
          If n_退现 = 1 And 三方退费_In = 0 Then
            Select 名称 Into v_结算方式 From 结算方式 Where 性质 = 1 And Rownum < 2;
          End If;
        Else
          If 三方退费_In = 1 Then
            v_结算方式 := r_Pay.结算方式;
          End If;
          If n_退现 = 0 And 三方退费_In = 0 Then
            v_结算方式 := r_Pay.结算方式;
          End If;
        End If;
      End If;
    
      If r_Pay.性质 = 8 Then
        Zl_Square_Update(n_原结帐id, n_结帐id, n_组id, 退费时间_In, -1 * n_结帐id);
      End If;
      If (r_Pay.性质 Not In (3, 4, 7, 8)) Or (r_Pay.性质 = 7 And 三方退费_In = 0) Then
        Update 病人预交记录
        Set 金额 = 金额 + r_Pay.冲预交
        Where 记录性质 = 1 And 记录状态 = 1 And 收款时间 = 退费时间_In And 病人id + 0 = n_病人id And 结算方式 = v_结算方式 And Nvl(预交类别, 2) = 2;
        If Sql%RowCount = 0 Then
          v_预交no := Nextno(11);
          Insert Into 病人预交记录
            (ID, NO, 实际票号, 记录性质, 记录状态, 病人id, 主页id, 科室id, 金额, 结算方式, 结算号码, 收款时间, 缴款单位, 单位开户行, 单位帐号, 操作员编号, 操作员姓名, 摘要,
             缴款组id, 预交类别)
          Values
            (病人预交记录_Id.Nextval, v_预交no, Null, 1, 1, n_病人id, 主页id_In, 入院科室id_In, r_Pay.冲预交, v_结算方式, Null, 退费时间_In, Null,
             Null, Null, 操作员编号_In, 操作员姓名_In, '门诊转住院预交', n_组id, 2);
        End If;
      
        --病人余额
        Update 病人余额
        Set 预交余额 = Nvl(预交余额, 0) + r_Pay.冲预交
        Where 性质 = 1 And 病人id = n_病人id And 类型 = 2
        Returning 预交余额 Into n_返回值;
        If Sql%RowCount = 0 Then
          Insert Into 病人余额 (病人id, 性质, 类型, 预交余额, 费用余额) Values (n_病人id, 1, 2, r_Pay.冲预交, 0);
          n_返回值 := r_Pay.冲预交;
        End If;
        If Nvl(n_返回值, 0) = 0 Then
          Delete From 病人余额 Where 病人id = n_病人id And 性质 = 1 And Nvl(预交余额, 0) = 0 And Nvl(费用余额, 0) = 0;
        End If;
      End If;
      --4.2缴款数据处理
      --   因为没有实际收病人的钱,所以不处理
      --部分退费情况，退原预交记录
      If r_Pay.性质 In (3, 4, 8) Or (r_Pay.性质 = 7 And 三方退费_In = 1) Then
        Update 人员缴款余额
        Set 余额 = Nvl(余额, 0) - r_Pay.冲预交
        Where 收款员 = 操作员姓名_In And 性质 = 1 And 结算方式 = r_Pay.结算方式
        Returning 余额 Into n_返回值;
        If Sql%RowCount = 0 Then
          Insert Into 人员缴款余额
            (收款员, 结算方式, 性质, 余额)
          Values
            (操作员姓名_In, r_Pay.结算方式, 1, -1 * r_Pay.冲预交);
          n_返回值 := r_Pay.冲预交;
        End If;
        If Nvl(n_返回值, 0) = 0 Then
          Delete From 人员缴款余额
          Where 收款员 = 操作员姓名_In And 性质 = 1 And 结算方式 = r_Pay.结算方式 And Nvl(余额, 0) = 0;
        End If;
      End If;
    
      If r_Pay.性质 <> 8 Then
        Update 病人预交记录
        Set 冲预交 = 冲预交 + (-1 * r_Pay.冲预交)
        Where 记录性质 = 3 And 记录状态 = 2 And 结帐id = n_结帐id And 结算方式 = v_结算方式;
        If Sql%RowCount = 0 Then
          Insert Into 病人预交记录
            (ID, NO, 实际票号, 记录性质, 记录状态, 病人id, 主页id, 科室id, 冲预交, 结算方式, 结算号码, 收款时间, 缴款单位, 单位开户行, 单位帐号, 操作员编号, 操作员姓名, 摘要,
             缴款组id, 卡类别id, 结算卡序号, 卡号, 交易流水号, 交易说明, 合作单位, 结帐id, 结算序号, 校对标志)
          Values
            (病人预交记录_Id.Nextval, Null, Null, 3, 2, n_病人id, 主页id_In, 入院科室id_In, -1 * r_Pay.冲预交, v_结算方式, Null, 退费时间_In,
             Null, Null, Null, 操作员编号_In, 操作员姓名_In, '', n_组id, r_Pay.卡类别id, r_Pay.结算卡序号, r_Pay.卡号, r_Pay.交易流水号,
             r_Pay.交易说明, r_Pay.合作单位, n_结帐id, -1 * n_结帐id, 0);
        End If;
      End If;
    End Loop;
  End If;
  Delete From 病人预交记录 Where 结帐id = n_原结帐id And 摘要 = '预交临时记录' And 记录性质 = 3;
  Delete From 病人预交记录 Where 结帐id = n_结帐id And 记录性质 = 3 And 记录状态 = 2 And 冲预交 = 0;
Exception
  When Err_Item Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Err_Msg || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_门诊转住院_收费转出;
/

--79592:马政,2014-11-27,卫材注册证检验
Create Or Replace Procedure Zl_卫生材料_Insert
(
  诊疗id_In         In 材料特性.诊疗id%Type,
