  指导差价率_In     In 材料特性.指导差价率%Type := Null,
  费用类型_In       In 收费项目目录.费用类型%Type := Null,
  服务对象_In       In 收费项目目录.服务对象%Type := Null,
  屏蔽费别_In       In 收费项目目录.屏蔽费别%Type := 0,
