) Is
  -------------------------------------------------------------------------------------------------- 
  --功能:获取病人的唯一标识(病人ID) 
  --入参:Xml_In: 
  --   <IN> 
  --     <SFZ>身份证号</SFZ>
  --     <XM>姓名</XM>
  --     <KH>卡号</KH> 
  --     <KLB>卡类别</KLB>
  --    </IN>
  --出参:Xml_Out 
  -- <OUTPUT>
  --   <BRID>病人ID</BRID>    
  --   <ERROR><MSG>错误信息</MSG></ERROR> 
  --  </OUTPUT>

  -------------------------------------------------------------------------------------------------- 
  v_Err_Msg Varchar2(200);
  Err_Item Exception;

  x_Templet Xmltype; --模板XML 

  v_身份证 Varchar2(50);
  v_姓名   Varchar2(100);
  v_卡号   Varchar2(100);
  v_卡类别 Varchar2(100);

  n_病人id 病人信息.病人id%Type;

  v_Temp Varchar2(32767); --临时XML 
Begin
  x_Templet := Xmltype('<OUTPUT></OUTPUT>');

