  From Table(Xmlsequence(Extract(Xml_In, 'IN'))) A;

  If Nvl(v_卡类别, '-') <> '-' And Nvl(v_卡号, '-') <> '-' Then
  
    Select Max(病人id)
    Into n_病人id
    From 病人医疗卡信息
    Where 卡类别id = (Select Max(ID) From 医疗卡类别 Where 名称 = v_卡类别 And Nvl(是否启用, 0) = 1) And 卡号 = v_卡号;
  End If;
  If Nvl(n_病人id, 0) = 0 And Not v_身份证 Is Null Then
    Begin
      Select 病人id Into n_病人id From 病人信息 Where 身份证号 = v_身份证;
    Exception
      When Others Then
        n_病人id := Null;
    End;
    If Nvl(n_病人id, 0) = 0 Then
      Select Max(病人id)
      Into n_病人id
      From 病人医疗卡信息
      Where 卡类别id = (Select Max(ID) From 医疗卡类别 Where 名称 Like '%身份证%' And Nvl(是否启用, 0) = 1) And 卡号 = v_身份证;
    End If;
  End If;

  If Nvl(n_病人id, 0) = 0 Then
    Select Max(病人id)
    Into n_病人id
    From 病人医疗卡信息
    Where 卡类别id = (Select Max(ID) From 医疗卡类别 Where 名称 Like '%身份证%' And Nvl(是否启用, 0) = 1) And 卡号 = v_身份证;
  End If;

  If Nvl(n_病人id, 0) <> 0 Then
    v_Temp := '<BRID>' || n_病人id || '</BRID>';
    Select Appendchildxml(x_Templet, '/OUTPUT', Xmltype(v_Temp)) Into x_Templet From Dual;
  End If;
  Xml_Out := x_Templet;
Exception
  When Err_Item Then
    v_Temp := '<ERROR><MSG>' || v_Err_Msg || '</MSG></ERROR>';
    Select Appendchildxml(x_Templet, '/OUTPUT', Xmltype(v_Temp)) Into x_Templet From Dual;
    Xml_Out := x_Templet;
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_Third_Getpati_Unique;
/

--77834:刘兴洪,2014-12-17,医保补充结算,移动平台接口整理
Create Or Replace Procedure Zl_Third_Waitingpay
(
  Xml_In  In Xmltype,
  Xml_Out Out Xmltype
) Is
  -------------------------------------------------------------------------------------------------- 
  --功能:获取待付款收费单(即付款单据)
  --入参:Xml_In: 
  --<IN>
  --    <GHDH>挂号单号</GHDH>
  --</IN>
  --出参:Xml_Out 
  --<OUTPUT>
  --        <FYLIST> 如果为空表示未找到数据
  --            <FY>
  --                <NO>单据号</NO>                           
  --                <KDSJ>开单时间</SJ>                           
  --                <KDKS>开单科室</KDKS>                     
  --                <YS></YS>                           //医生
  --                <FMLIST>
  --                    <FM>
  --                        <MC></MC>                           //费目名称
  --                        <MXLIST>
  --                            <MX>
  --                                <XMMC></XMMC>           //项目名称
  --                                <GG></GG>                   //规格
  --                                <DW></DW>               //单位
  --                                <SL></SL>                   //数量
  --                                <DJ></DJ>                   //单价
  --                                <YSJE></YSJE>               //应收金额
  --                                <SSJE></SSJE>               //实收金额
  --                            </MX>
  --                        </MXLIST>
  --                    </FM>
  --                </FMLIST>
  --            </FY>
  --        </FYLIST>
  --        <ERROR><MSG></MSG></ERROR>                  //如果有错误返回
  --</OUTPUT>

  -------------------------------------------------------------------------------------------------- 
  v_Err_Msg Varchar2(200);
  Err_Item Exception;
  x_Templet Xmltype; --模板XML 

  v_挂号单 Varchar2(10);

  n_Count Number(18);

  v_Temp Varchar2(32767); --临时XML 
  v_No   Varchar2(50);
  v_费目 Varchar2(50);

Begin
  x_Templet := Xmltype('<OUTPUT></OUTPUT>');

  Select Extractvalue(Value(A), 'IN/GHDH') Into v_挂号单 From Table(Xmlsequence(Extract(Xml_In, 'IN'))) A;

  If v_挂号单 Is Null Then
    v_Err_Msg := '不能找到指定的挂号单号(当前挂号单号为空)';
    Raise Err_Item;
  End If;

  --1.检查是否有挂号单
  Select Count(*) Into n_Count From 病人挂号记录 A Where NO = v_挂号单;
  If Nvl(n_Count, 0) = 0 Then
    v_Err_Msg := '未找到指定的挂号单据:' || v_挂号单 || '!';
    Raise Err_Item;
  End If;

  If Nvl(n_Count, 0) > 1 Then
    v_Err_Msg := '单据号:' || v_挂号单 || '已经被退号!';
    Raise Err_Item;
  End If;
  --2.费用汇总

  v_No   := '-_';
  v_费目 := '-_';

  For c_费用 In (Select 1 As 顺序号, b.No, b.收据费目, b.结帐id, b.执行部门id, b.开单部门id, b.开单人,
                      To_Char(b.登记时间, 'yyyy-mm-dd hh24:mi:ss') As 登记时间, Nvl(b.价格父号, b.序号) As 序号, b.收费细目id, b.计算单位,
                      Max(m.名称) As 名称, Max(m.规格) As 规格, Sum(b.标准单价) As 单价, Avg(Nvl(b.付数, 1) * b.数次) As 数量,
                      Sum(b.实收金额) As 实收金额, Max(j.名称) As 开单科室, Max(q.名称) As 执行科室
               From 门诊费用记录 B, 收费项目目录 M, 部门表 J, 部门表 Q
               Where b.No In (Select Replace(摘要, '划价:', '')
                              From 病人挂号记录
                              Where NO = v_挂号单 And Instr(摘要, '划价:') > 0) And Mod(b.记录性质, 10) = 1 And Nvl(b.费用状态, 0) = 0 And
                     b.记录状态 = 0 And b.收费细目id = m.Id And b.开单部门id = j.Id(+) And b.执行部门id = q.Id(+)
               Group By b.No, b.收据费目, b.结帐id, b.执行部门id, b.开单部门id, b.开单人, b.登记时间, Nvl(b.价格父号, b.序号), b.收费细目id, b.计算单位
               Union All
               
               Select 1 As 顺序号, b.No, b.收据费目, b.结帐id, b.执行部门id, b.开单部门id, b.开单人,
                      To_Char(b.登记时间, 'yyyy-mm-dd hh24:mi:ss') As 登记时间, Nvl(b.价格父号, b.序号) As 序号, b.收费细目id, b.计算单位,
                      Max(m.名称) As 名称, Max(m.规格) As 规格, Sum(b.标准单价) As 单价, Avg(Nvl(b.付数, 1) * b.数次) As 数量,
                      Sum(b.实收金额) As 实收金额, Max(j.名称) As 开单科室, Max(q.名称) As 执行科室
               From 病人医嘱记录 A1, 门诊费用记录 B, 收费项目目录 M, 部门表 J, 部门表 Q
               Where A1.挂号单 = v_挂号单 And Mod(b.记录性质, 10) = 1 And A1.Id = b.医嘱序号 And Nvl(b.费用状态, 0) = 0 And b.记录状态 = 0 And
                     b.收费细目id = m.Id And b.开单部门id = j.Id(+) And b.执行部门id = q.Id(+)
               Group By b.No, b.收据费目, b.结帐id, b.执行部门id, b.开单部门id, b.开单人, b.登记时间, Nvl(b.价格父号, b.序号), b.收费细目id, b.计算单位
               Order By 顺序号, NO, 收据费目, 序号) Loop
    If v_No <> c_费用.No Then
      v_Temp := '<NO>' || c_费用.No || '</NO>';
      v_Temp := v_Temp || '<KDSJ>' || c_费用.登记时间 || '</KDSJ>';
      v_Temp := v_Temp || '<KDKS>' || c_费用.开单科室 || '</KDKS>';
      v_Temp := v_Temp || '<YS>' || c_费用.开单人 || '</YS>';
      v_Temp := '<FYLIST NO="' || c_费用.No || '"><FY>' || v_Temp || '<FMLIST></FMLIST></FY></FYLIST>';
      Select Appendchildxml(x_Templet, '/OUTPUT', Xmltype(v_Temp)) Into x_Templet From Dual;
      v_No   := c_费用.No;
      v_费目 := '-_';
    End If;
  
    If v_费目 <> c_费用.收据费目 Then
    
      v_费目 := c_费用.收据费目;
    
      v_Temp := '<MC>' || v_费目 || '</MC>';
      v_Temp := '<FM SJFM="' || v_费目 || '">' || v_Temp || '<MXLIST></MXLIST></FM>';
      Select Appendchildxml(x_Templet, '/OUTPUT/FYLIST[@NO="' || v_No || '"]/FY/FMLIST', Xmltype(v_Temp))
      Into x_Templet
      From Dual;
    End If;
  
    v_Temp := '<XMMC>' || c_费用.名称 || '</XMMC>';
    v_Temp := v_Temp || '<GG>' || c_费用.规格 || '</GG>';
    v_Temp := v_Temp || '<SL>' || c_费用.数量 || '</SL>';
    v_Temp := v_Temp || '<DW>' || c_费用.计算单位 || '</DW>';
    v_Temp := v_Temp || '<SL>' || c_费用.数量 || '</SL>';
    v_Temp := v_Temp || '<DJ>' || c_费用.单价 || '</DJ>';
    v_Temp := v_Temp || '<YSJE>' || c_费用.实收金额 || '</YSJE>';
    v_Temp := v_Temp || '<SSJE>' || c_费用.实收金额 || '</SSJE>';
  
    v_Temp := '<MX>' || v_Temp || '</MX>';
  
    Select Appendchildxml(x_Templet, '/OUTPUT/FYLIST[@NO="' || v_No || '"]/FY/FMLIST/FM[@SJFM="' || v_费目 || '"]/MXLIST',
                           Xmltype(v_Temp))
    
    Into x_Templet
    From Dual;
  End Loop;

  Xml_Out := x_Templet;
Exception
  When Err_Item Then
    v_Temp := '<ERROR><MSG>' || v_Err_Msg || '</MSG></ERROR>';
    Select Appendchildxml(x_Templet, '/OUTPUT', Xmltype(v_Temp)) Into x_Templet From Dual;
    Xml_Out := x_Templet;
  
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_Third_Waitingpay;
/

--77834:刘兴洪,2014-12-22,医保补充结算,移动平台接口整理
Create Or Replace Procedure Zl_Third_Paymentinfo
(
  Xml_In  In Xmltype,
  Xml_Out Out Xmltype
) Is
  -------------------------------------------------------------------------------------------------- 
  --功能:获取指定单据交易信息 
  --入参:Xml_In: 
  --<IN>
  --    <DJ>单据号,性质|....</DJ> //可以传入多个单据
  --    <JSKLB>结算卡类别</JSKLB>
  --</IN>
  --出参:Xml_Out
  --<OUTPUT>
  --<DJ>  //如果为空表示没有找到数据
  --    <DJH>单据号</DJH>
  --    <JLXZ>记录性质</JLXZ>   
  --    <YSJE>应收金额</YSJE>
  --    <SSJE>实收金额</SSJE>
  --    <FYMC>收据费目</FYMC> //(多个用逗号分离 )
  --    <JSKZF>结算卡支付</JSKZF> //:0-不是结算卡支付;1-结算卡支付(入参中:JSKLB判断,存在该类别支付的,返回1,否则返回0)
  --    <FKSJ>付款时间</FKSJ>
  --    <JYLSH>交易流水号</JYLSH> //(非结算卡支付的，不返回)
  --    <YXTK>是否允许退款</YXTK>            //0不允许1允许
  --    <YBJS>医保是否结算</YBJS>   //1-已结算;0-未结算
  --    <KP>是否开票</KP>  1是0否
  --    <JZTFSM>禁止退款说明</JZTFSM>
  --</DJ>
  --<ERROR><MSG></MSG></ERROR>          //错误情况返回
  --</OUTPUT>
  -------------------------------------------------------------------------------------------------- 
  v_Err_Msg Varchar2(200);
  Err_Item Exception;

  x_Templet      Xmltype; --模板XML 
  v_No           Varchar2(4000);
  v_结算卡类别   Varchar2(100);
  n_结算卡类别id 医疗卡类别.Id%Type;

  n_Temp       Number(18);
  v_Temp       Varchar2(32767); --临时XML 
  v_交易流水号 病人预交记录.交易流水号%Type;
  n_医保结算   Number(2);
  n_允许退款   Number(2);
  v_说明       Varchar2(100);
  n_补结算     Number(18);
  n_已开医嘱   Number(2);

Begin
  x_Templet := Xmltype('<OUTPUT></OUTPUT>');

  Select Extractvalue(Value(A), 'IN/DJ'), Extractvalue(Value(A), 'IN/JSKLB')
  Into v_No, v_结算卡类别
  From Table(Xmlsequence(Extract(Xml_In, 'IN'))) A;

  If v_No Is Null Then
    v_Err_Msg := '不能找到指定的收费单据(当前单据为空)';
    Raise Err_Item;
  End If;
  If v_结算卡类别 Is Not Null Then
    v_Err_Msg := Null;
    Begin
      n_结算卡类别id := To_Number(v_结算卡类别);
    Exception
      When Others Then
        n_结算卡类别id := 0;
    End;
    If Nvl(n_结算卡类别id, 0) = 0 Then
      Begin
        Select ID, Decode(Nvl(是否启用, 0), 1, Null, 名称 || '未启用,不允许进行缴费!')
        Into n_结算卡类别id, v_Err_Msg
        From 医疗卡类别
        Where 名称 = v_结算卡类别;
      Exception
        When Others Then
          n_结算卡类别id := 0;
          v_Err_Msg      := v_结算卡类别 || '不存在!';
      End;
    Else
      Begin
        Select ID, Decode(Nvl(是否启用, 0), 1, Null, 名称 || '未启用,不允许进行缴费!')
        Into n_结算卡类别id, v_Err_Msg
        From 医疗卡类别
        Where ID = n_结算卡类别id;
      Exception
        When Others Then
          n_结算卡类别id := 0;
          v_Err_Msg      := '未找到指定的结算支付信息!';
      End;
    End If;
    If Not v_Err_Msg Is Null Then
      Raise Err_Item;
    End If;
  End If;

  For c_收费单 In (Select /*+ rule*/
                 记录性质, NO, Sum(应收金额) As 应收金额, Sum(实收金额) As 实收金额, Max(收款时间) As 收款时间, Max(结帐id) As 结帐id, Max(是否退款) As 是否退款,
                 Max(是否打印) As 是否打印, Max(记录状态) As 记录状态, f_List2str(Cast(Collect(收据费目) As t_Strlist)) As 收据费目,
                 Max(挂号单) As 挂号单
                
                From (Select 记录性质, NO, 收据费目, Sum(应收金额) As 应收金额, Sum(实收金额) As 实收金额,
                              To_Char(Max(登记时间), 'yyyy-mm-dd hh24:mi:ss') As 收款时间, Max(结帐id) As 结帐id,
                              Max(Decode(Nvl(a.执行状态, 0), 1, 0, 1)) As 是否退款, Max(Decode(a.实际票号, Null, 0, 1)) 是否打印,
                              Max(a.记录状态) As 记录状态, Max(Decode(Instr(摘要, '挂号:'), 0, '', Replace(摘要, '挂号:', ''))) As 挂号单
                       From 门诊费用记录 A, Table(f_Str2list2(v_No, '|', ',')) B
                       Where a.No = b.C1 And a.记录性质 = b.C2 And 记录状态 In (1, 3)
                       Group By 记录性质, NO, 收据费目
                       Order By 记录性质, NO, 收据费目)
                Group By 记录性质, NO) Loop
    n_Temp := 0;
    If n_结算卡类别id <> 0 Then
      Begin
        Select 交易流水号, 1
        Into v_交易流水号, n_Temp
        From 病人预交记录
        Where 卡类别id = n_结算卡类别id And 结帐id = Nvl(c_收费单.结帐id, 0);
      Exception
        When Others Then
          n_Temp := 0;
      End;
    
    End If;
    Begin
      Select Count(*) Into n_补结算 From 费用补充记录 Where 收费结帐id = Nvl(c_收费单.结帐id, 0) And Rownum < 2;
    Exception
      When Others Then
        n_补结算 := 0;
    End;
  
    Select Decode(Nvl(Count(*), 0), 0, 0, 1)
    Into n_医保结算
    From 保险结算记录
    Where 性质 = 1 And 记录id = Nvl(c_收费单.结帐id, 0);
  
    n_允许退款 := Nvl(c_收费单.是否退款, 0);
    n_已开医嘱 := 0;
  
    If c_收费单.挂号单 Is Not Null Then
      Begin
        Select 1 Into n_已开医嘱 From 病人医嘱记录 Where 挂号单 = c_收费单.挂号单;
      Exception
        When Others Then
          n_已开医嘱 := 0;
      End;
      If Nvl(n_已开医嘱, 0) = 1 Then
        n_允许退款 := 0;
      End If;
    End If;
    If Nvl(n_已开医嘱, 0) = 1 Then
      v_说明 := '挂号单已开医嘱';
    Elsif Nvl(n_允许退款, 0) = 0 Then
      v_说明 := '单据已经执行';
    End If;
    If Nvl(n_医保结算, 0) = 1 Then
      v_说明     := '单据已经医保结算';
      n_允许退款 := 0;
    End If;
    If Nvl(n_补结算, 0) <> 0 Then
      v_说明     := '单据已经补充结算';
      n_允许退款 := 0;
    End If;
  
    If Nvl(n_允许退款, 0) = 1 Then
      --检查是否有可退数量
      Select Decode(Nvl(Count(*), 0), 0, 0, 1)
      Into n_允许退款
      From (Select 序号, Sum(Nvl(付数, 0) * 数次) As 数量
             From 门诊费用记录 A
             Where NO = c_收费单.No And Mod(记录性质, 10) = c_收费单.记录性质 And 价格父号 Is Null Having Sum(Nvl(付数, 0) * 数次) <> 0
             Group By 序号);
      If Nvl(c_收费单.记录状态, 0) = 3 And n_允许退款 = 0 Then
        v_说明     := '单据已全退';
        n_允许退款 := 0;
      Elsif Nvl(c_收费单.记录状态, 0) = 3 Then
        v_说明     := '单据已部分退费';
        n_允许退款 := 0;
      End If;
    End If;
    If Nvl(n_允许退款, 0) = 1 And Nvl(c_收费单.是否打印, 0) = 1 Then
      v_说明     := '单据已开发票';
      n_允许退款 := 0;
    End If;
  
    v_Temp := '<DJH>' || c_收费单.No || '</DJH>';
    v_Temp := v_Temp || '<JLXZ>' || c_收费单.记录性质 || '</JLXZ>';
    v_Temp := v_Temp || '<YSJE>' || c_收费单.应收金额 || '</YSJE>';
    v_Temp := v_Temp || '<SSJE>' || c_收费单.实收金额 || '</SSJE>';
    v_Temp := v_Temp || '<FYMC>' || c_收费单.收据费目 || '</FYMC>';
    v_Temp := v_Temp || '<FKSJ>' || c_收费单.收款时间 || '</FKSJ>';
    v_Temp := v_Temp || '<JSKZF>' || n_Temp || '</JSKZF>';
    v_Temp := v_Temp || '<JYLSH>' || Nvl(v_交易流水号, '') || '</JYLSH>';
    v_Temp := v_Temp || '<YXTK>' || Nvl(n_允许退款, 0) || '</YXTK>';
    v_Temp := v_Temp || '<YBJS>' || n_医保结算 || '</YBJS>';
    v_Temp := v_Temp || '<KP>' || Nvl(c_收费单.是否打印, 0) || '</KP>';
    v_Temp := v_Temp || '<JZTFSM>' || v_说明 || '</JZTFSM>';
  
    v_Temp := '<DJ>' || v_Temp || '</DJ>';
  
    Select Appendchildxml(x_Templet, '/OUTPUT', Xmltype(v_Temp)) Into x_Templet From Dual;
  End Loop;

  Xml_Out := x_Templet;
Exception
  When Err_Item Then
    v_Temp := '<ERROR><MSG>' || v_Err_Msg || '</MSG></ERROR>';
    Select Appendchildxml(x_Templet, '/OUTPUT', Xmltype(v_Temp)) Into x_Templet From Dual;
    Xml_Out := x_Templet;
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_Third_Paymentinfo;
/

--77834:刘兴洪,2014-12-04,医保补充结算,移动平台接口整理
Create Or Replace Procedure Zl_Third_Payment
(
  Xml_In  In Xmltype,
  Xml_Out Out Xmltype
) Is

  -------------------------------------------------------------------------------------------------- 
  --功能:三方接口支付 
  --入参:Xml_In: 
  --<IN>
  --        <NO></NO>                       //收费单据号串,逗号分隔多个单据号
  --        <JE></JE>                       //总金额
  --        <BRID>病人ID</BRID>
  --        <WCJE>误差额</WCJE>             //误差项不传时,以总金额-本次结算费用总额为准
  --       <JSLIST>
  --         <JS>
  --              <JSKLB>支付卡类别</JSKLB >
  --              <JSKH>支付卡号</ JSKH >
  --              <JSFS>支付方式</JSFS> //支付方式:现金;支票,如果是三方卡,可以传空
  --              <JSJE>支付金额</JSJE>
  --              <JYLSH>交易流水号</JYLSH>
  --              <ZY>摘要</ZY>
  --              <SFCYJ>是否冲预交</SFCYJ>  //允冲预交时,只填JSJE节点:1-冲预交
  --              <SFXFK>是否消费卡</SFXFK>  //(1-是消费卡),消费卡时,传入结算卡类别,结算卡号,结算金额等接点
  --              <EXPENDLIST>  //扩展交易信息
  --                  <EXPEND>
  --                        <JYMC >交易名称</交易名称>
  --                        <JYLR>交易内容</JYLR>
  --                  </EXPEND>
  --              </EXPENDLIST>
  --         </JS>
  --       </JSLIST >
  --</IN>

  --出参:Xml_Out 
  --  <OUT> 
  --    DD如无下列错误结点则说明正确执行 
  --    <ERROR> 
  --      <MSG>错误信息</MSG> 
  --    </ERROR> 
  --  </OUT> 
  -------------------------------------------------------------------------------------------------- 
  v_Nos      Varchar2(4000);
  n_收费总额 门诊费用记录.实收金额%Type;

  n_卡类别id 医疗卡类别.Id%Type;
  v_结算方式 Varchar2(2000);
  n_病人id   门诊费用记录.病人id%Type;
  v_姓名     门诊费用记录.姓名%Type;
  v_性别     门诊费用记录.性别%Type;
  v_年龄     门诊费用记录.年龄%Type;

  v_医疗付款方式编码 医疗付款方式.编码%Type;
  v_操作员编码       门诊费用记录.操作员编号%Type;
  v_操作员姓名       门诊费用记录.操作员姓名%Type;
  n_结帐id           门诊费用记录.结帐id%Type;
  n_结帐金额         门诊费用记录.结帐金额%Type;
  d_收费时间         病人预交记录.收款时间%Type;
  n_消费卡id         消费卡目录.Id%Type;
  v_收费结算         Varchar2(2000);
  v_普通结算         Varchar2(4000);

  v_Temp    Varchar2(32767); --临时XML 
  x_Templet Xmltype; --模板XML 

  v_Err_Msg Varchar2(200);
  Err_Item Exception;
  n_Count    Number(18);
  v_发药窗口 Varchar2(4000);
  n_误差额   病人预交记录.冲预交%Type;
  Procedure Third_Cardbalance_Modfiy
  (
    结帐id_In     病人预交记录.结帐id%Type,
    卡类别_In     Varchar2,
    卡号_In       病人预交记录.卡号%Type,
    支付金额_In   病人预交记录.冲预交%Type,
    交易流水号_In 病人预交记录.交易流水号%Type,
    交易说明_In   病人预交记录.交易说明%Type,
    Xmlexpned_In  Xmltype
  ) Is
    n_卡类别id 医疗卡类别.Id%Type;
    v_结算方式 病人预交记录.结算方式%Type;
    v_名称     医疗卡类别.名称%Type;
  Begin
    v_Err_Msg := Null;
    Begin
      n_卡类别id := To_Number(卡类别_In);
    Exception
      When Others Then
        n_卡类别id := 0;
    End;
    If n_卡类别id = 0 Then
      Begin
        Select ID, 结算方式, Decode(Nvl(是否启用, 0), 1, Null, 名称 || '未启用,不允许进行缴费!'), 名称
        Into n_卡类别id, v_结算方式, v_Err_Msg, v_名称
        From 医疗卡类别
        Where 名称 = 卡类别_In;
      Exception
        When Others Then
          n_卡类别id := -1;
          v_Err_Msg  := 卡类别_In || '不存在!';
      End;
    Else
      Begin
        Select ID, 结算方式, Decode(Nvl(是否启用, 0), 1, Null, 名称 || '未启用,不允许进行缴费!'), 名称
        Into n_卡类别id, v_结算方式, v_Err_Msg, v_名称
        From 医疗卡类别
        Where ID = n_卡类别id;
      Exception
        When Others Then
          n_卡类别id := -1;
          v_Err_Msg  := '未找到指定的结算支付信息!';
      End;
    End If;
    If Not v_Err_Msg Is Null Then
      Raise Err_Item;
    End If;
    If v_结算方式 Is Null Then
      v_Err_Msg := Nvl(v_名称, '') || '未设置结算方式,请在医疗卡类别中设置结算方式';
      Raise Err_Item;
    End If;
  
    v_收费结算 := v_结算方式 || '|' || 支付金额_In || '|' || ' |' || ' ';
    --结算方式|结算金额|结算号码|结算摘要 
    Zl_门诊收费结算_Modify(1, n_病人id, 结帐id_In, v_收费结算, 0, 0, n_卡类别id, 卡号_In, 交易流水号_In, 交易说明_In, 0, 0, 0, 0);
  
    --保存扩展结算信息
    For c_扩展 In (Select Extractvalue(j.Column_Value, '/EXPEND/JYMC') As Jymc,
                        Extractvalue(j.Column_Value, '/EXPEND/JYLR') As Jylr
                 From Table(Xmlsequence(Extract(Xmlexpned_In, '/EXPENDLIST/EXPEND'))) J) Loop
      Zl_三方结算交易_Insert(n_卡类别id, 0, 卡号_In, 结帐id_In, c_扩展.Jymc || '|' || c_扩展.Jylr, 0);
    End Loop;
  End Third_Cardbalance_Modfiy;

  Procedure Square_Cardbalance_Modfiy
  (
    结帐id_In     病人预交记录.结帐id%Type,
    卡类别_In     Varchar2,
    卡号_In       病人预交记录.卡号%Type,
    支付金额_In   病人预交记录.冲预交%Type,
    交易流水号_In 病人预交记录.交易流水号%Type,
    交易说明_In   病人预交记录.交易说明%Type,
    Xmlexpned_In  Xmltype
  ) Is
    n_卡类别id 医疗卡类别.Id%Type;
    v_结算方式 病人预交记录.结算方式%Type;
    v_名称     卡消费接口目录.名称%Type;
  Begin
    v_Err_Msg := Null;
    Begin
      n_卡类别id := To_Number(卡类别_In);
    Exception
      When Others Then
        n_卡类别id := 0;
    End;
  
    If n_卡类别id = 0 Then
      Begin
        Select 编号, 结算方式, Decode(Nvl(启用, 0), 1, Null, 名称 || '未启用,不允许进行缴费!'), 名称
        Into n_卡类别id, v_结算方式, v_Err_Msg, v_名称
        From 卡消费接口目录
        Where 名称 = 卡类别_In;
      Exception
        When Others Then
          v_Err_Msg := '消费:' || 卡类别_In || '不存在!';
      End;
    
    Else
    
      Begin
        Select 编号, 结算方式, Decode(Nvl(启用, 0), 1, Null, 名称 || '未启用,不允许进行缴费!'), 名称
        Into n_卡类别id, v_结算方式, v_Err_Msg, v_名称
        From 卡消费接口目录
        Where 编号 = 卡类别_In;
      Exception
        When Others Then
          v_Err_Msg := '未找到指定的结算支付信息!';
      End;
    
    End If;
    If Not v_Err_Msg Is Null Then
      Raise Err_Item;
    End If;
    If v_结算方式 Is Null Then
      v_Err_Msg := Nvl(v_名称, '') || '未设置结算方式,请在医疗卡类别中设置结算方式';
      Raise Err_Item;
    End If;
  
    Select ID
    Into n_消费卡id
    From 消费卡目录
    Where 接口编号 = n_卡类别id And 卡号 = 卡号_In And
          序号 = (Select Max(序号) From 消费卡目录 Where 接口编号 = n_卡类别id And 卡号 = 卡号_In);
  
    --结算方式_IN格式为:卡类别ID|卡号|消费卡ID|消费金额||.... 
    v_收费结算 := n_卡类别id || '|' || 卡号_In || '|' || n_消费卡id || '|' || 支付金额_In;
    Zl_门诊收费结算_Modify(3, n_病人id, 结帐id_In, v_收费结算, 0, 0, n_卡类别id, 卡号_In, 交易流水号_In, 交易说明_In, 0, 0, 0, 0);
    --保存扩展结算信息
    For c_扩展 In (Select Extractvalue(j.Column_Value, '/EXPEND/JYMC') As Jymc,
                        Extractvalue(j.Column_Value, '/EXPEND/JYLR') As Jylr
                 From Table(Xmlsequence(Extract(Xmlexpned_In, '/EXPENDLIST/EXPEND'))) J) Loop
      Zl_三方结算交易_Insert(n_卡类别id, 1, 卡号_In, 结帐id_In, c_扩展.Jymc || '|' || c_扩展.Jylr, 0);
    End Loop;
  End Square_Cardbalance_Modfiy;

Begin
  x_Templet := Xmltype('<OUTPUT></OUTPUT>');

  Select Extractvalue(Value(A), 'IN/NO'), To_Number(Extractvalue(Value(A), 'IN/BRID')),
         To_Number(Extractvalue(Value(A), 'IN/JE')), To_Number(Extractvalue(Value(A), 'IN/WCJE'))
  Into v_Nos, n_病人id, n_收费总额, n_误差额
  From Table(Xmlsequence(Extract(Xml_In, 'IN'))) A;

  --0.相关检查

  If Nvl(n_病人id, 0) = 0 Then
    v_Err_Msg := '不能有效识别病人身份,不允许缴费!';
    Raise Err_Item;
  
  End If;

  If v_Nos Is Null Then
    v_Err_Msg := '没有指定相关的收费单据,不允许缴费!';
    Raise Err_Item;
  
  End If;

  --人员id,人员编号,人员姓名 
  v_Temp := Zl_Identity(1);
  If Nvl(v_Temp, '0') = '0' Or Nvl(v_Temp, '_') = '_' Then
    v_Err_Msg := '系统不能认别有效的操作员,不允许缴费!';
    Raise Err_Item;
  End If;
  v_Temp       := Substr(v_Temp, Instr(v_Temp, ';') + 1);
  v_Temp       := Substr(v_Temp, Instr(v_Temp, ',') + 1);
  v_操作员编码 := Substr(v_Temp, 1, Instr(v_Temp, ',') - 1);
  v_Temp       := Substr(v_Temp, Instr(v_Temp, ',') + 1);
  v_操作员姓名 := v_Temp;
  v_Err_Msg    := Null;
  Begin
    Select b.编码, a.姓名, a.性别, a.年龄
    Into v_医疗付款方式编码, v_姓名, v_性别, v_年龄
    From 病人信息 A, 医疗付款方式 B
    Where a.医疗付款方式 = b.名称(+) And a.病人id = n_病人id;
  Exception
    When Others Then
      v_Err_Msg := '指定的缴费单据中不能有效识别病人,不允许缴费!';
  End;
  If Not v_Err_Msg Is Null Then
    Raise Err_Item;
  End If;

  v_发药窗口 := Zl_Getclinicchargepaywins(v_Nos);

  --1.进行费用收费处理
  --获取发药窗口
  Select 病人结帐记录_Id.Nextval, Sysdate Into n_结帐id, d_收费时间 From Dual;
  n_结帐金额 := 0;
  For c_缴费单 In (Select /*+ rule */
                 a.No, Max(a.开单部门id) As 开单部门id, Max(a.病人科室id) As 病人科室id, Max(a.病人id) As 病人id, Sum(实收金额) As 实收金额,
                 Max(a.开单人) As 开单人
                From 门诊费用记录 A, Table(f_Str2list(v_Nos)) J
                Where a.记录性质 = 1 And a.No = j.Column_Value And a.记录状态 = 0
                Group By a.No) Loop
    If Nvl(c_缴费单.病人id, 0) <> n_病人id Then
      v_Err_Msg := '缴费单据:' || c_缴费单.No || '与当前病人身份不符,不允许缴费!';
      Raise Err_Item;
    End If;
  
    n_结帐金额 := n_结帐金额 + Nvl(c_缴费单.实收金额, 0);
    Zl_病人划价收费_Insert(c_缴费单.No, n_病人id, 1, v_医疗付款方式编码, v_姓名, v_性别, v_年龄, c_缴费单.病人科室id, c_缴费单.开单部门id, c_缴费单.开单人, n_结帐id,
                     d_收费时间, v_操作员编码, v_操作员姓名, v_发药窗口, 0, d_收费时间);
  
  End Loop;

  --检查总金额是否正确 
  If Nvl(n_误差额, 0) = 0 Then
    n_误差额 := Nvl(n_收费总额, 0) - Nvl(n_结帐金额, 0);
    If Abs(n_误差额) > 1.00 Then
      v_Err_Msg := '单据缴款金额与实际结算的误差太大!';
      Raise Err_Item;
    End If;
  End If;
  If Nvl(n_结帐金额, 0) + Nvl(n_误差额, 0) <> Nvl(n_收费总额, 0) Then
    v_Err_Msg := '指定的缴费单据的总金额不对,请重新选择缴费单据!';
    Raise Err_Item;
  End If;

  --2.确定支付方式
  n_Count := 0;
  For c_结算方式 In (Select Extractvalue(b.Column_Value, '/JS/JSKLB') As 结算卡类别,
                        Extractvalue(b.Column_Value, '/JS/JSKH') As 结算卡号,
                        Extractvalue(b.Column_Value, '/JS/JSFS') As 结算方式,
                        Extractvalue(b.Column_Value, '/JS/JSJE') As 结算金额,
                        Extractvalue(b.Column_Value, '/JS/JYLSH') As 交易流水号,
                        Extractvalue(b.Column_Value, '/JS/JYSM') As 交易说明, Extractvalue(b.Column_Value, '/JS/ZY') As 摘要,
                        Extractvalue(b.Column_Value, '/JS/SFCYJ') As 是否冲预交,
                        Extractvalue(b.Column_Value, '/JS/SFXFK') As 是否消费卡,
                        Extract(b.Column_Value, '/JS/EXPENDLIST') As Expend
                 From Table(Xmlsequence(Extract(Xml_In, '/IN/JSLIST/JS'))) B) Loop
  
    --1.三方卡结算
  
    If c_结算方式.结算卡类别 Is Not Null And Nvl(c_结算方式.是否消费卡, 0) = 0 Then
      --1.三方卡结算
      Third_Cardbalance_Modfiy(n_结帐id, c_结算方式.结算卡类别, c_结算方式.结算卡号, c_结算方式.结算金额, c_结算方式.交易流水号, c_结算方式.交易说明, c_结算方式.Expend);
    Elsif c_结算方式.结算卡类别 Is Not Null And Nvl(c_结算方式.是否消费卡, 0) = 1 Then
      --2.消费卡结算
      Square_Cardbalance_Modfiy(n_结帐id, c_结算方式.结算卡类别, c_结算方式.结算卡号, c_结算方式.结算金额, c_结算方式.交易流水号, c_结算方式.交易说明,
                                c_结算方式.Expend);
    Elsif Nvl(c_结算方式.是否冲预交, 0) = 1 Then
      --3.冲预交款
      Zl_门诊收费结算_Modify(0, n_病人id, n_结帐id, Null, c_结算方式.结算金额, 0, Null, Null, Null, Null, 0, 0, 0, 0);
    Else
      --4.普通结算
      If c_结算方式.结算方式 Is Null Then
        v_Err_Msg := '未指定指付方式，不允缴款!';
        Raise Err_Item;
      End If;
      --结算方式|结算金额|结算号码|结算摘要||..
      v_收费结算 := c_结算方式.结算方式 || '|' || c_结算方式.结算金额 || '| | ';
      v_普通结算 := Nvl(v_普通结算, '') || '||' || v_收费结算;
    End If;
    n_Count := n_Count + 1;
  End Loop;
  If n_Count = 0 Then
    v_Err_Msg := '不能有效确认当前的支付方式!';
    Raise Err_Item;
  End If;
  --5.普通结算及完成结
  If v_普通结算 Is Not Null Then
    v_普通结算 := Substr(v_普通结算, 3);
  End If;
  Zl_门诊收费结算_Modify(0, n_病人id, n_结帐id, v_普通结算, Null, 0, Null, Null, Null, Null, 0, 0, n_误差额, 1);
  Xml_Out := x_Templet;
Exception
  When Err_Item Then
    v_Temp := v_Err_Msg;
    -- Select Appendchildxml(x_Templet, '/OUTPUT', Xmltype(v_Temp)) Into x_Templet From Dual;
    -- Xml_Out := x_Templet;
    Raise_Application_Error(-20101, v_Temp);
  
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_Third_Payment;
/

--77834:刘兴洪,2014-12-22,医保补充结算,移动平台接口整理
Create Or Replace Procedure Zl_Third_Charge_Del
(
  Xml_In  In Xmltype,
  Xml_Out Out Xmltype
) Is

  -------------------------------------------------------------------------------------------------- 
  --功能:三方退费交易 
  --入参:Xml_In: 
  --<IN>
  --    <BRID>病人ID</BRID>
  --    <JE></JE> //退款总金额
  --    <TFZY>退费摘要</TFZY>
  --    <FYLIST>
  --        <FY>
  --           <DJH>退款单据号</DJH>
  --           <XH>退款序号(格式:1,2,3..为空代表退剩余数量)</DJH>
  --        <FY>
  --    </FYLIST>
  --    <TKLIST>
  --        <TK>
  --            <TKKLB>退款卡类别</TKKLB>
  --            <TKKH>退款卡号</TKKH>
  --            <TKFS>退款方式</TKFS> //退款方式:现金;支票,如果是三方卡,可以传空
  --            <TKJE>支付金额</TKJE>
  --            <JYLSH>交易流水号</JYLSH>
  --            <TKZY>摘要</TKZY>
  --            <TYJK>退回预交款</TYJK> //允冲预交时,只填JSJE节点:1-冲预交
  --            <SFXFK>是否消费卡</SFXFK>   //(1-是消费卡),消费卡时,传入结算卡类别,结算卡号,结算金额等接点
  --            <EXPENDLIST>  //扩展交易信息
  --                <EXPEND>
  --                    <JYMC>交易名称</JYMC>
  --                    <JYLR>交易内容</JYLR>
  --                </EXPEND>
  --            </EXPENDLIST>
  --        </TK>
  --    </TKLIST>
  --</IN>

  --出参:Xml_Out 
  --  <OUT> 
  --    DD如无下列错误结点则说明正确执行 
  --    <ERROR> 
  --      <MSG>错误信息</MSG> 
  --    </ERROR> 
  --  </OUT> 
  -------------------------------------------------------------------------------------------------- 
  n_退款总额 门诊费用记录.实收金额%Type;
  n_卡类别id 医疗卡类别.Id%Type;
  v_结算方式 Varchar2(2000);

  n_病人id     门诊费用记录.病人id%Type;
  n_单据病人id 门诊费用记录.病人id%Type;
  v_操作员编码 门诊费用记录.操作员编号%Type;
  v_操作员姓名 门诊费用记录.操作员姓名%Type;
  n_冲销id     门诊费用记录.结帐id%Type;
  n_结帐id     门诊费用记录.结帐id%Type;
  n_结帐金额   门诊费用记录.结帐金额%Type;
  n_误差额     病人预交记录.冲预交%Type;
  n_原结算序号 病人预交记录.结算序号%Type;
  l_挂号单     t_Strlist := t_Strlist();
  v_挂号单     门诊费用记录.No%Type;

  n_消费卡id 消费卡目录.Id%Type;
  v_摘要     门诊费用记录.摘要%Type;
  n_Count    Number(18);

  d_退费时间 病人预交记录.收款时间%Type;

  v_退费结算 Varchar2(2000);
  v_普通结算 Varchar2(4000);
  n_Temp     Number(18);

  v_Temp    Varchar2(32767); --临时XML 
  x_Templet Xmltype; --模板XML 

  v_Err_Msg Varchar2(200);
  Err_Item Exception;
  Procedure Third_Cardbalance_Modfiy
  (
    冲销id_In     病人预交记录.结帐id%Type,
    卡类别_In     Varchar2,
    卡号_In       病人预交记录.卡号%Type,
    退款金额_In   病人预交记录.冲预交%Type,
    交易流水号_In 病人预交记录.交易流水号%Type,
    交易说明_In   病人预交记录.交易说明%Type,
    摘要_In       病人预交记录.摘要%Type,
    Xmlexpned_In  Xmltype
  ) Is
    n_卡类别id 医疗卡类别.Id%Type;
    v_结算方式 病人预交记录.结算方式%Type;
  
  Begin
    v_Err_Msg := Null;
    Begin
      n_卡类别id := To_Number(卡类别_In);
    Exception
      When Others Then
        n_卡类别id := 0;
    End;
    If n_卡类别id = 0 Then
      Begin
        Select ID, 结算方式, Decode(Nvl(是否启用, 0), 1, Null, 名称 || '未启用,不允许进行缴费!')
        Into n_卡类别id, v_结算方式, v_Err_Msg
        From 医疗卡类别
        Where 名称 = 卡类别_In;
      Exception
        When Others Then
          n_卡类别id := -1;
          v_Err_Msg  := 卡类别_In || '不存在!';
      End;
    Else
      Begin
        Select ID, 结算方式, Decode(Nvl(是否启用, 0), 1, Null, 名称 || '未启用,不允许进行缴费!')
        Into n_卡类别id, v_结算方式, v_Err_Msg
        From 医疗卡类别
        Where ID = n_卡类别id;
      Exception
        When Others Then
          n_卡类别id := -1;
          v_Err_Msg  := '未找到指定的结算支付信息!';
      End;
    End If;
    If Not v_Err_Msg Is Null Then
      Raise Err_Item;
    End If;
  
    v_退费结算 := v_结算方式 || '|' || 退款金额_In || '|' || ' |' || Nvl(摘要_In, ' ');
    --   2.三方卡退费结算:
    --     ①结算方式_IN:只能传入一个结算方式,但允许包含一些辅助信息,格式为:结算方式|结算金额|结算号码|结算摘要
    --     ②卡类别ID_IN,卡号_IN,交易流水号_IN,交易说明_In:需要传入
    --结算方式|结算金额|结算号码|结算摘要 
    Zl_门诊退费结算_Modify(2, n_病人id, 冲销id_In, v_退费结算, 0, n_卡类别id, 卡号_In, 交易流水号_In, 交易说明_In, 0, 0, 0, 0);
  
    --保存扩展结算信息
    For c_扩展 In (Select Extractvalue(j.Column_Value, '/EXPEND/JYMC') As Jymc,
                        Extractvalue(j.Column_Value, '/EXPEND/JYLR') As Jylr
                 From Table(Xmlsequence(Extract(Xmlexpned_In, '/EXPENDLIST/EXPEND'))) J) Loop
      Zl_三方结算交易_Insert(n_卡类别id, 0, 卡号_In, 冲销id_In, c_扩展.Jymc || '|' || c_扩展.Jylr, 0);
    End Loop;
  End Third_Cardbalance_Modfiy;

  Procedure Square_Cardbalance_Modfiy
  (
    冲销id_In     病人预交记录.结帐id%Type,
    卡类别_In     Varchar2,
    卡号_In       病人预交记录.卡号%Type,
    退款金额_In   病人预交记录.冲预交%Type,
    交易流水号_In 病人预交记录.交易流水号%Type,
    交易说明_In   病人预交记录.交易说明%Type,
    摘要_In       病人预交记录.摘要%Type,
    
    Xmlexpned_In Xmltype
  ) Is
    n_卡类别id 医疗卡类别.Id%Type;
    v_结算方式 病人预交记录.结算方式%Type;
  
  Begin
    v_Err_Msg := Null;
    Begin
      n_卡类别id := To_Number(卡类别_In);
    Exception
      When Others Then
        n_卡类别id := 0;
    End;
  
    If n_卡类别id = 0 Then
      Begin
        Select 编号, 结算方式, Decode(Nvl(启用, 0), 1, Null, 名称 || '未启用,不允许进行缴费!')
        Into n_卡类别id, v_结算方式, v_Err_Msg
        From 卡消费接口目录
        Where 名称 = 卡类别_In;
      Exception
        When Others Then
          v_Err_Msg := '消费:' || 卡类别_In || '不存在!';
      End;
    
    Else
    
      Begin
        Select 编号, 结算方式, Decode(Nvl(启用, 0), 1, Null, 名称 || '未启用,不允许进行缴费!')
        Into n_卡类别id, v_结算方式, v_Err_Msg
        From 卡消费接口目录
        Where 编号 = 卡类别_In;
      Exception
        When Others Then
          v_Err_Msg := '未找到指定的结算支付信息!';
      End;
    
    End If;
    If Not v_Err_Msg Is Null Then
      Raise Err_Item;
    End If;
  
    v_退费结算 := v_结算方式 || '|' || 退款金额_In || '|' || ' |' || Nvl(摘要_In, ' ');
    --   4-消费卡结算:
    --     ①结算方式_IN:允许一次刷多张卡,格式为:卡类别ID|卡号|消费卡ID|消费金额||
    --     ②退支票额_In:传入零
    Select ID
    Into n_消费卡id
    From 消费卡目录
    Where 接口编号 = n_卡类别id And 卡号 = 卡号_In And
          序号 = (Select Max(序号) From 消费卡目录 Where 接口编号 = n_卡类别id And 卡号 = 卡号_In);
  
    --卡类别ID|卡号|消费卡ID|消费金额||.
    v_退费结算 := n_卡类别id || '|' || 卡号_In || '|' || n_消费卡id || '|' || 退款金额_In;
    Zl_门诊退费结算_Modify(4, n_病人id, 冲销id_In, v_退费结算, 0, Null, 卡号_In, 交易流水号_In, 交易说明_In, 0, 0, 0, 0);
  
    --保存扩展结算信息
    For c_扩展 In (Select Extractvalue(j.Column_Value, '/EXPEND/JYMC') As Jymc,
                        Extractvalue(j.Column_Value, '/EXPEND/JYLR') As Jylr
                 From Table(Xmlsequence(Extract(Xmlexpned_In, '/EXPENDLIST/EXPEND'))) J) Loop
      Zl_三方结算交易_Insert(n_卡类别id, 1, 卡号_In, 冲销id_In, c_扩展.Jymc || '|' || c_扩展.Jylr, 0);
    End Loop;
  End Square_Cardbalance_Modfiy;

Begin
  x_Templet := Xmltype('<OUTPUT></OUTPUT>');

  --0.获取入参中的病人ID等信息
  Select To_Number(Extractvalue(Value(A), 'IN/BRID')), To_Number(Extractvalue(Value(A), 'IN/JE')),
         Extractvalue(Value(A), 'IN/TFZY')
  Into n_病人id, n_退款总额, v_摘要
  From Table(Xmlsequence(Extract(Xml_In, 'IN'))) A;

  --0.相关检查
  If Nvl(n_病人id, 0) = 0 Then
    v_Err_Msg := '不能有效识别病人身份,不允许退费操作!';
    Raise Err_Item;
  
  End If;

  --人员id,人员编号,人员姓名 
  v_Temp := Zl_Identity(1);
  If Nvl(v_Temp, '0') = '0' Or Nvl(v_Temp, '_') = '_' Then
    v_Err_Msg := '系统不能认别有效的操作员,不允许缴费!';
    Raise Err_Item;
  End If;
  v_Temp       := Substr(v_Temp, Instr(v_Temp, ';') + 1);
  v_Temp       := Substr(v_Temp, Instr(v_Temp, ',') + 1);
  v_操作员编码 := Substr(v_Temp, 1, Instr(v_Temp, ',') - 1);
  v_Temp       := Substr(v_Temp, Instr(v_Temp, ',') + 1);
  v_操作员姓名 := v_Temp;
  v_Err_Msg    := Null;

  --1.先进行退费

  Select 病人结帐记录_Id.Nextval, Sysdate Into n_冲销id, d_退费时间 From Dual;

  n_Count      := 0;
  n_原结算序号 := 0;
  For c_费用 In (Select Extractvalue(b.Column_Value, '/FY/DJH') As 单据号, Extractvalue(b.Column_Value, '/FY/XH') As 退款序号
               From Table(Xmlsequence(Extract(Xml_In, '/IN/FYLIST/FY'))) B) Loop
  
    If c_费用.单据号 Is Null Then
      v_Err_Msg := '未确定指定退费的单据号,不能退费!';
      Raise Err_Item;
    End If;
    Begin
      Select 结算序号, 结帐id, 病人id
      Into n_Temp, n_结帐id, n_单据病人id
      From 病人预交记录
      Where 结帐id In (Select 结帐id
                     From 门诊费用记录
                     Where NO = c_费用.单据号 And 记录性质 = 1 And Nvl(费用状态, 0) = 0 And 记录状态 In (1, 3) And Rownum < 2) And
            Rownum < 2;
    Exception
      When Others Then
        n_Temp := Null;
    End;
  
    If n_Temp Is Null Then
      v_Err_Msg := '指定的单据号:' || c_费用.单据号 || '未找到,不能退费!';
      Raise Err_Item;
    End If;
    Begin
      Select Max(Decode(Instr(摘要, '挂号:'), 0, '', Replace(摘要, '挂号:', '')))
      Into v_挂号单
      From 门诊费用记录
      Where NO = c_费用.单据号 And 记录性质 = 1 And Nvl(费用状态, 0) = 0 And 记录状态 In (1, 3) And Rownum < 2;
    Exception
      When Others Then
        v_挂号单 := Null;
    End;
    If Not v_挂号单 Is Null Then
      l_挂号单.Extend;
      l_挂号单(l_挂号单.Count) := v_挂号单;
    End If;
  
    If Nvl(n_单据病人id, 0) = 0 Then
      Begin
        Select 病人id
        Into n_单据病人id
        From 门诊费用记录
        Where NO = c_费用.单据号 And 记录性质 = 1 And Nvl(费用状态, 0) = 0 And 记录状态 In (1, 3) And Rownum < 2;
      Exception
        When Others Then
          n_单据病人id := 0;
      End;
    End If;
  
    If Nvl(n_病人id, 0) <> Nvl(n_单据病人id, 0) Then
      v_Err_Msg := '本次退费的收费单:' || c_费用.单据号 || '不是当前病人的收费单,不能退费!';
      Raise Err_Item;
    End If;
  
    If n_原结算序号 <> 0 And n_原结算序号 <> n_Temp Then
      v_Err_Msg := '本次退费的单据号不是一次收费结算,不能退费!';
      Raise Err_Item;
    End If;
    n_原结算序号 := n_Temp;
  
    Select Count(*) Into n_Temp From 费用补充记录 Where 收费结帐id = n_结帐id;
    If Nvl(n_Temp, 0) <> 0 Then
      v_Err_Msg := '本次退费的单据号已经进行了保险补充结算,不能退费!';
      Raise Err_Item;
    End If;
  
    Zl_门诊收费记录_销帐(c_费用.单据号, v_操作员编码, v_操作员姓名, c_费用.退款序号, d_退费时间, v_摘要, n_冲销id);
    n_Count := n_Count + 1;
  End Loop;
  If n_Count = 0 Then
    v_Err_Msg := '未确定本次需要退费的单据,不能退费!';
    Raise Err_Item;
  End If;

  --2.处理退费的结算信息

  n_结帐金额 := 0;

  --检查总金额是否正确 
  Select Sum(结帐金额) Into n_结帐金额 From 门诊费用记录 Where 结帐id = n_冲销id;

  n_误差额 := -1 * Nvl(n_结帐金额, 0) - Nvl(n_退款总额, 0);
  If Abs(n_误差额) > 1.00 Then
    v_Err_Msg := '单据缴款金额与实际结算的误差太大!';
    Raise Err_Item;
  End If;

  --2.确定支付方式
  n_Count := 0;
  For c_结算方式 In (Select Extractvalue(b.Column_Value, '/TK/TKKLB') As 卡类别, Extractvalue(b.Column_Value, '/TK/TKKH') As 卡号,
                        Extractvalue(b.Column_Value, '/TK/TKFS') As 结算方式,
                        -1 * To_Number(Extractvalue(b.Column_Value, '/TK/TKJE')) As 退款金额,
                        Extractvalue(b.Column_Value, '/TK/JYLSH') As 交易流水号,
                        Extractvalue(b.Column_Value, '/TK/JYSM') As 交易说明, Extractvalue(b.Column_Value, '/TK/TKZY') As 摘要,
                        Extractvalue(b.Column_Value, '/TK/TYJK') As 是否退预交,
                        Extractvalue(b.Column_Value, '/TK/SFXFK') As 是否消费卡,
                        Extract(b.Column_Value, '/TK/EXPENDLIST') As Expend
                 From Table(Xmlsequence(Extract(Xml_In, '/IN/TKLIST/TK'))) B) Loop
  
    --1.退回三方卡
    If c_结算方式.卡类别 Is Not Null And Nvl(c_结算方式.是否消费卡, 0) = 0 Then
      --1.三方卡结算
      Third_Cardbalance_Modfiy(n_冲销id, c_结算方式.卡类别, c_结算方式.卡号, c_结算方式.退款金额, c_结算方式.交易流水号, c_结算方式.交易说明, c_结算方式.摘要,
                               c_结算方式.Expend);
    Elsif c_结算方式.卡类别 Is Not Null And Nvl(c_结算方式.是否消费卡, 0) = 1 Then
      --2.消费卡结算
      Square_Cardbalance_Modfiy(n_冲销id, c_结算方式.卡类别, c_结算方式.卡号, c_结算方式.退款金额, c_结算方式.交易流水号, c_结算方式.交易说明, c_结算方式.摘要,
                                c_结算方式.Expend);
    Elsif Nvl(c_结算方式.是否退预交, 0) = 1 Then
      --3.退预交款
      Zl_门诊退费结算_Modify(4, n_病人id, n_冲销id, Null, c_结算方式.退款金额, Null, Null, Null, Null, 0, 0, 0, 0);
    Else
      --4.普通结算
      If c_结算方式.结算方式 Is Null Then
        v_Err_Msg := '未指定指付方式，不允缴款!';
        Raise Err_Item;
      End If;
      --结算方式|结算金额|结算号码|结算摘要||..
      v_退费结算 := c_结算方式.结算方式 || '|' || c_结算方式.退款金额 || '| |' || Nvl(c_结算方式.摘要, '  ');
      v_普通结算 := Nvl(v_普通结算, '') || '||' || v_退费结算;
    End If;
    n_Count := n_Count + 1;
  End Loop;

  --   0-原样退
  --      原样结算一起全退,所有校对标志都为1,医保调用成功后,调整为2,完成后变成0
  --   1-普通退费方式:
  --     ①结算方式_IN:允许传入多个,格式为:"结算方式|结算金额|结算号码|结算摘要||.." ;也允许传入空.
  --   3-医保结算(如果存在医保的结算,则要先删除原医保结算,后按新传入的更新)
  --     ①结算方式_IN:允许传入多个,格式为:结算方式|结算金额||..
  --     ②退支票额_In:传入零
  If n_Count = 0 Then
    v_Err_Msg := '不能有效确认当前的支付方式!';
    Raise Err_Item;
  End If;

  --5.普通结算及完成结
  If v_普通结算 Is Not Null Then
    v_普通结算 := Substr(v_普通结算, 3);
  End If;
  Zl_门诊退费结算_Modify(1, n_病人id, n_冲销id, v_普通结算, 0, Null, Null, Null, Null, 0, 0, n_误差额, 2);

  If l_挂号单.Count <> 0 Then
  
    For I In 0 .. l_挂号单.Count Loop
      x_Templet := Xmltype('<IN></IN>');
      v_Temp    := '<GHDH>' || l_挂号单(I) || '</GHDH>';
      v_Temp    := v_Temp || '<JSKLB>' || 4 || '</JSKLB>';
      v_Temp    := v_Temp || '<GHJE>' || 0 || '</GHJE>';
    
      Select Appendchildxml(x_Templet, '/IN', Xmltype(v_Temp)) Into x_Templet From Dual;
      Zl_Third_Registdel(x_Templet, Xml_Out);
    End Loop;
  Else
    Xml_Out := x_Templet;
  End If;

Exception
  When Err_Item Then
    v_Temp := '<ERROR><MSG>' || v_Err_Msg || '</MSG></ERROR>';
    --Select Appendchildxml(x_Templet, '/OUTPUT', Xmltype(v_Temp)) Into x_Templet From Dual;
    --Xml_Out := x_Templet;
    Raise_Application_Error(-20101, v_Temp);
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_Third_Charge_Del;
/

--77834:刘兴洪,2014-12-05,医保补充结算,移动平台接口整理
Create Or Replace Procedure Zl_Third_Getvisitdetails
(
  Xml_In  In Xmltype,
  Xml_Out Out Xmltype
) Is
  -------------------------------------------------------------------------------------------------- 
  --功能:根据挂号单号获取该次就诊详情
  --入参:Xml_In: 
  --<IN>
  --    <GHDH>挂号单号</GHDH>
  --    <JSKLB>结算卡类别</JSKLB>
  --</IN>
  --出参:Xml_Out 
  --<OUTPUT>
  --    <DJLIST>  //如果为空表示为找到数据
  --        <DJ>
  --            <NO>单据号</NO>
  --            <DJLX>单据类型</DJLX> //1-收费单;4-挂号单
  --            <KDSJ>开单时间</KDSJ>
  --            <ZFZT>支付状态</ZFZT>    //0未支付1已支付
  --            <SFJSK>是否结算卡支付</SFJSK> //即该单据是否存在入参<JSKLB>来进行支付的,是返回1,否则返回0
  --            <LX>类型</LX> //挂号单固定为挂号,其他按收费类别汇总
  --            <ZXKS>执行科室</ZXKS>
  --            <ZXKSID>执行科室ID</ZXKSID>
  --            <MXLIST> 
  --                     <MX>
  --                                <JZSJ>就诊时间</JZSJ>    //挂号有效:yyyy-mm-dd hh24:mi:ss
  --                                <BW>部位</BW>               //检查,检验时有效
  --                                <XM>项目名称</XM>     //挂号无效:其他项目有效
  --                                <ZXZT>执行状态</ZXZT> //挂号:未接诊;已接诊;完成就诊;收费:未执行;已执行;部分执行
  --                                <BG>报告状态</BG>// 1-已出报告;0未出报告,检查,检验时有效 
  --                                <BLID>病历ID</BLID>  //如果<BG>字段为1，该值不为空,检查,检验时有效
  --                                <GG>规格</GG>                       //药品有效
  --                                <SL>数量</SL> //非挂号有效
  --                                <DW>单位</DW> //非挂号有效
  --                                <DJ>单价</DJ> //非挂号有效
  --                                <JE>金额</JE>  
  --                     </MX>
  --             </MXLIST>
  --             <DL> //队列
  --                        <XH>序号</XH>
  --                        <QMRS>前面人数</QMRS>  //(由Oracle函数zl_GetSequenceBeforPerons获取)
  --             </DL>
  --        </DJ>
  --    </DJLIST>
  --    <ERROR><MSG></MSG></ERROR>                      //如果错误返回
  --</OUTPUT>

  -------------------------------------------------------------------------------------------------- 
  v_Err_Msg Varchar2(200);
  Err_Item Exception;
  x_Templet Xmltype; --模板XML 

  v_卡类别   Varchar2(100);
  n_卡类别id Number(18);
  v_挂号单   Varchar2(10);
  v_排队号码 Varchar2(10);
  n_Temp     Number(18);

  n_Count Number(18);

  v_Temp       Varchar2(32767); --临时XML 
  v_队列       Varchar2(32767);
  v_No         Varchar2(50);
  v_Tmp        Varchar2(4000);
  n_Add_Djlist Number(1); --是否增加了DJLIST的;
Begin
  x_Templet := Xmltype('<OUTPUT></OUTPUT>');

  Select Extractvalue(Value(A), 'IN/GHDH'), Extractvalue(Value(A), 'IN/JSKLB')
  Into v_挂号单, v_卡类别
  From Table(Xmlsequence(Extract(Xml_In, 'IN'))) A;

  If v_挂号单 Is Null Then
    v_Err_Msg := '不能找到指定的挂号单号(当前挂号单号为空)';
    Raise Err_Item;
  End If;
  n_Add_Djlist := 0;

  v_Err_Msg := Null;
  If v_卡类别 Is Not Null Then
    Begin
      n_卡类别id := To_Number(v_卡类别);
    Exception
      When Others Then
        n_卡类别id := 0;
    End;
  
    If n_卡类别id = 0 Then
      Begin
        Select ID, Decode(Nvl(是否启用, 0), 1, Null, 名称 || '未启用,不允许进行缴费!')
        Into n_卡类别id, v_Err_Msg
        From 医疗卡类别
        Where 名称 = v_卡类别;
      Exception
        When Others Then
          v_Err_Msg := '卡类别:' || v_卡类别 || '不存在!';
      End;
    
    Else
    
      Begin
        Select ID, Decode(Nvl(是否启用, 0), 1, Null, 名称 || '未启用,不允许进行缴费!')
        Into n_卡类别id, v_Err_Msg
        From 医疗卡类别
        Where ID = n_卡类别id;
      Exception
        When Others Then
          v_Err_Msg := '未找到指定的结算支付信息!';
      End;
    
    End If;
    If Not v_Err_Msg Is Null Then
      Raise Err_Item;
    End If;
  End If;
  --1.获取挂号数据
  n_Count := 0;
  For c_挂号 In (Select a.Id, a.No, a.记录性质, a.执行部门id, c.名称 As 执行部门, To_Char(a.登记时间, 'yyyy-mm-dd hh24:mi:ss') As 登记时间,
                      a.预约时间, a.接收时间, To_Char(a.发生时间, 'yyyy-mm-dd HH24:mi') As 就诊时间, a.号别, a.号序, b.金额, a.记录状态,
                      Decode(Nvl(a.执行状态, 0), 0, '等待接诊', 1, '完成就诊', 2, '正在就诊', -1, '取消就诊') As 执行状态,
                      Decode(Nvl(b.结帐id, 0), 0, 0, 1) As 支付标志
               From 病人挂号记录 A,
                    (Select NO, Max(Nvl(结帐id, 0)) As 结帐id, Sum(实收金额) As 金额
                      From 门诊费用记录 B
                      Where 记录性质 = 4 And NO = v_挂号单
                      Group By NO) B, 部门表 C
               Where a.No = v_挂号单 And a.No = b.No And a.执行部门id = c.Id(+)) Loop
    If Nvl(c_挂号.记录状态, 0) <> 1 Then
      v_Err_Msg := '单据号:' || v_挂号单 || '已经被退号!';
      Raise Err_Item;
    End If;
    Begin
      Select 排队号码 Into v_排队号码 From 排队叫号队列 Where 业务id = c_挂号.Id And Nvl(业务类型, 0) = 0;
    Exception
      When Others Then
        v_排队号码 := Null;
    End;
    If v_排队号码 Is Not Null Then
      --业务id_In ,业务类型_In 排队号码_In Number := Null
      n_Temp := Zl_Getsequencebeforperons(c_挂号.Id, 0, v_排队号码);
      v_队列 := v_队列 || '<DL><XH>' || v_排队号码 || '</XH><QMRS>' || n_Temp || '</QMRS></DL>';
    End If;
    n_Temp := 0;
    If Nvl(n_卡类别id, 0) <> 0 Then
      Begin
        Select 1
        Into n_Temp
        From 病人预交记录
        Where NO = v_挂号单 And 记录性质 = 4 And 记录状态 In (1, 3) And 卡类别id = n_卡类别id And Rownum < 2;
      Exception
        When Others Then
          Null;
      End;
    End If;
    v_Temp := '<NO>' || c_挂号.No || '</NO>';
    v_Temp := v_Temp || '<DJLX>' || 4 || '</DJLX>';
    v_Temp := v_Temp || '<KDSJ>' || c_挂号.登记时间 || '</KDSJ>';
    v_Temp := v_Temp || '<ZFZT>' || c_挂号.支付标志 || '</ZFZT>';
    v_Temp := v_Temp || '<SFJSK>' || n_Temp || '</SFJSK>';
    v_Temp := v_Temp || '<LX>挂号</LX>';
    v_Temp := v_Temp || '<ZXKS>' || c_挂号.执行部门 || '</ZXKS>';
    v_Temp := v_Temp || '<ZXKSID>' || c_挂号.执行部门id || '</ZXKSID>';
    v_Temp := v_Temp || '<MXLIST><MX><JZSJ>' || c_挂号.就诊时间 || '</JZSJ><JE>' || c_挂号.金额 || '</JE></MX></MXLIST>';
    If v_队列 Is Not Null Then
      v_Temp := v_Temp || v_队列;
    End If;
  
    If Nvl(n_Add_Djlist, 0) = 0 Then
      --增加DJList节点
      Select Appendchildxml(x_Templet, '/OUTPUT', Xmltype('<DJLIST></DJLIST>')) Into x_Templet From Dual;
      n_Add_Djlist := 1;
    End If;
    v_Temp := '<DJ>' || v_Temp || '</DJ>';
    Select Appendchildxml(x_Templet, '/OUTPUT/DJLIST', Xmltype(v_Temp)) Into x_Templet From Dual;
    n_Count := n_Count + 1;
  End Loop;
  If Nvl(n_Count, 0) = 0 Then
    v_Err_Msg := '未找到指定的挂号单据:' || v_挂号单 || '!';
    Raise Err_Item;
  End If;

  --2.分类汇总收费单
  v_No := '-_';

  For c_费用 In (Select j.医嘱id, j.相关id As 组号, j.No, j.序号, j.收费类别, i.名称 As 收费类别名, j.执行部门id, q.名称 As 执行部门, j.收费细目id, m.名称,
                      m.规格, Max(j.计算单位) As 计算单位, Decode(Max(j.执行状态), 0, '未执行', 1, '完全执行', 2, '部分执行', '') As 执行状态,
                      Max(j.付款状态) As 付款状态, To_Char(Max(j.登记时间), 'yyyy-mm-dd hh24:mi:ss') As 登记时间, Max(j.单价) As 单价,
                      Sum(j.数量) As 数量, Sum(j.实收金额) As 实收金额
               From (Select a.相关id, a.Id As 医嘱id, b.No, b.收费类别, Max(Decode(b.记录状态, 0, 0, 1)) As 付款状态, b.结帐id, b.执行部门id,
                             Max(Decode(b.记录状态, 2, 0, b.执行状态)) As 执行状态,
                             Max(Decode(b.记录状态, 2, Null + Sysdate, b.登记时间)) As 登记时间, Nvl(b.价格父号, b.序号) As 序号, b.收费细目id,
                             b.计算单位, Sum(b.标准单价) As 单价, Avg(Nvl(b.付数, 1) * b.数次) As 数量, Sum(b.实收金额) As 实收金额


                      From 门诊费用记录 B, 病人医嘱记录 A
                      Where Mod(b.记录性质, 10) = 1 And a.Id = b.医嘱序号 And Nvl(b.费用状态, 0) = 0 And a.挂号单 = v_挂号单
                      Group By a.相关id, a.Id, b.No, b.收费类别, b.结帐id, b.执行部门id, Nvl(b.价格父号, b.序号), b.收费细目id, b.计算单位) J,
                    收费项目目录 M, 部门表 Q, 收费项目类别 I
               Where j.收费细目id = m.Id And j.执行部门id = q.Id(+) And j.收费类别 = i.编码(+)
               Group By j.医嘱id, j.相关id, j.No, j.序号, j.收费类别, i.名称, j.执行部门id, q.名称, j.收费细目id, m.名称, m.规格
               Order By 登记时间 Desc, NO Desc, 收费类别, 序号) Loop
    If c_费用.No <> v_No Then
      n_Temp := 0;
      --单据不同,则产生的结构不同
      If Nvl(c_费用.付款状态, 0) = 1 Then
        --是否结算卡支付的
        Begin
          Select 1
          Into n_Temp
          From 病人预交记录 A, 门诊费用记录 B
          Where a.结帐id = b.结帐id And b.No = c_费用.No And Mod(b.记录性质, 10) = 1 And b.记录状态 In (1, 3) And a.卡类别id = n_卡类别id And
                Rownum < 2;
        Exception
          When Others Then
            n_Temp := 0;
        End;
      End If;
      v_Tmp := Null;
      Begin
        Select Wm_Concat(名称)
        Into v_Tmp
        From (Select Distinct b.名称
               From 门诊费用记录 A, 收费项目类别 B
               Where a.收费类别 = b.编码 And a.No = c_费用.No And a.记录性质 = 1 And a.记录状态 In (1, 3));
      Exception
        When Others Then
          Null;
      End;
    
      If Nvl(n_Add_Djlist, 0) = 0 Then
        --增加DJList节点
        Select Appendchildxml(x_Templet, '/OUTPUT', Xmltype('<DJLIST></DJLIST>')) Into x_Templet From Dual;
        n_Add_Djlist := 1;
      End If;
    
      v_No   := c_费用.No;
      v_Temp := '<NO>' || c_费用.No || '</NO>';
      v_Temp := v_Temp || '<DJLX>' || 1 || '</DJLX>';
      v_Temp := v_Temp || '<KDSJ>' || c_费用.登记时间 || '</KDSJ>';
      v_Temp := v_Temp || '<ZFZT>' || c_费用.付款状态 || '</ZFZT>';
      v_Temp := v_Temp || '<SFJSK>' || n_Temp || '</SFJSK>';
    
      v_Temp := v_Temp || '<LX>' || Nvl(Replace(v_Tmp, ',', '/'), '') || '</LX>';
      v_Temp := v_Temp || '<ZXKS>' || c_费用.执行部门 || '</ZXKS>';
      v_Temp := v_Temp || '<ZXKSID>' || c_费用.执行部门id || '</ZXKSID>';
      v_Temp := v_Temp || '<MXLIST></MXLIST>' || Nvl(v_队列, '') || '';
      v_Temp := '<DJ NO="' || c_费用.No || '">' || v_Temp || '</DJ>';
      Select Appendchildxml(x_Templet, '/OUTPUT/DJLIST', Xmltype(v_Temp)) Into x_Templet From Dual;
    End If;
  
    v_Temp := '<XM>' || Nvl(c_费用.名称, '') || '</XM>';
    If c_费用.收费类别 = 'D' Then
      --检查获取部位
      Begin
        Select Wm_Concat(标本部位) Into v_Tmp From 病人医嘱记录 Where 相关id = c_费用.医嘱id;
      Exception
        When Others Then
          v_Tmp := Null;
      End;
      v_Temp := v_Temp || '<BW>' || Nvl(v_Tmp, '') || '</BW>';
    Elsif c_费用.收费类别 = 'C' Then
      --检验
      Begin
        Select Max(Decode(b.审核时间, Null, 0, 1))
        Into n_Temp
        From 病人医嘱记录 A, 检验标本记录 B
        Where a.Id = c_费用.医嘱id And a.Id = b.医嘱id(+) And Exists
         (Select 1 From 病人医嘱记录 Where 相关id = c_费用.医嘱id And 诊疗类别 = 'C');
      Exception
        When Others Then
          n_Temp := 0;
      End;
      v_Temp := v_Temp || '<BG>' || n_Temp || '</BG>';
      If n_Temp = 1 Then
        --取病历ID
        Begin
          Select 病历id
          Into n_Temp
          From 病人医嘱报告
          Where 医嘱id = c_费用.医嘱id And Nvl(病历id, 0) <> 0 And Rownum < 2;
        Exception
          When Others Then
            n_Temp := Null;
        End;
        v_Temp := v_Temp || '<BLID>' || Nvl(n_Temp, '') || '</BLID>';
      End If;
    End If;
  
    v_Temp := v_Temp || '<GG>' || Nvl(c_费用.规格, '') || '</GG>';
    v_Temp := v_Temp || '<SL>' || Nvl(c_费用.数量, 0) || '</SL>';
    v_Temp := v_Temp || '<DW>' || Nvl(c_费用.计算单位, '') || '</DW>';
    v_Temp := v_Temp || '<DJ>' || Nvl(c_费用.单价, 0) || '</DJ>';
    v_Temp := v_Temp || '<JE>' || Nvl(c_费用.实收金额, 0) || '</JE>';
    v_Temp := '<MX>' || v_Temp || '</MX>';
    Select Appendchildxml(x_Templet, '/OUTPUT/DJLIST/DJ[@NO="' || v_No || '"]/MXLIST', Xmltype(v_Temp))
    Into x_Templet
    From Dual;
  
  End Loop;
  Xml_Out := x_Templet;
Exception
  When Err_Item Then
    v_Temp := '<ERROR><MSG>' || v_Err_Msg || '</MSG></ERROR>';
    Select Appendchildxml(x_Templet, '/OUTPUT', Xmltype(v_Temp)) Into x_Templet From Dual;
    Xml_Out := x_Templet;
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_Third_Getvisitdetails;
/

--77834:刘兴洪,2014-10-30,医保补充结算,移动平台接口整理
Create Or Replace Function Zl_Getclinicchargepaywins(Nos_In Varchar2)
--功能:获取本次收费的发药窗口
  --返回:执行部门ID|发药窗口;... 
 Return Varchar2 Is
  v_发药窗口 Varchar2(4000);
  v_Paywin   Varchar2(100);
  Function Getnotpaydrugwinds
  (
    病人id_In 门诊费用记录.病人id%Type,
    药房id_In 门诊费用记录.执行部门id%Type
  ) Return Varchar2 Is
    v_Temp Varchar2(100);
  Begin
    For c_发药窗口 In (Select 发药窗口
                   From 未发药品记录
                   Where 单据 = 8 And 发药窗口 Is Not Null And 病人id = 病人id_In And 库房id = 药房id_In
                   Order By 已收费 Desc, 填制日期 Desc) Loop
      v_Temp := c_发药窗口.发药窗口;
      Exit;
    End Loop;
    Return v_Temp;
  End;

  Function Getdefaultpaydrugwinds
  (
    收费类别_In 门诊费用记录.收费类别%Type,
    药房id_In   门诊费用记录.执行部门id%Type
  ) Return Varchar2 Is
    v_西窗   Varchar2(100);
    n_西药房 Number(18);
  
    v_成窗   Varchar2(100);
    n_成药房 Number(18);
    v_中窗   Varchar2(100);
    n_中药房 Number(18);
    v_Temp   Varchar2(1000);
  
    n_药房id    Number(18);
    v_Split_Str Varchar2(4000);
  Begin
    v_Temp := Null;
    If 收费类别_In = '5' Then
      v_西窗   := zl_GetSysParameter(50, 1121);
      n_西药房 := To_Number(zl_GetSysParameter(18, 1121));
      If Instr(v_西窗, ':') > 0 Then
        --旧数据没有存药房ID
        v_Temp := v_西窗;
      Elsif n_西药房 > 0 And Nvl(v_西窗, '-') <> '-' Then
        v_Temp := n_西药房 || ':' || v_西窗;
      
      End If;
    Elsif 收费类别_In = '6' Then
      v_成窗   := zl_GetSysParameter(51, 1121);
      n_成药房 := To_Number(zl_GetSysParameter(19, 1121));
      If Instr(v_成窗, ':') > 0 Then
        v_Temp := v_成窗;
      Elsif n_成药房 > 0 And Nvl(v_成窗, '-') <> '-' Then
        v_Temp := n_成药房 || ':' || v_成窗;
      End If;
    Elsif 收费类别_In = '7' Then
      v_中窗   := zl_GetSysParameter(51, 1121);
      n_中药房 := To_Number(zl_GetSysParameter(19, 1121));
      If Instr(v_中窗, ':') > 0 Then
        v_Temp := v_中窗;
      Elsif n_中药房 > 0 And Nvl(v_中窗, '-') <> '-' Then
        v_Temp := n_中药房 || ':' || v_中窗;
      End If;
    Else
      Return Null;
    End If;
  
    If v_Temp Is Null Then
      Return Null;
    End If;
  
    --发药窗口格式:"药房ID:发药窗口,药房ID:发药窗口"
    v_Split_Str := v_Temp || ',';
    While v_Split_Str Is Not Null Loop
      v_Temp := Substr(v_Split_Str, 1, Instr(v_Split_Str, ',') - 1);
    
      n_药房id := To_Number(Substr(v_Temp, 1, Instr(v_Temp, ':') - 1));
      v_Temp   := Substr(v_Temp, Instr(v_Temp, ':') + 1);
    
      If n_药房id = 药房id_In Then
        Return v_Temp;
      End If;
      v_Split_Str := Substr(v_Split_Str, Instr(v_Split_Str, ',') + 1);
    End Loop;
  End;

Begin

  v_发药窗口 := Null;
  For c_发药窗口 In (Select Distinct 执行部门id, 收费类别, 发药窗口, 病人id
                 From 门诊费用记录 A
                 Where 记录状态 = 0 And a.记录性质 = 1 And a.收费类别 In ('5', '6', '7') And
                       NO In (Select Column_Value From Table(f_Str2list(Nos_In)))) Loop
  
    If Instr(';' || Nvl(v_发药窗口, '-'), ';' || c_发药窗口.执行部门id || '|') = 0 Then
      --a.存在未发药的，先以未发药的为准
      v_Paywin := Getnotpaydrugwinds(c_发药窗口.病人id, c_发药窗口.执行部门id);
      If v_Paywin Is Null Then
        v_Paywin := c_发药窗口.发药窗口;
      End If;
      If v_Paywin Is Null Then
        --b.获取缺省的发药窗口
        v_Paywin := Getdefaultpaydrugwinds(c_发药窗口.收费类别, c_发药窗口.执行部门id);
      End If;
      If v_Paywin Is Null Then
        --b.根据闲忙或平均方式获取发药窗口
        v_Paywin := Zl_Get发药窗口(c_发药窗口.执行部门id);
      End If;
      v_发药窗口 := v_发药窗口 || ';' || c_发药窗口.执行部门id || '|' || v_Paywin;
    End If;
  End Loop;

  Return v_发药窗口;

Exception
  When Others Then
    Return v_发药窗口;
End Zl_Getclinicchargepaywins;
/

--77872:李南春,2014-10-28,是否转帐及代扣
Create Or Replace Procedure Zl_医疗卡类别_Update
(
  Id_In           In 医疗卡类别.ID%Type,
  编码_In         In 医疗卡类别.编码%Type,
  名称_In         In 医疗卡类别.名称%Type,
  短名_In         In 医疗卡类别.短名%Type,
  前缀文本_In     In 医疗卡类别.前缀文本%Type,
  卡号长度_In     In 医疗卡类别.卡号长度%Type,
  缺省标志_In     In 医疗卡类别.缺省标志%Type,
  是否固定_In     In 医疗卡类别.是否固定%Type,
  是否严格控制_In In 医疗卡类别.是否严格控制%Type,
  是否刷卡_In     In 医疗卡类别.是否刷卡%Type,
  是否自制_In     In 医疗卡类别.是否自制%Type,
  是否存在帐户_In In 医疗卡类别.是否存在帐户%Type,
  是否全退_In     In 医疗卡类别.是否全退%Type,
  部件_In         In 医疗卡类别.部件%Type,
  备注_In         In 医疗卡类别.备注%Type,
  特定项目_In     In 医疗卡类别.特定项目%Type,
  收费细目id_In   In 收费项目目录.ID%Type,
  结算方式_In     In 医疗卡类别.结算方式%Type,
  是否启用_In     In 医疗卡类别.是否启用%Type,
  卡号密文_In     In 医疗卡类别.卡号密文%Type,
  是否重复使用_In In 医疗卡类别.是否重复使用%Type,
  密码长度_In     In 医疗卡类别.密码长度%Type,
  密码长度限制_In In 医疗卡类别.密码长度限制%Type,
  密码规则_In     In 医疗卡类别.密码规则%Type,
  是否退现_In     In 医疗卡类别.是否退现%Type,
  操作方式_In     In Integer := 0,
  是否模糊查找_In     In 医疗卡类别.是否模糊查找%Type:=0,
  密码输入限制_In In 医疗卡类别.密码输入限制%Type := 0,
  是否缺省密码_In In 医疗卡类别.是否缺省密码%Type := 0,
  是否制卡_In     In 医疗卡类别.是否制卡%Type :=0,
  是否发卡_In     In 医疗卡类别.是否发卡%Type:=0,
  是否写卡_In     In 医疗卡类别.是否写卡%Type:=0,
  险类_In         In 医疗卡类别.险类%Type:=0,
  发卡性质_In     In 医疗卡类别.发卡性质%Type:=0,
  是否转帐及代扣_In In 医疗卡类别.是否转帐及代扣%Type:=0
) Is
  v_Err_Msg Varchar2(200);
  Err_Item Exception;
Begin
  If Nvl(是否自制_In, 0) = 1 And Nvl(是否存在帐户_In, 0) = 1 Then
    v_Err_Msg := '[ZLSOFT]是否存在帐户只有非自制卡才会存在,请检查![ZLSOFT]';
    Raise Err_Item;
  End If;
  If Nvl(是否自制_In, 0) = 0 And Nvl(是否存在帐户_In, 0) = 1 And 结算方式_In Is Null Then
    v_Err_Msg := '[ZLSOFT]第三方机构的卡结算，必须要设置结算方式,请检查![ZLSOFT]';
    Raise Err_Item;
  End If;
  If Nvl(是否自制_In, 0) = 1 And 特定项目_In Is Null Then
    v_Err_Msg := '[ZLSOFT]特定项目必须输入,请检查![ZLSOFT]';
    Raise Err_Item;
  End If;

  If 操作方式_In = 0 Then
    Insert Into 医疗卡类别
      (ID, 编码, 名称, 短名, 前缀文本, 卡号长度, 缺省标志, 是否固定, 是否严格控制, 是否刷卡, 是否自制, 是否存在帐户,
       是否全退, 部件, 备注, 特定项目, 结算方式, 是否启用, 卡号密文, 是否重复使用, 密码长度, 密码长度限制, 密码规则,
       是否退现,是否模糊查找,密码输入限制,是否缺省密码,是否制卡,是否发卡,是否写卡,险类,发卡性质,是否转帐及代扣)
    Values
      (Id_In, 编码_In, 名称_In, 短名_In, 前缀文本_In, 卡号长度_In, 缺省标志_In, 是否固定_In, 是否严格控制_In,
       是否刷卡_In, 是否自制_In, 是否存在帐户_In, 是否全退_In, 部件_In, 备注_In, 特定项目_In, 结算方式_In, 是否启用_In,
       卡号密文_In, 是否重复使用_In, 密码长度_In, 密码长度限制_In, 密码规则_In, 是否退现_In,是否模糊查找_In,密码输入限制_In,是否缺省密码_In,是否制卡_In,是否发卡_In,是否写卡_In,险类_In,
     发卡性质_In,是否转帐及代扣_In);

  Else

    Delete 收费特定项目 Where 特定项目 = (Select Max(特定项目) From 医疗卡类别 Where ID = Id_In);

    Update 医疗卡类别
    Set 编码 = 编码_In, 名称 = 名称_In, 短名 = 短名_In, 前缀文本 = 前缀文本_In, 卡号长度 = 卡号长度_In,
        缺省标志 = 缺省标志_In, 是否严格控制 = 是否严格控制_In, 是否刷卡 = 是否刷卡_In, 是否自制 = 是否自制_In,
        是否存在帐户 = 是否存在帐户_In, 是否全退 = 是否全退_In, 部件 = 部件_In, 备注 = 备注_In, 特定项目 = 特定项目_In,
        结算方式 = 结算方式_In, 是否启用 = 是否启用_In, 卡号密文 = 卡号密文_In, 是否重复使用 = 是否重复使用_In,
        密码长度 = 密码长度_In, 密码长度限制 = 密码长度限制_In, 密码规则 = 密码规则_In, 是否退现 = 是否退现_In,是否模糊查找=是否模糊查找_In,
    密码输入限制=密码输入限制_In,是否缺省密码=是否缺省密码_In,是否制卡=是否制卡_In,是否发卡=是否发卡_In,是否写卡=是否写卡_In,险类=险类_In,
    发卡性质=发卡性质_In,是否转帐及代扣=是否转帐及代扣_In
    Where ID = Id_In;
    If Nvl(缺省标志_In, 0) = 1 Then
      Update 医疗卡类别 Set 缺省标志 = 0 Where ID <> Id_In;
    End If;

  End If;
  If Not 特定项目_In Is Null And Nvl(收费细目id_In, 0) <> 0 Then
    Insert Into 收费特定项目 (特定项目, 收费细目id) Values (特定项目_In, 收费细目id_In);
  End If;
Exception
  When Err_Item Then
    Raise_Application_Error(-20101, v_Err_Msg);
  When Others Then
    Zl_Errorcenter(Sqlcode, Sqlerrm);
End Zl_医疗卡类别_Update;
/

--77834:刘尔旋,2014-10-24,门诊二次结算
Create Or Replace Procedure Zl_病人结算记录_Update
(
  结帐id_In       病人预交记录.结帐id%Type,
  保险结算_In     Varchar2, --"结算方式|结算金额||....."
  结帐_In         Number := 0,
  缺省结算方式_In Varchar2 := Null,
  缺省冲预交_In   Number := 0, --0-用现金缴款,1:剩于款项用冲预交支付(门诊预交),2-剩于款项用冲预交支付(住院预交)
  卡类别id_In     病人预交记录.卡类别id%Type := Null,
  结算卡序号_In   病人预交记录.结算卡序号%Type := Null,
  卡号_In         病人预交记录.卡号%Type := Null,
  交易流水号_In   病人预交记录.交易流水号%Type := Null,
  交易说明_In     病人预交记录.交易说明%Type := Null,
  合作单位_In     病人预交记录.合作单位%Type := Null
  
) As
  --该游标为要删除的由费用记录产生的结算记录

  Cursor c_Del Is
    Select a.Id, a.记录性质, a.冲预交, a.结算方式, b.性质, a.预交类别
    From 病人预交记录 A, 结算方式 B
    Where a.结算方式 = b.名称 And a.结帐id = 结帐id_In;

  --相关信息
  v_No         病人预交记录.No%Type;
  v_病人id     住院费用记录.病人id%Type;
  v_主页id     住院费用记录.主页id%Type;
  v_登记时间   住院费用记录.登记时间%Type;
  v_操作员编号 住院费用记录.操作员编号%Type;
  v_操作员姓名 住院费用记录.操作员姓名%Type;

  --本次结算变量
  v_金额合计 病人预交记录.冲预交%Type;

  --保险结算
  v_保险结算 Varchar2(255);
  v_当前结算 Varchar2(50);
  v_结算方式 病人预交记录.结算方式%Type;
  v_结算金额 病人预交记录.冲预交%Type;

  v_记录性质 病人预交记录.记录性质%Type;
  v_缺省     病人预交记录.结算方式%Type;

  --分币处理及误差变量
  v_Centmode   Number;
  v_现金金额   病人预交记录.冲预交%Type;
  v_Cashcented 病人预交记录.冲预交%Type;
  v_误差金额   病人预交记录.冲预交%Type;
  v_零钱       病人预交记录.冲预交%Type;
  v_费用id     住院费用记录.Id%Type;
  v_序号       住院费用记录.序号%Type;
  v_收费类别   住院费用记录.收费类别%Type;
  v_收费细目id 住院费用记录.收费细目id%Type;
  v_收入项目id 住院费用记录.收入项目id%Type;
  v_收据费目   住院费用记录.收据费目%Type;
  v_Sign       Number;
  n_Noexists   Number(3);
  n_医疗小组id 住院费用记录.医疗小组id%Type;
  n_结算序号   病人预交记录.结算序号%Type;
  n_费用状态   门诊费用记录.费用状态%Type;
  n_预交金额   病人预交记录.金额%Type;
  n_当前金额   病人预交记录.金额%Type;
  v_误差项     结算方式.名称%Type;

  --临时变量
  Err_Item Exception;
  v_Err_Msg  Varchar2(255);
  n_组id     财务缴款分组.Id%Type;
  n_执行状态 门诊费用记录.执行状态%Type;
Begin
  --如果缺省结算方式为空，则取现金结算方式
  If 缺省结算方式_In Is Null Then
    Begin
      Select 名称 Into v_缺省 From 结算方式 Where 性质 = 1 And Rownum < 2;
    Exception
      When Others Then
        v_缺省 := '现金';
    End;
  Else
    v_缺省 := 缺省结算方式_In;
  End If;

  --取得本次结算的相关信息
  If Nvl(结帐_In, 0) = 1 Then
    Select NO, 病人id, 收费时间, 操作员编号, 操作员姓名, 缴款组id, 0
    Into v_No, v_病人id, v_登记时间, v_操作员编号, v_操作员姓名, n_组id, n_执行状态
    From 病人结帐记录
    Where ID = 结帐id_In;
  Else
    Begin
      n_Noexists := 0;
      Select NO, 病人id, 登记时间, 操作员编号, 操作员姓名, 缴款组id, 执行状态, 费用状态
      Into v_No, v_病人id, v_登记时间, v_操作员编号, v_操作员姓名, n_组id, n_执行状态, n_费用状态
      From 门诊费用记录
      Where 结帐id = 结帐id_In And Rownum < 2;
    Exception
      When Others Then
        n_Noexists := 1;
    End;
    If n_Noexists = 1 Then
      --费用记录不存在，从补充记录中找
      Select NO, 病人id, 登记时间, 操作员编号, 操作员姓名, 缴款组id, 费用状态
      Into v_No, v_病人id, v_登记时间, v_操作员编号, v_操作员姓名, n_组id, n_费用状态
      From 费用补充记录
      Where 结算id = 结帐id_In And Rownum < 2;
    End If;
    If Nvl(n_费用状态, 0) = 1 Then
      --异常单据为空:
      v_缺省 := Null;
    End If;
  
    Begin
      --20051027 陈东
      Select 记录性质
      Into v_记录性质
      From 病人预交记录
      Where 结帐id = 结帐id_In And Rownum = 1 And Mod(记录性质, 10) <> 1;
    Exception
      When Others Then
        v_记录性质 := -1;
    End;
    If v_记录性质 = -1 Then
      Begin
        Select Decode(记录性质, 1, 3, 11, 3, 4, 4, 记录性质)
        Into v_记录性质
        From 门诊费用记录
        Where 结帐id = 结帐id_In And Rownum = 1;
      Exception
        When Others Then
          --可能是卡费
          Select 记录性质 Into v_记录性质 From 住院费用记录 Where 结帐id = 结帐id_In And Rownum = 1;
      End;
    End If;
  End If;

  If Nvl(v_病人id, 0) <> 0 And Nvl(结帐_In, 0) = 1 Then
    Select 主页id Into v_主页id From 病人信息 Where 病人id = v_病人id;
  End If;
  Select 结算序号 Into n_结算序号 From 病人预交记录 Where 结帐id = 结帐id_In And Rownum = 1;

  ----回退缴款,预交不动,因为没有改冲预交的
  --收费未最未最终完成的,代表按异常单据修正,不处理人员缴款余额
  v_金额合计 := 0;
  For r_Del In c_Del Loop
    If r_Del.记录性质 Not In (1, 11) Then
      If Nvl(n_费用状态, 0) <> 1 Then
        Update 人员缴款余额
        Set 余额 = Nvl(余额, 0) - r_Del.冲预交
        Where 收款员 = v_操作员姓名 And 性质 = 1 And 结算方式 = r_Del.结算方式;
      
        If Sql%RowCount = 0 Then
          Insert Into 人员缴款余额
            (收款员, 结算方式, 性质, 余额)
          Values
            (v_操作员姓名, r_Del.结算方式, 1, -1 * r_Del.冲预交);
        End If;
      End If;
      v_金额合计 := v_金额合计 + r_Del.冲预交;
      Delete From 病人预交记录 Where ID = r_Del.Id;
    Else
      --检查是否冲预交
      If Nvl(缺省冲预交_In, 0) <> 0 Then
        v_金额合计 := v_金额合计 + r_Del.冲预交;
        If Nvl(n_费用状态, 0) <> 1 Then
          Update 病人余额
          Set 预交余额 = Nvl(预交余额, 0) + Nvl(r_Del.冲预交, 0)
          Where 病人id = v_病人id And 类型 = Nvl(r_Del.预交类别, 2);
          If Sql%NotFound Then
            Insert Into 病人余额
              (病人id, 性质, 预交余额, 费用余额, 类型)
            Values
              (v_病人id, 1, Nvl(r_Del.冲预交, 0), 0, Nvl(r_Del.预交类别, 2));
          End If;
        End If;
        If r_Del.记录性质 = 1 Then
          Update 病人预交记录 Set 冲预交 = 0 Where ID = r_Del.Id;
        Else
          Delete 病人预交记录 Where ID = r_Del.Id;
        End If;
      End If;
    End If;
  End Loop;

  --------------------------------------------------------------------------------------------------------------
  --------------------------------------------------------------------------------------------------------------
  --产生医保支付结算
  If 保险结算_In Is Not Null Then
    --各个保险结算
    v_保险结算 := 保险结算_In || '||';
    While v_保险结算 Is Not Null Loop
      v_当前结算 := Substr(v_保险结算, 1, Instr(v_保险结算, '||') - 1);
    
      v_结算方式 := Substr(v_当前结算, 1, Instr(v_当前结算, '|') - 1);
      v_结算金额 := To_Number(Substr(v_当前结算, Instr(v_当前结算, '|') + 1));
    
      Insert Into 病人预交记录
        (ID, 记录性质, NO, 记录状态, 病人id, 主页id, 摘要, 结算方式, 收款时间, 操作员编号, 操作员姓名, 冲预交, 结帐id, 缴款组id, 结算序号)
      Values
        (病人预交记录_Id.Nextval, Decode(结帐_In, 1, 2, v_记录性质), v_No, 1, v_病人id, v_主页id, '保险部份', v_结算方式, v_登记时间, v_操作员编号,
         v_操作员姓名, v_结算金额, 结帐id_In, n_组id, n_结算序号);
    
      v_金额合计 := v_金额合计 - v_结算金额;
    
      v_保险结算 := Substr(v_保险结算, Instr(v_保险结算, '||') + 2);
    End Loop;
  End If;
  --剩余部分用预交
  If Nvl(缺省冲预交_In, 0) <> 0 And v_金额合计 <> 0 Then
  
    n_预交金额 := v_金额合计;
    For c_预交 In (Select *
                 From (Select a.Id, a.记录状态, a.预交类别, a.No, Nvl(a.金额, 0) As 金额
                        From 病人预交记录 A,
                             (Select NO, Sum(Nvl(a.金额, 0)) As 金额
                               From 病人预交记录 A
                               Where a.结帐id Is Null And Nvl(a.金额, 0) <> 0 And a.病人id = v_病人id And Nvl(a.预交类别, 2) = 缺省冲预交_In
                               Group By NO
                               Having Sum(Nvl(a.金额, 0)) <> 0) B
                        Where a.结帐id Is Null And Nvl(a.金额, 0) <> 0 And
                              a.结算方式 Not In (Select 名称 From 结算方式 Where 性质 = 5) And a.No = b.No And a.病人id = v_病人id And
                              Nvl(a.预交类别, 2) = 缺省冲预交_In
                        Union All
                        Select 0 As ID, 记录状态, 预交类别, NO, Sum(Nvl(金额, 0) - Nvl(冲预交, 0)) As 金额
                        From 病人预交记录
                        Where 记录性质 In (1, 11) And 结帐id Is Not Null And Nvl(金额, 0) <> Nvl(冲预交, 0) And 病人id = v_病人id And
                              Nvl(预交类别, 2) = 缺省冲预交_In Having Sum(Nvl(金额, 0) - Nvl(冲预交, 0)) <> 0
                        Group By 记录状态, NO, 预交类别)
                 Order By ID, NO) Loop
    
      n_当前金额 := Case
                  When c_预交.金额 - n_预交金额 < 0 Then
                   c_预交.金额
                  Else
                   n_预交金额
                End;
    
      If c_预交.Id <> 0 Then
        --第一次冲预交
        Update 病人预交记录 Set 冲预交 = n_当前金额, 结帐id = 结帐id_In, 结算序号 = n_结算序号 Where ID = c_预交.Id;
      Else
        --冲上次剩余额
        Insert Into 病人预交记录
          (ID, NO, 实际票号, 记录性质, 记录状态, 病人id, 主页id, 预交类别, 科室id, 金额, 结算方式, 结算号码, 摘要, 缴款单位, 单位开户行, 单位帐号, 收款时间, 操作员姓名, 操作员编号,
           冲预交, 结帐id, 缴款组id, 卡类别id, 结算卡序号, 卡号, 交易流水号, 交易说明, 合作单位, 结算序号)
          Select 病人预交记录_Id.Nextval, NO, 实际票号, 11, 记录状态, 病人id, 主页id, 预交类别, 科室id, Null, 结算方式, 结算号码, 摘要, 缴款单位, 单位开户行, 单位帐号,
                 收款时间, 操作员姓名, 操作员编号, n_当前金额, 结帐id_In, 缴款组id, 卡类别id, 结算卡序号, 卡号, 交易流水号, 交易说明, 合作单位, n_结算序号
          From 病人预交记录
          Where NO = c_预交.No And 记录状态 = c_预交.记录状态 And 记录性质 In (1, 11) And Rownum = 1;
      End If;
      --更新病人预交余额
      Update 病人余额
      Set 预交余额 = Nvl(预交余额, 0) - n_当前金额
      Where 病人id = v_病人id And 性质 = 1 And 类型 = Nvl(c_预交.预交类别, 2);
      --检查是否已经处理完
      If c_预交.金额 < n_预交金额 Then
        n_预交金额 := n_预交金额 - c_预交.金额;
      Else
        n_预交金额 := 0;
      End If;
    
      If n_预交金额 = 0 Then
        Exit;
      End If;
    End Loop;
    If n_预交金额 <> 0 Then
      v_Err_Msg := '[ZLSOFT]预交余不够支付本次支付金额,不能继续操作！[ZLSOFT]';
      Raise Err_Item;
    End If;
    Delete From 病人余额 Where 病人id = v_病人id And 性质 = 1 And Nvl(费用余额, 0) = 0 And Nvl(预交余额, 0) = 0;
    v_金额合计 := n_预交金额;
  End If;

  --剩余部份全部用缺省结算方式结算，(小于零也不进行额外处理)
  If v_金额合计 <> 0 Then
    Update 病人预交记录
    Set 冲预交 = 冲预交 + v_金额合计, 卡类别id = 卡类别id_In, 结算卡序号 = 结算卡序号_In, 卡号 = 卡号_In, 交易流水号 = 交易流水号_In, 交易说明 = 交易说明_In,
        合作单位 = 合作单位_In, 结算序号 = n_结算序号
    
    Where 结帐id = 结帐id_In And Nvl(结算方式, 'LXH_Test') = Nvl(v_缺省, 'LXH_Test') And 记录性质 = Decode(结帐_In, 1, 2, v_记录性质);
    If Sql%RowCount = 0 Then
      Insert Into 病人预交记录
        (ID, 记录性质, NO, 记录状态, 病人id, 主页id, 摘要, 结算方式, 收款时间, 操作员编号, 操作员姓名, 冲预交, 结帐id, 缴款组id, 结算序号, 卡类别id, 结算卡序号, 卡号, 交易流水号,
         交易说明, 合作单位)
      Values
        (病人预交记录_Id.Nextval, Decode(结帐_In, 1, 2, v_记录性质), v_No, 1, v_病人id, v_主页id, '保险结算修正', v_缺省, v_登记时间, v_操作员编号,
         v_操作员姓名, v_金额合计, 结帐id_In, n_组id, n_结算序号, 卡类别id_In, 结算卡序号_In, 卡号_In, 交易流水号_In, 交易说明_In, 合作单位_In);
    End If;
  
    --挂号结算,分币处理(由于挂号界面没有预结算,所以在此过程中根据分币处理规则来修正)
    If v_记录性质 = 4 Then
    
      Begin
        Select a.冲预交
        Into v_现金金额
        From 病人预交记录 A, 结算方式 B
        Where a.结算方式 = b.名称 And b.性质 = 1 And a.结帐id = 结帐id_In;
      Exception
        When Others Then
          v_现金金额 := 0;
      End;
      If Floor(Abs(v_现金金额) * 10) <> Abs(v_现金金额) * 10 Then
        v_Centmode := Substr(Nvl(zl_GetSysParameter(14), 0), 1, 1);
      
        v_Sign     := Sign(v_现金金额);
        v_现金金额 := Abs(v_现金金额);
        If v_Centmode = 1 Then
          v_Cashcented := Round(v_现金金额, 1); --1.四舍五入法,eg:0.51=0.50;0.56=0.60
        Elsif v_Centmode = 2 Then
          v_Cashcented := Ceil(v_现金金额 * 10) / 10; --2.补整收法,eg:0.51=0.60,0.56=0.60
        Elsif v_Centmode = 3 Then
          v_Cashcented := Floor(v_现金金额 * 10) / 10; --3.舍分收法,eg:0.51=0.50,0.56=0.50
          --4.四舍六入五成双,由于Oracle没有相关函数,算法复杂,暂不支持
        Elsif v_Centmode = 5 Then
          v_零钱 := v_现金金额 - Floor(v_现金金额);
          If v_零钱 >= 0.8 Then
            v_零钱 := 1;
          Elsif v_零钱 < 0.3 Then
            v_零钱 := 0;
          Else
            v_零钱 := 0.5;
          End If;
          v_Cashcented := Floor(v_现金金额) + v_零钱; --5.三七作五、二舍八入,eg:0.29=0,0.30=0.50,0.79=0.50,0.80=1.00
        Elsif v_Centmode = 6 Then
          --6:五舍六入
          v_Cashcented := Round(v_现金金额 - 0.01, 1); --6.五舍六入,eg:0.55=0.50,0.56=0.60
        Else
          v_Cashcented := v_现金金额;
        End If;
        v_Cashcented := v_Sign * v_Cashcented;
        v_现金金额   := v_Sign * v_现金金额;
      
        v_误差金额 := v_Cashcented - v_现金金额;
        If v_误差金额 <> 0 Then
          If n_结算序号 < 0 Then
            --10.34之后误差数据
            Begin
              Select 名称 Into v_误差项 From 结算方式 Where 性质 = 9;
            Exception
              When Others Then
                v_Err_Msg := '不能正确读取误差项的信息，请先检查结算方式中误差费是否设置正确。';
                Raise Err_Item;
            End;
            Insert Into 病人预交记录
              (ID, 记录性质, NO, 记录状态, 病人id, 主页id, 摘要, 结算方式, 收款时间, 操作员编号, 操作员姓名, 冲预交, 结帐id, 缴款组id, 结算序号)
            Values
              (病人预交记录_Id.Nextval, Decode(结帐_In, 1, 2, v_记录性质), v_No, 1, v_病人id, v_主页id, '误差费', v_误差项, v_登记时间, v_操作员编号,
               v_操作员姓名, v_误差金额, 结帐id_In, n_组id, n_结算序号);
          Else
            --1.更新预交记录(一定存在记录)
            Update 病人预交记录
            Set 冲预交 = v_Cashcented
            Where 结算方式 = (Select 名称 From 结算方式 Where 性质 = 1 And Rownum = 1) And 结帐id = 结帐id_In;
          
            --2.生成误差费用记录(注:计算单位记录的是号别,所以不取误差项的)
            Begin
              Select a.类别, a.Id, c.Id, c.收据费目
              Into v_收费类别, v_收费细目id, v_收入项目id, v_收据费目
              From 收费项目目录 A, 收费价目 B, 收入项目 C, 收费特定项目 D
              Where d.特定项目 = '误差项' And d.收费细目id = a.Id And a.Id = b.收费细目id And b.收入项目id = c.Id And
                    Sysdate Between b.执行日期 And Nvl(b.终止日期, To_Date('3000-01-01', 'YYYY-MM-DD'));
            Exception
              When Others Then
                v_Err_Msg := '不能正确读取收费误差项的信息，请先检查该项目是否设置正确。';
                Raise Err_Item;
            End;
            If Nvl(结帐_In, 0) = 1 Then
              Select 病人费用记录_Id.Nextval Into v_费用id From Dual;
              Select Max(序号) + 1 Into v_序号 From 住院费用记录 Where 结帐id = 结帐id_In;
              n_医疗小组id := Zl_医疗小组_Get(0, v_操作员姓名, v_病人id, v_主页id);
            
              Insert Into 住院费用记录
                (ID, 记录性质, NO, 实际票号, 记录状态, 序号, 从属父号, 价格父号, 门诊标志, 病人id, 标识号, 床号, 姓名, 性别, 年龄, 病人病区id, 病人科室id, 费别, 收费类别,
                 收费细目id, 计算单位, 发药窗口, 付数, 数次, 加班标志, 附加标志, 收入项目id, 收据费目, 标准单价, 应收金额, 实收金额, 记帐费用, 划价人, 开单部门id, 开单人, 发生时间,
                 登记时间, 执行部门id, 执行人, 执行状态, 结帐id, 结帐金额, 操作员编号, 操作员姓名, 是否上传, 缴款组id, 医疗小组id)
                Select v_费用id, 记录性质, NO, 实际票号, 记录状态, v_序号, Null, Null, 门诊标志, 病人id, 标识号, 床号, 姓名, 性别, 年龄, 病人病区id, 病人科室id,
                       费别, v_收费类别, v_收费细目id, 计算单位, 发药窗口, 1, 1, 加班标志, 9, v_收入项目id, v_收据费目, v_误差金额, v_误差金额, v_误差金额, 记帐费用,
                       划价人, 开单部门id, 开单人, 发生时间, 登记时间, 执行部门id, 执行人, 执行状态, 结帐id_In, v_误差金额, 操作员编号, 操作员姓名, 1, 缴款组id,
                       Decode(n_医疗小组id, Null, 医疗小组id, n_医疗小组id)
                From 住院费用记录
                Where 结帐id = 结帐id_In And Rownum = 1;
            Else
              Select 病人费用记录_Id.Nextval Into v_费用id From Dual;
              Select Max(序号) + 1 Into v_序号 From 门诊费用记录 Where 结帐id = 结帐id_In;
              Insert Into 门诊费用记录
                (ID, 记录性质, NO, 实际票号, 记录状态, 序号, 从属父号, 价格父号, 门诊标志, 病人id, 标识号, 付款方式, 姓名, 性别, 年龄, 病人科室id, 费别, 收费类别, 收费细目id,
                 计算单位, 发药窗口, 付数, 数次, 加班标志, 附加标志, 收入项目id, 收据费目, 标准单价, 应收金额, 实收金额, 记帐费用, 划价人, 开单部门id, 开单人, 发生时间, 登记时间,
                 执行部门id, 执行人, 执行状态, 费用状态, 结帐id, 结帐金额, 操作员编号, 操作员姓名, 是否上传, 缴款组id)
                Select v_费用id, 记录性质, NO, 实际票号, 记录状态, v_序号, Null, Null, 门诊标志, 病人id, 标识号, 付款方式, 姓名, 性别, 年龄, 病人科室id, 费别,
                       v_收费类别, v_收费细目id, 计算单位, 发药窗口, 1, 1, 加班标志, 9, v_收入项目id, v_收据费目, v_误差金额, v_误差金额, v_误差金额, 记帐费用, 划价人,
                       开单部门id, 开单人, 发生时间, 登记时间, 执行部门id, 执行人, 执行状态, 费用状态, 结帐id_In, v_误差金额, 操作员编号, 操作员姓名, 1, 缴款组id
                From 门诊费用记录
                Where 结帐id = 结帐id_In And Rownum = 1;
            End If;
          End If;
          --3.更新汇总表
          --只可能产生误差金额的变化.仅为了变量处理方便而用游标
        End If;
      End If;
    End If;
  End If;

  --最后再处理"人员缴款余额"(没有动冲预交那部分,所以"病人余额"的预交余额不用更新)
  For r_Del In c_Del Loop
    If r_Del.记录性质 Not In (1, 11) Then
      If Nvl(n_费用状态, 0) <> 1 Then
        Update 人员缴款余额
        Set 余额 = Nvl(余额, 0) + r_Del.冲预交
        Where 收款员 = v_操作员姓名 And 性质 = 1 And 结算方式 = r_Del.结算方式;
        If Sql%RowCount = 0 Then
          Insert Into 人员缴款余额
            (收款员, 结算方式, 性质, 余额)
          Values
            (v_操作员姓名, r_Del.结算方式, 1, r_Del.冲预交);
        End If;
      End If;
    End If;
  End Loop;
  Delete From 人员缴款余额 Where 性质 = 1 And 收款员 = v_操作员姓名 And Nvl(余额, 0) = 0;
Exception
  When Err_Item Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Err_Msg || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_病人结算记录_Update;
/

--68201:刘尔旋,2014-10-23,门诊转住院修正
Create Or Replace Procedure Zl_门诊转住院结帐_结帐作废
(
  No_In         病人结帐记录.NO%Type,
  操作员编号_In 病人结帐记录.操作员编号%Type,
  操作员姓名_In 病人结帐记录.操作员姓名%Type,
  作废日期_In   病人结帐记录.收费时间%Type
) As
  --该游标包含误差项目的相关信息
  Cursor c_Erritem Is
    Select A.类别 As 收费类别, A.ID As 收费细目id, A.计算单位, C.ID As 收入项目id, C.收据费目
    From 收费细目 A, 收费价目 B, 收入项目 C, 收费特定项目 D
    Where D.特定项目 = '误差项' And D.收费细目id = A.ID And A.ID = B.收费细目id And B.收入项目id = C.ID And
          ((Sysdate Between B.执行日期 And B.终止日期) Or (Sysdate >= B.执行日期 And B.终止日期 Is Null));
  r_Erritem c_Erritem%Rowtype;

  --该游标包含病人的相关信息
  Cursor c_Pati(n_病人id 病人信息.病人id%Type) Is
    Select A.姓名, A.性别, A.年龄, A.住院号, A.门诊号, B.主页id, B.出院病床, B.当前病区id, B.出院科室id,
           Nvl(B.费别, A.费别) As 费别, A.险类, C.编码 As 付款方式
    From 病人信息 A, 病案主页 B, 医疗付款方式 C
    Where A.病人id = n_病人id And A.病人id = B.病人id(+) And Nvl(A.主页ID, 0) = B.主页id(+) And
          A.医疗付款方式 = C.名称(+);
  r_Pati c_Pati%Rowtype;

  Err_Item Exception;
  v_Err_Msg   Varchar2(255);
  v_Temp      Varchar2(255);
  v_实际票号  病人预交记录.实际票号%Type;
  v_误差no    住院费用记录.NO%Type;
  v_预交no    病人预交记录.NO%Type;
  v_New误差no 住院费用记录.NO%Type;

  n_病人id     病人信息.病人id%Type;
  n_人员部门id 部门人员.部门id%Type;

  n_原结帐id 病人结帐记录.ID%Type;
  n_结帐id   病人结帐记录.ID%Type;
  n_打印id   票据打印内容.ID%Type;

  n_医疗小组id 住院费用记录.医疗小组id%Type;
  n_来源       Number; --1-门诊;2-住院;3-门诊和住院
  n_返回值     病人余额.预交余额%Type;
  n_组id       财务缴款分组.ID%Type;
  v_现金结算   结算方式.名称%Type;
  n_冲预交     病人预交记录.冲预交%Type;
  n_Temp       病人预交记录.冲预交%Type;
  n_误差金额   病人预交记录.冲预交%Type;
  n_原主页id   病人预交记录.主页id%Type;
  n_类别       Number;
Begin
  n_组id := Zl_Get组id(操作员姓名_In);
  Begin
    Select 名称 Into v_现金结算 From 结算方式 Where 性质 = 1 And Rownum = 1;
  Exception
    When Others Then
      v_现金结算 := '现金';
  End;

  Begin
    Select ID, 病人id, 实际票号
    Into n_原结帐id, n_病人id, v_实际票号
    From 病人结帐记录
    Where 记录状态 = 1 And NO = No_In;
    --最后一次打印的内容
    Select Max(ID) Into n_打印id From 票据打印内容 Where 数据性质 = 3 And NO = No_In;
  Exception
    When Others Then
      Begin
        v_Err_Msg := '没有发现要作废的结帐单据,可能已经作废！';
        Raise Err_Item;
      End;
  End;

  Open c_Pati(n_病人id);
  Fetch c_Pati
    Into r_Pati; --体检系统调用此过程,团体结帐时没有病人信息

  Select 病人结帐记录_Id.Nextval Into n_结帐id From Dual;

  --病人结帐记录
  Insert Into 病人结帐记录
    (ID, NO, 实际票号, 记录状态, 中途结帐, 病人id, 操作员编号, 操作员姓名, 开始日期, 结束日期, 收费时间, 备注, 原因,
     缴款组id, 结帐类型)
    Select n_结帐id, NO, 实际票号, 2, 中途结帐, 病人id, 操作员编号_In, 操作员姓名_In, 开始日期, 结束日期, 作废日期_In,
           备注, 原因, n_组id, 结帐类型
    From 病人结帐记录
    Where ID = n_原结帐id;

  Update 病人结帐记录 Set 记录状态 = 3 Where ID = n_原结帐id;

  --作废收回票据(可能以前没有使用票据,无法收回)
  If n_打印id Is Not Null Then
    Insert Into 票据使用明细
      (ID, 票种, 号码, 性质, 原因, 领用id, 打印id, 使用时间, 使用人)
      Select 票据使用明细_Id.Nextval, 票种, 号码, 2, 2, 领用id, 打印id, 作废日期_In, 操作员姓名_In
      From 票据使用明细
      Where 打印id = n_打印id And 票种 = 3 And 性质 = 1;
  End If;
  -------------------------------------------------------------------------------------------------------------
  --冲预交处理
  Select Nvl(Sum(冲预交), 0) Into n_冲预交 From 病人预交记录 A Where Mod(A.记录性质, 10) = 1 And A.结帐id = n_原结帐id;

  n_Temp := Zl_Cent_Money(n_冲预交, 3);
  If n_Temp <> n_冲预交 Then
    --算误差费
    n_误差金额 := n_Temp - n_冲预交;
    n_冲预交   := n_Temp;
  End If;
  --先处理医保结算方式

  For v_预交 In (Select A.ID, A.记录性质, A.结算方式, 冲预交, B.性质, A.主页id
                 From 病人预交记录 A, 结算方式 B
                 Where A.结帐id = n_原结帐id And Mod(A.记录性质, 10) = 2 And A.结算方式 = B.名称(+)) Loop
    If v_预交.性质 >= 3 Then
      Insert Into 病人预交记录
        (ID, NO, 实际票号, 记录性质, 记录状态, 病人id, 主页id, 科室id, 金额, 结算方式, 结算号码, 摘要, 缴款单位,
         单位开户行, 单位帐号, 收款时间, 操作员姓名, 操作员编号, 冲预交, 结帐id, 缴款组id, 预交类别, 卡类别id,
         结算卡序号, 卡号, 交易流水号, 交易说明, 合作单位)
        Select 病人预交记录_Id.Nextval, NO, 实际票号, To_Number('1' || Substr(记录性质, Length(记录性质), 1)), 记录状态,
               病人id, 主页id, 科室id, Null, 结算方式, 结算号码, 摘要, 缴款单位, 单位开户行, 单位帐号, 收款时间,
               操作员姓名, 操作员编号, -1 * 冲预交, n_结帐id, 缴款组id, 预交类别, 卡类别id, 结算卡序号, 卡号, 交易流水号,
               交易说明, 合作单位
        From 病人预交记录
        Where ID = v_预交.ID;
    Else
      n_冲预交 := Nvl(n_冲预交, 0) + Nvl(v_预交.冲预交, 0);
    End If;
    n_原主页id := Nvl(v_预交.主页id, 0);
  End Loop;
  If Nvl(n_原主页id, 0) = 0 Then
    n_原主页id := Null;
  End If;
  n_冲预交 := Nvl(n_冲预交, 0);
  If n_冲预交 <> 0 Then
    --增加"现金冲回"
    Insert Into 病人预交记录
      (ID, NO, 实际票号, 记录性质, 记录状态, 病人id, 主页id, 科室id, 金额, 结算方式, 结算号码, 摘要, 缴款单位,
       单位开户行, 单位帐号, 收款时间, 操作员姓名, 操作员编号, 冲预交, 结帐id, 缴款组id)
    Values
      (病人预交记录_Id.Nextval, No_In, v_实际票号, 12, 1, n_病人id, n_原主页id, r_Pati.出院科室id, Null, v_现金结算,
       Null, '结帐作废退款', Null, Null, Null, 作废日期_In, 操作员姓名_In, 操作员编号_In, -1 * n_冲预交, n_结帐id,
       n_组id);

    --增加:预交款(现金)
    v_预交no := Nextno(11);
    Insert Into 病人预交记录
      (ID, NO, 实际票号, 记录性质, 记录状态, 病人id, 主页id, 科室id, 金额, 结算方式, 结算号码, 收款时间, 缴款单位,
       单位开户行, 单位帐号, 操作员编号, 操作员姓名, 摘要, 缴款组id,预交类别)
    Values
      (病人预交记录_Id.Nextval, v_预交no, Null, 1, 1, n_病人id, r_Pati.主页id, r_Pati.出院科室id, n_冲预交, v_现金结算,
       Null, 作废日期_In, Null, Null, Null, 操作员编号_In, 操作员姓名_In, '门诊转住院预交', n_组id,'2');

    Update 病人余额
    Set 预交余额 = Nvl(预交余额, 0) + n_冲预交 --注:新的结帐ID产生的是负数金额
    Where 病人id = n_病人id And 性质 = 1 And 类型 = 2 Returning 预交余额 Into n_返回值;

    If Sql%Rowcount = 0 Then
      Insert Into 病人余额 (病人id, 性质, 类型, 预交余额, 费用余额) Values (n_病人id, 2, 1, n_冲预交, 0);
    End If;

    Update 人员缴款余额
    Set 余额 = Nvl(余额, 0) + n_冲预交
    Where 收款员 = 操作员姓名_In And 结算方式 = v_现金结算 And 性质 = 1 Returning 余额 Into n_返回值;

    If Sql%Rowcount = 0 Then
      Insert Into 人员缴款余额 (收款员, 结算方式, 性质, 余额) Values (操作员姓名_In, v_现金结算, 1, n_冲预交);
      n_返回值 := -1 * n_冲预交;
    End If;
    If Nvl(n_返回值, 0) = 0 Then
      Delete From 人员缴款余额
      Where 收款员 = 操作员姓名_In And 结算方式 = v_现金结算 And 性质 = 1 And Nvl(余额, 0) = 0;
    End If;
  End If;
  --住院费用记录
  --读取原结帐时产生的误差费用,并销帐,然后将销帐记录处理在本次结帐作废中
  Begin
    Select NO, 医疗小组id
    Into v_误差no, n_医疗小组id
    From 住院费用记录
    Where 结帐id = n_原结帐id And Nvl(附加标志, 0) = 9 And 记录性质 = 2 And 记录状态 = 1;
    n_来源 := 2;
  Exception
    When Others Then
      Null;
  End;

  If v_误差no Is Null Then
    Begin
      Select NO
      Into v_误差no
      From 门诊费用记录
      Where 结帐id = n_原结帐id And Nvl(附加标志, 0) = 9 And 记录性质 = 2 And 记录状态 = 1;
    Exception
      When Others Then
        Null;
    End;
    n_来源 := 1;
  End If;

  --a.如果结帐时有误差,则在原误差单据上处理
  If v_误差no Is Not Null Then
    --在原有误差基础上产生新误差费用并结帐
    If n_来源 = 1 Then
      Insert Into 门诊费用记录
        (ID, NO, 实际票号, 记录性质, 记录状态, 序号, 从属父号, 价格父号, 病人id, 医嘱序号, 门诊标志, 姓名, 性别, 年龄,
         标识号, 付款方式, 病人科室id, 费别, 收费类别, 收费细目id, 计算单位, 付数, 发药窗口, 数次, 加班标志, 附加标志,
         婴儿费, 记帐费用, 收入项目id, 收据费目, 标准单价, 应收金额, 实收金额, 划价人, 开单部门id, 开单人, 发生时间,
         登记时间, 执行部门id, 执行状态, 操作员姓名, 操作员编号, 结帐金额, 结帐id, 是否上传, 缴款组id)
        Select 病人费用记录_Id.Nextval, NO, Null, 记录性质, 2, 1, 从属父号, 价格父号, 病人id, 医嘱序号, 门诊标志, 姓名,
               性别, 年龄, 标识号, 付款方式, 病人科室id, 费别, 收费类别, 收费细目id, 计算单位, 付数, 发药窗口, -1 * 数次,
               加班标志, 附加标志, 婴儿费, 记帐费用, 收入项目id, 收据费目,
               Decode(n_误差金额, 0, 应收金额, 应收金额 + n_误差金额),
               Decode(n_误差金额, 0, -1 * 应收金额, -1 * (应收金额 + n_误差金额)),
               Decode(n_误差金额, 0, -1 * 实收金额, -1 * (实收金额 + n_误差金额)), 划价人, 开单部门id, 开单人, 发生时间,
               登记时间, 执行部门id, -1, 操作员姓名, 操作员编号,
               Decode(n_误差金额, 0, -1 * 实收金额, -1 * (实收金额 + n_误差金额)), n_结帐id,
               Decode(r_Pati.险类, Null, Null, 1), 缴款组id
        From 门诊费用记录
        Where 结帐id = n_原结帐id And Nvl(附加标志, 0) = 9 And 记录性质 = 2 And 记录状态 = 1;

      --作废结帐时的误差记录
      Update 门诊费用记录
      Set 记录状态 = 3, 执行状态 = 0
      Where 结帐id = n_原结帐id And Nvl(附加标志, 0) = 9 And 记录性质 = 2 And 记录状态 = 1;

    Else
      Insert Into 住院费用记录
        (ID, NO, 实际票号, 记录性质, 记录状态, 序号, 从属父号, 价格父号, 多病人单, 病人id, 主页id, 医嘱序号, 门诊标志,
         姓名, 性别, 年龄, 标识号, 床号, 病人病区id, 病人科室id, 费别, 收费类别, 收费细目id, 计算单位, 付数, 发药窗口,
         数次, 加班标志, 附加标志, 婴儿费, 记帐费用, 收入项目id, 收据费目, 标准单价, 应收金额, 实收金额, 划价人,
         开单部门id, 开单人, 发生时间, 登记时间, 执行部门id, 执行状态, 操作员姓名, 操作员编号, 结帐金额, 结帐id,
         是否上传, 缴款组id, 医疗小组id)
        Select 病人费用记录_Id.Nextval, NO, Null, 记录性质, 2, 1, 从属父号, 价格父号, 多病人单, 病人id, 主页id, 医嘱序号,
               门诊标志, 姓名, 性别, 年龄, 标识号, 床号, 病人病区id, 病人科室id, 费别, 收费类别, 收费细目id, 计算单位,
               付数, 发药窗口, -1 * 数次, 加班标志, 附加标志, 婴儿费, 记帐费用, 收入项目id, 收据费目,
               Decode(n_误差金额, 0, 应收金额, 应收金额 + n_误差金额),
               Decode(n_误差金额, 0, -1 * 应收金额, -1 * (应收金额 + n_误差金额)),
               Decode(n_误差金额, 0, -1 * 实收金额, -1 * (实收金额 + n_误差金额)), 划价人, 开单部门id, 开单人, 发生时间,
               登记时间, 执行部门id, -1, 操作员姓名, 操作员编号,
               Decode(n_误差金额, 0, -1 * 实收金额, -1 * (实收金额 + n_误差金额)), n_结帐id,
               Decode(r_Pati.险类, Null, Null, 1), 缴款组id, 医疗小组id
        From 住院费用记录
        Where 结帐id = n_原结帐id And Nvl(附加标志, 0) = 9 And 记录性质 = 2 And 记录状态 = 1;

      --作废结帐时的误差记录
      Update 住院费用记录
      Set 记录状态 = 3, 执行状态 = 0
      Where 结帐id = n_原结帐id And Nvl(附加标志, 0) = 9 And 记录性质 = 2 And 记录状态 = 1;
    End If;

    Begin
      Select Case
               When Nvl(Max(住院), 0) = 1 And Nvl(Max(门诊), 0) = 1 Then
                3
               When Nvl(Max(住院), 0) = 1 Then
                2
               Else
                1
             End
      Into n_来源
      From (Select 1 As 住院, 0 As 门诊
             From 住院费用记录
             Where 结帐id = n_原结帐id And Rownum = 1
             Union All
             Select 0 As 住院, 1 As 门诊 From 门诊费用记录 Where 结帐id = n_原结帐id And Rownum = 1);

    Exception
      When Others Then
        n_来源 := 3;
    End;

  Else
    Begin
      Select Case
               When Nvl(Max(住院), 0) = 1 And Nvl(Max(门诊), 0) = 1 Then
                3
               When Nvl(Max(住院), 0) = 1 Then
                2
               Else
                1
             End
      Into n_来源
      From (Select 1 As 住院, 0 As 门诊
             From 住院费用记录
             Where 结帐id = n_原结帐id And Rownum = 1
             Union All
             Select 0 As 住院, 1 As 门诊 From 门诊费用记录 Where 结帐id = n_原结帐id And Rownum = 1);
    Exception
      When Others Then
        n_来源 := 3;
    End;

    --b.作废时新产生的误差
    If n_误差金额 <> 0 Then
      v_Temp       := Zl_Identity;
      n_人员部门id := To_Number(Substr(v_Temp, 1, Instr(v_Temp, ',') - 1));

      Open c_Erritem;
      Fetch c_Erritem
        Into r_Erritem;
      If c_Erritem%Rowcount = 0 Then
        Close c_Erritem;
        v_Err_Msg := '不能正确读取处理费用误差的项目信息，请先检查该项目是否正确设置。';
        Raise Err_Item;
      End If;

      If Nvl(n_来源, 1) = 1 Then
        --产生误差单据并结帐此误差费用
        v_New误差no := Nextno(14);

        Insert Into 门诊费用记录
          (ID, NO, 实际票号, 记录性质, 记录状态, 序号, 从属父号, 价格父号, 病人id, 医嘱序号, 门诊标志, 姓名, 性别, 年龄,
           标识号, 付款方式, 病人科室id, 费别, 收费类别, 收费细目id, 计算单位, 付数, 发药窗口, 数次, 加班标志, 附加标志,
           婴儿费, 记帐费用, 收入项目id, 收据费目, 标准单价, 应收金额, 实收金额, 划价人, 开单部门id, 开单人, 发生时间,
           登记时间, 执行部门id, 执行状态, 操作员姓名, 操作员编号, 结帐金额, 结帐id, 是否上传, 记帐单id, 保险项目否,
           缴款组id)
        Values
          (病人费用记录_Id.Nextval, v_New误差no, Null, 2, 1, 1, Null, Null, n_病人id, Null, 1, r_Pati.姓名, r_Pati.性别,
           r_Pati.年龄, Decode(r_Pati.主页id, Null, r_Pati.门诊号, r_Pati.住院号), r_Pati.付款方式,
           Nvl(r_Pati.出院科室id, n_人员部门id), r_Pati.费别, r_Erritem.收费类别, r_Erritem.收费细目id,
           r_Erritem.计算单位, 1, Null, 1, Null, 9, 0, 1, r_Erritem.收入项目id, r_Erritem.收据费目, -1 * n_误差金额,
           -1 * n_误差金额, -1 * n_误差金额, Null, n_人员部门id, 操作员姓名_In, 作废日期_In, 作废日期_In, n_人员部门id,
           0, 操作员姓名_In, 操作员编号_In, -1 * n_误差金额, n_结帐id, Decode(r_Pati.险类, Null, Null, 1), Null, 0,
           n_组id);

      Else
        n_医疗小组id := Zl_医疗小组_Get(n_人员部门id, 操作员姓名_In, n_病人id, r_Pati.主页id);
        v_New误差no  := Nextno(14);

        --产生误差单据并结帐此误差费用
        Insert Into 住院费用记录
          (ID, NO, 实际票号, 记录性质, 记录状态, 序号, 从属父号, 价格父号, 多病人单, 病人id, 主页id, 医嘱序号, 门诊标志,
           姓名, 性别, 年龄, 标识号, 床号, 病人病区id, 病人科室id, 费别, 收费类别, 收费细目id, 计算单位, 付数, 发药窗口,
           数次, 加班标志, 附加标志, 婴儿费, 记帐费用, 收入项目id, 收据费目, 标准单价, 应收金额, 实收金额, 划价人,
           开单部门id, 开单人, 发生时间, 登记时间, 执行部门id, 执行状态, 操作员姓名, 操作员编号, 结帐金额, 结帐id,
           是否上传, 记帐单id, 保险项目否, 缴款组id, 医疗小组id)
        Values
          (病人费用记录_Id.Nextval, v_New误差no, Null, 2, 1, 1, Null, Null, 0, n_病人id, r_Pati.主页id, Null,
           Decode(r_Pati.主页id, Null, 1, 2), r_Pati.姓名, r_Pati.性别, r_Pati.年龄,
           Decode(r_Pati.主页id, Null, r_Pati.门诊号, r_Pati.住院号), r_Pati.出院病床,
           Nvl(r_Pati.当前病区id, n_人员部门id), Nvl(r_Pati.出院科室id, n_人员部门id), r_Pati.费别, r_Erritem.收费类别,
           r_Erritem.收费细目id, r_Erritem.计算单位, 1, Null, 1, Null, 9, 0, 1, r_Erritem.收入项目id, r_Erritem.收据费目,
           -1 * n_误差金额, -1 * n_误差金额, -1 * n_误差金额, Null, n_人员部门id, 操作员姓名_In, 作废日期_In,
           作废日期_In, n_人员部门id, 0, 操作员姓名_In, 操作员编号_In, -1 * n_误差金额, n_结帐id,
           Decode(r_Pati.险类, Null, Null, 1), Null, 0, n_组id, n_医疗小组id);

      End If;
      v_误差no := v_New误差no; --用来在后面排开误差的汇总处理
      Close c_Erritem;
    End If;
  End If;

  If n_来源 = 2 Or n_来源 = 3 Then
    --作废结帐对应的费用记录:不包含原始结帐产生的误差项目
    Insert Into 住院费用记录
      (ID, NO, 实际票号, 记录性质, 记录状态, 序号, 从属父号, 价格父号, 多病人单, 记帐单id, 病人id, 主页id, 医嘱序号,
       门诊标志, 姓名, 性别, 年龄, 标识号, 床号, 病人病区id, 病人科室id, 费别, 收费类别, 收费细目id, 计算单位, 付数,
       发药窗口, 数次, 加班标志, 附加标志, 婴儿费, 记帐费用, 收入项目id, 收据费目, 标准单价, 应收金额, 实收金额, 划价人,
       开单部门id, 开单人, 发生时间, 登记时间, 执行部门id, 执行状态, 执行人, 执行时间, 操作员姓名, 操作员编号, 结帐金额,
       结帐id, 保险项目否, 保险大类id, 统筹金额, 是否急诊, 保险编码, 费用类型, 摘要, 缴款组id, 医疗小组id)
      Select 病人费用记录_Id.Nextval, NO, 实际票号, To_Number('1' || Substr(记录性质, Length(记录性质), 1)), 记录状态,
             序号, 从属父号, 价格父号, 多病人单, 记帐单id, 病人id, 主页id, 医嘱序号, 门诊标志, 姓名, 性别, 年龄, 标识号,
             床号, 病人病区id, 病人科室id, 费别, 收费类别, 收费细目id, 计算单位, 付数, 发药窗口, 数次, 加班标志,
             附加标志, 婴儿费, 记帐费用, 收入项目id, 收据费目, 标准单价, Null, Null, 划价人, 开单部门id, 开单人,
             发生时间, 登记时间, 执行部门id, 执行状态, 执行人, 执行时间, 操作员姓名, 操作员编号, -1 * 结帐金额, n_结帐id,
             保险项目否, 保险大类id, 统筹金额, 是否急诊, 保险编码, 费用类型, 摘要, 缴款组id, 医疗小组id
      From 住院费用记录
      Where 结帐id = n_原结帐id And Nvl(附加标志, 0) <> 9;
  End If;
  If n_来源 = 1 Or n_来源 = 3 Then
    Insert Into 门诊费用记录
      (ID, NO, 实际票号, 记录性质, 记录状态, 序号, 从属父号, 价格父号, 记帐单id, 病人id, 医嘱序号, 门诊标志, 姓名, 性别,
       年龄, 标识号, 付款方式, 病人科室id, 费别, 收费类别, 收费细目id, 计算单位, 付数, 发药窗口, 数次, 加班标志,
       附加标志, 婴儿费, 记帐费用, 收入项目id, 收据费目, 标准单价, 应收金额, 实收金额, 划价人, 开单部门id, 开单人,
       发生时间, 登记时间, 执行部门id, 执行状态, 执行人, 执行时间, 操作员姓名, 操作员编号, 结帐金额, 结帐id, 保险项目否,
       保险大类id, 统筹金额, 是否急诊, 保险编码, 费用类型, 摘要, 缴款组id)
      Select 病人费用记录_Id.Nextval, NO, 实际票号, To_Number('1' || Substr(记录性质, Length(记录性质), 1)), 记录状态,
             序号, 从属父号, 价格父号, 记帐单id, 病人id, 医嘱序号, 门诊标志, 姓名, 性别, 年龄, 标识号, 付款方式,
             病人科室id, 费别, 收费类别, 收费细目id, 计算单位, 付数, 发药窗口, 数次, 加班标志, 附加标志, 婴儿费,
             记帐费用, 收入项目id, 收据费目, 标准单价, Null, Null, 划价人, 开单部门id, 开单人, 发生时间, 登记时间,
             执行部门id, 执行状态, 执行人, 执行时间, 操作员姓名, 操作员编号, -1 * 结帐金额, n_结帐id, 保险项目否,
             保险大类id, 统筹金额, 是否急诊, 保险编码, 费用类型, 摘要, 缴款组id
      From 门诊费用记录
      Where 结帐id = n_原结帐id And Nvl(附加标志, 0) <> 9;

  End If;

  --相关汇总表处理
  For v_预交 In (Select 病人id, 记录性质, Nvl(预交类别, 2) As 预交类型, 结算方式, 冲预交
                 From 病人预交记录
                 Where 结帐id = n_结帐id) Loop
    If v_预交.记录性质 In (1, 11) Then
      --病人余额(预交)
      Update 病人余额
      Set 预交余额 = Nvl(预交余额, 0) - v_预交.冲预交 --注:新的结帐ID产生的是负数金额
      Where 病人id = v_预交.病人id And 性质 = 1 And 类型 = Nvl(v_预交.预交类型, 2) Returning 预交余额 Into n_返回值;

      If Sql%Rowcount = 0 Then
        Insert Into 病人余额
          (病人id, 性质, 类型, 预交余额, 费用余额)
        Values
          (v_预交.病人id, 1, Nvl(v_预交.预交类型, 2), -1 * v_预交.冲预交, 0);
        n_返回值 := -1 * v_预交.冲预交;
      End If;

      If Nvl(n_返回值, 0) = 0 Then
        Delete 病人余额 Where 性质 = 1 And 病人id = v_预交.病人id And Nvl(预交余额, 0) = 0 And Nvl(费用余额, 0) = 0;
      End If;

    Else
      --人员缴款余额,医保不支持作废的结算方式在新的预交结算中已被处理为了退现金,
      --此处用加,表示收回退给病人的现金(结帐时,退款是负,作废时是正)
      Update 人员缴款余额
      Set 余额 = Nvl(余额, 0) + v_预交.冲预交
      Where 收款员 = 操作员姓名_In And 结算方式 = v_预交.结算方式 And 性质 = 1 Returning 余额 Into n_返回值;

      If Sql%Rowcount = 0 Then
        Insert Into 人员缴款余额
          (收款员, 结算方式, 性质, 余额)
        Values
          (操作员姓名_In, v_预交.结算方式, 1, v_预交.冲预交);
        n_返回值 := -1 * v_预交.冲预交;
      End If;
      If Nvl(n_返回值, 0) = 0 Then
        Delete From 人员缴款余额
        Where 收款员 = 操作员姓名_In And 结算方式 = v_预交.结算方式 And 性质 = 1 And Nvl(余额, 0) = 0;
      End If;
    End If;
  End Loop;
  Close c_Pati;
Exception
  When Err_Item Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Err_Msg || '[ZLSOFT]');
  When Others Then
    Zl_Errorcenter(Sqlcode, Sqlerrm);
End Zl_门诊转住院结帐_结帐作废;
/

--78413:胡俊勇,2014-11-18,医嘱清单打印
Create Or Replace Procedure Zl_病人医嘱打印_Insert
(
  病人id_In     病人医嘱记录.病人id%Type,
  主页id_In     病人医嘱记录.主页id%Type,
  婴儿_In       病人医嘱记录.婴儿%Type,
  期效_In       病人医嘱记录.医嘱期效%Type,
  序号_In       病人医嘱记录.序号%Type,
  页号_In       病人医嘱打印.页号%Type,
  行号_In       病人医嘱打印.行号%Type,
  打印模式_In   Number,
  打给药方式_In Number,
  位置_In       Number,
  重整标记_In   Number,
  转科_In       Number,
  行数_In       Number,
  重整日期_In   病案主页.医嘱重整时间%Type := Null
  --功能：将病人没有打印过的医嘱插入 病人医嘱打印 中，页号，行号同时产生，默认一条医嘱只占用一行。
  --参数：行数_In：报表医嘱单一页可以打多少行,打印模式_In：0-校对后打印，1-新开时打印。
  --      序号_In： 病人医嘱打印 表中的最后一条医嘱 页号_In，行号_In 是最后一条记录对应的页号和行号
  --      打给药方式_In：西药中成药的给药方法是否单独打一行，1－单独打一行，0－不打给药方式，
  --      位置_In：转科、出院、死亡医嘱打印位置，0-长期医嘱单上，1-临时医嘱单上，2-两者都打印。
  --      重整标记_In：是否打重整医嘱字样，1－要打，0－不打。转科_n：前一条打印记录是不是转科医嘱，用于判断是否换页打重开字样
  --      行数_In医嘱单报表的行数通常是28行。
) Is
  v_Max_Date Date;
  n_换页打   Number;
  n_打重开   Number;
  n_转科     Number;
  n_页号     Number;
  n_行号     Number;
Begin
  n_页号 := 页号_In;
  n_行号 := 行号_In;
  n_转科 := 转科_In;

  If 期效_In = 0 Then
    n_换页打 := Zl_To_Number(zl_GetSysParameter('重整和术后医嘱换页打印', 1254));
    n_打重开 := Zl_To_Number(zl_GetSysParameter('转科换页后在首行打印重开医嘱', 1254));
  End If;

  If 重整日期_In Is Null Then
    v_Max_Date := To_Date('1900-01-01', 'YYYY-MM-DD');
  Else
    v_Max_Date := 重整日期_In;
  End If;

  --插入重整医嘱，只插入一条
  If 重整标记_In = 1 Then
    --生成行号和页号
    If n_行号 = 行数_In Then
      n_行号 := 1;
      n_页号 := n_页号 + 1;
    Else
      n_行号 := n_行号 + 1;
