--78413:胡俊勇,2014-11-12,医嘱清单打印
CREATE OR REPLACE Procedure Zl_病人医嘱打印_Update
(
  医嘱id_In     病人医嘱打印.医嘱id%Type,
  页号_In       病人医嘱打印.页号%Type,
  行号_In       病人医嘱打印.行号%Type,
  病人id_In     病人医嘱打印.病人id%Type := Null,
  主页id_In     病人医嘱打印.主页id%Type := Null,
  婴儿_In       病人医嘱打印.婴儿%Type := Null,
  期效_In       病人医嘱打印.期效%Type := Null,
  时间_In       病人医嘱打印.打印时间%Type := Null,
  操作员姓名_In 人员表.姓名%Type := Null,
  打印模式_In   Number := 0, --0-校对后打印，1-新开后打印,
  标记_In       病人医嘱打印.打印标记%Type := Null
) Is
  ----标记_In 停嘱打印时传入，否则认为是常规打印
  v_标记     病人医嘱打印.打印标记%Type;
  v_Temp     Varchar2(255);
  v_人员姓名 病人医嘱打印.打印人%Type;
Begin
  If 标记_In <> 0 Then
    If 医嘱id_In Is Not Null Then
      Select Decode(确认停嘱时间, Null, Decode(执行终止时间, Null, 0, 1), 2)
      Into v_标记
      From 病人医嘱记录
      Where ID = 医嘱id_In;
    End If;
    Update 病人医嘱打印 Set 打印标记 = v_标记 Where 医嘱id = 医嘱id_In And 页号 = 页号_In And 行号 = 行号_In;
  Else
    --当前操作人员
    If 操作员姓名_In Is Not Null Then
      v_人员姓名 := 操作员姓名_In;
    Else
      v_Temp     := Zl_Identity;
      v_Temp     := Substr(v_Temp, Instr(v_Temp, ';') + 1);
      v_Temp     := Substr(v_Temp, Instr(v_Temp, ',') + 1);
      v_人员姓名 := Substr(v_Temp, Instr(v_Temp, ',') + 1);
    End If;
  
    --打印时已确认停止的医嘱标记为已打印停止标记
    v_标记 := 0;
    If 医嘱id_In Is Not Null And 期效_In = 0 Then
      Select Decode(确认停嘱时间, Null, Decode(打印模式_In, 1, Decode(执行终止时间, Null, 0, 1), 0), 2)
      Into v_标记
      From 病人医嘱记录
      Where ID = 医嘱id_In;
    End If;
  
    Update 病人医嘱打印
    Set 打印标记 = v_标记, 打印人 = v_人员姓名, 打印时间 = 时间_In
    Where 医嘱id = 医嘱id_In And 页号 = 页号_In And 行号 = 行号_In And 病人id = 病人id_In And 主页id = 主页id_In And Nvl(婴儿, 0) = 婴儿_In And
          期效 = 期效_In;
          
    If 医嘱id_In Is Null Then
      ----特殊医嘱标记为打印
      Update 病人医嘱打印
      Set 打印标记 = v_标记, 打印人 = v_人员姓名, 打印时间 = 时间_In
      Where 医嘱id Is Null And 页号 = 页号_In And 行号 = 行号_In And 病人id = 病人id_In And 主页id = 主页id_In And Nvl(婴儿, 0) = 婴儿_In And
            期效 = 期效_In;
    End If;  
  End If;
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_病人医嘱打印_Update;
/

--78413:胡俊勇,2014-11-17,医嘱清单打印
Create Or Replace Procedure Zl_病人医嘱打印_Delete
(
  病人id_In   病人医嘱记录.病人id%Type,
  主页id_In   病人医嘱记录.主页id%Type,
  婴儿_In     病人医嘱记录.婴儿%Type,
  期效_In     病人医嘱记录.医嘱期效%Type,
  起始页号_In 病人医嘱打印.页号%Type,
  医嘱位置_In Varchar2 := Null,
  清除方式_In Number := 0
  --1－清除未打印但已生成的预打印记录，0－清除已经打过的记录及之后的
) Is
  v_位置 Varchar2(20);
Begin

  If 期效_In = 0 Then
    Select Nvl(Max(LPad(页号, 4, '0') || LPad(行号, 3, '0')), '0000000')
    Into v_位置
    From 病人医嘱打印
    Where 病人id = 病人id_In And 主页id = 主页id_In And Nvl(婴儿, 0) = 婴儿_In And 期效 = 期效_In And 医嘱id Is Null And 特殊医嘱 Is Null;
  Else
    v_位置 := '0000000';
  End If;

  If 医嘱位置_In > v_位置 And 清除方式_In = 1 Then
    v_位置 := 医嘱位置_In;
  
    --长期医嘱单有可能产生了特殊医嘱，重整，重开 的预打印记录。如果是紧挨着的要删掉
    If 期效_In = 0 Then
      For R In (Select 医嘱id, 打印时间, LPad(页号, 4, '0') || LPad(行号, 3, '0') As 位置
                From 病人医嘱打印
                Where 病人id = 病人id_In And 主页id = 主页id_In And Nvl(婴儿, 0) = 婴儿_In And 期效 = 期效_In And 页号 = 起始页号_In And
                      LPad(页号, 4, '0') || LPad(行号, 3, '0') < v_位置
                Order By 行号 Desc) Loop
        If r.医嘱id Is Null And r.打印时间 Is Null Then
          v_位置 := r.位置;
        Else
          Exit;
        End If;
      End Loop;
    End If;
  End If;

  Delete From 病人医嘱打印
  Where 病人id = 病人id_In And 主页id = 主页id_In And Nvl(婴儿, 0) = 婴儿_In And 期效 = 期效_In And 页号 >= 起始页号_In And
        LPad(页号, 4, '0') || LPad(行号, 3, '0') >= v_位置;

Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_病人医嘱打印_Delete;
/

--79209:刘尔旋,2014-12-02,二次结算异常退费更新登记时间
--77834:李南春,2014-10-15,门诊收费二次结算
Create Or Replace Procedure Zl_门诊收费异常_Update
(
  No_In       门诊费用记录.No%Type,
  登记时间_In 门诊费用记录.登记时间%Type,
  结帐id_In   门诊费用记录.结帐id%Type := Null
) As
  v_Err_Msg Varchar2(255);
  Err_Item Exception;
  d_Date Date;
Begin
  --功能：更新异常单据的登记时间及收款时间
  --结帐ID_IN: 传入时,以结帐ID进行更新操作;否则以NO_IN进行操作

  d_Date := 登记时间_In;
  If d_Date Is Null Then
    d_Date := Sysdate;
  End If;

  If Nvl(结帐id_In, 0) <> 0 Then
    --更新指定结帐的门诊费用及预交费的登记时间
    Update 门诊费用记录 Set 登记时间 = d_Date Where 结帐id = 结帐id_In;
    If Sql%NotFound Then
      Update 费用补充记录 Set 登记时间 = d_Date Where 结算id = 结帐id_In;
      Update 门诊费用记录
      Set 登记时间 = d_Date
      Where 记录状态 = 2 And 结帐id In (Select Distinct a.结帐id
                                  From 病人预交记录 A, 费用补充记录 B
                                  Where a.结算序号 = b.结算序号 And a.记录性质 = 3 And b.结算id = 结帐id_In);
    End If;
    Update 病人预交记录 Set 收款时间 = d_Date Where 结帐id = 结帐id_In And Mod(记录性质, 10) <> 1;
    Return;
  End If;
  If No_In Is Null Then
    v_Err_Msg := '未传入指定的单据信息！!';
    Raise Err_Item;
  
  End If;
  Update 门诊费用记录
  Set 登记时间 = d_Date
  Where NO = No_In And Mod(记录性质, 10) = 1 And 记录状态 = 1 And Nvl(费用状态, 0) = 1;
  If Sql%NotFound Then
    Update 费用补充记录
    Set 登记时间 = d_Date
    Where NO = No_In And Mod(记录性质, 10) = 1 And 记录状态 = 1 And Nvl(费用状态, 0) = 1;
  
    Update 病人预交记录
    Set 收款时间 = d_Date
    Where 结帐id In (Select Distinct 结算id
                   From 费用补充记录
                   Where NO = No_In And 记录性质 = 1 And 记录状态 = 1 And Nvl(费用状态, 0) = 1) And Mod(记录性质, 10) <> 1;
  Else
    Update 病人预交记录
    Set 收款时间 = d_Date
    Where 结帐id In (Select Distinct 结帐id
                   From 门诊费用记录
                   Where NO = No_In And 记录性质 = 1 And 记录状态 = 1 And Nvl(费用状态, 0) = 1) And Mod(记录性质, 10) <> 1;
  End If;
Exception
  When Err_Item Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Err_Msg || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_门诊收费异常_Update;
/

--80704:冉俊明,2014-12-16,挂号使用收费票据，补充结算时未收回票据
--79973:刘尔旋,2014-12-03,补打票据只对照一张单据的问题
--79096:冉俊明,2014-10-31,在门诊收费结算时多张收费单据打印到了同一张票据上时，二次补结算收回票据失败
--77834:李南春,2014-10-15,门诊收费二次结算
Create Or Replace Procedure Zl_门诊收费记录_Reprint
(
  No_In         门诊费用记录.No%Type,
  票据号_In     票据使用明细.号码%Type,
  领用id_In     票据使用明细.领用id%Type,
  使用人_In     票据使用明细.使用人%Type,
  使用时间_In   票据使用明细.使用时间%Type,
  退费_In       Number := 0,
  票据张数_In   Number := 0,
  收回票据号_In Varchar2 := Null,
  票种_In       Number := 1
) As
  --功能：用于重打收费票据 
  --参数： 
  --      NO_IN         =   要重打的单据号，可能是一起收费的多张单据中的一张。 
  --      票据号        =   用于重打的起始票据号.而该张收费单据可能使用多张票据。 
  --      领用ID        =   如果为0或NULL,表示不严格控制票据。 
  --      退费_IN       =   是否部份退费时调用该过程 
  --      收回票据号_In =   部分退费时,界面选择要收回的票据号,这些票据只收回,不重新发出,传入空表示全部收回并重打 
  --该游标用于范围判断 
  Cursor c_Fact Is
    Select 开始号码, 终止号码 From 票据领用记录 Where ID = Nvl(领用id_In, 0);
  r_Factrow c_Fact%RowType;

  v_收回id 票据打印内容.Id%Type;
  v_打印id 票据打印内容.Id%Type;
  v_票据号 票据使用明细.号码%Type;
  n_补打   Number(2);
  n_分单据 Number(2);

  v_Error Varchar2(255);
  Err_Custom Exception;
Begin
  n_分单据 := Zl_Getsysparameter('多张单据收费分别打印', 1121);
  --获取该张单据的最后一次的的打印ID(可能没有) 
  Begin
    Select Max(ID) Into v_收回id From 票据打印内容 Where 数据性质 = 票种_In And NO = No_In;
  Exception
    When Others Then
      n_补打 := 1;
  End;

  --收回票据(可能以前未控制票据,无法收回) 
  If v_收回id Is Not Null Then
    If 收回票据号_In Is Null Then
      If Nvl(退费_In, 0) = 0 Then
        --a.重打 
        Insert Into 票据使用明细
          (ID, 票种, 号码, 性质, 原因, 领用id, 打印id, 使用人, 使用时间)
          Select 票据使用明细_Id.Nextval, 票种, 号码, 2, 4, 领用id, 打印id, 使用人_In, 使用时间_In
          From 票据使用明细 A
          Where 打印id = v_收回id And 性质 = 1 And Not Exists
           (Select 1 From 票据使用明细 B Where a.号码 = b.号码 And 打印id = v_收回id And 性质 = 2);
      Else
        --b.部分退费重打先收回 
        Insert Into 票据使用明细
          (ID, 票种, 号码, 性质, 原因, 领用id, 打印id, 使用人, 使用时间)
          Select 票据使用明细_Id.Nextval, 票种, 号码, 2, 2, 领用id, 打印id, 使用人_In, 使用时间_In
          From 票据使用明细 A
          Where 打印id = v_收回id And 性质 = 1 And Not Exists
           (Select 1 From 票据使用明细 B Where a.号码 = b.号码 And 打印id = v_收回id And 性质 = 2);
      End If;
    Else
      --c.部分退费收回指定的票据号 
      Insert Into 票据使用明细
        (ID, 票种, 号码, 性质, 原因, 领用id, 打印id, 使用人, 使用时间)
        Select 票据使用明细_Id.Nextval, 票种, 号码, 2, 2, 领用id, 打印id, 使用人_In, 使用时间_In
        From 票据使用明细 A
        Where 打印id = v_收回id And 性质 = 1 And Instr(收回票据号_In, 号码) > 0 And Not Exists
         (Select 1 From 票据使用明细 B Where a.号码 = b.号码 And 打印id = v_收回id And 性质 = 2);
      Return;
    End If;
  Else
    n_补打 := 1;
  End If;

  --无票据号时,不用处理票据 
  If 票据号_In Is Null Then
    Return;
  End If;

  --重新发出票据并填写票据打印内容 
  Select 票据打印内容_Id.Nextval Into v_打印id From Dual;
  If n_补打 = 1 And n_分单据 = 0 Then
    Insert Into 票据打印内容
      (ID, 数据性质, NO)
      Select Distinct v_打印id, 1, NO
      From (Select Distinct a.No
             From 门诊费用记录 A, 病人预交记录 B
             Where a.结帐id = b.结帐id And b.结算序号 In (Select Distinct a.结算序号
                                                  From 病人预交记录 A, 门诊费用记录 B
                                                  Where b.No = No_In And a.结帐id = b.结帐id));
  
    --门诊费用记录中记录票据号以便显示
    If 票种_In = 1 Or 票种_In = 4 Then
      Update 门诊费用记录
      Set 实际票号 = 票据号_In
      Where Mod(记录性质, 10) = Decode(票种_In, 1, 1, 4) And
            NO In
            (Select Distinct a.No
             From 门诊费用记录 A, 病人预交记录 B
             Where a.结帐id = b.结帐id And b.结算序号 In (Select Distinct a.结算序号
                                                  From 病人预交记录 A, 门诊费用记录 B
                                                  Where b.No = No_In And a.结帐id = b.结帐id));
    End If;
  Else
    Insert Into 票据打印内容
      (ID, 数据性质, NO)
      Select Distinct v_打印id, 1, NO
      From (Select NO
             From 票据打印内容
             Where ID = v_收回id And 数据性质 = 票种_In
             Union All
             Select No_In From Dual);
  
    --门诊费用记录中记录票据号以便显示 
    If 票种_In = 1 Or 票种_In = 4 Then
      Update 门诊费用记录
      Set 实际票号 = 票据号_In
      Where Mod(记录性质, 10) = Decode(票种_In, 1, 1, 4) And
            NO In (Select NO
                   From 票据打印内容
                   Where ID = v_收回id And 数据性质 = 票种_In
                   Union
                   Select No_In From Dual);
    End If;
  End If;
  v_票据号 := 票据号_In;
  If Nvl(领用id_In, 0) <> 0 Then
    Open c_Fact;
    Fetch c_Fact
      Into r_Factrow;
    If c_Fact%RowCount = 0 Then
      v_Error := '无效的票据领用批次,无法完成重打操作！';
      Close c_Fact;
      Raise Err_Custom;
    End If;
  End If;
  For I In 1 .. 票据张数_In Loop
    If Nvl(领用id_In, 0) <> 0 Then
      --检查票据范围 
      If Not (Upper(v_票据号) >= Upper(r_Factrow.开始号码) And Upper(v_票据号) <= Upper(r_Factrow.终止号码) And
          Length(v_票据号) = Length(r_Factrow.终止号码)) Then
        v_Error := '单据需要打印多张票据,但票据号"' || v_票据号 || '"超出票据领用的号码范围！';
        Close c_Fact;
        Raise Err_Custom;
      End If;
    End If;
  
    --发出票据:重打时,如果是部份退费重打,则处理为新发生；如果以前没有收回，也处理为新发出。 
    Insert Into 票据使用明细
      (ID, 票种, 号码, 性质, 原因, 领用id, 打印id, 使用人, 使用时间)
    Values
      (票据使用明细_Id.Nextval, 票种_In, v_票据号, 1, Decode(Nvl(退费_In, 0), 0, Decode(v_收回id, Null, 1, 3), 1), 领用id_In, v_打印id,
       使用人_In, 使用时间_In);
  
    Update 票据领用记录
    Set 当前号码 = v_票据号, 剩余数量 = Decode(Sign(Nvl(剩余数量, 0) - 1), -1, 0, Nvl(剩余数量, 0) - 1)
    Where ID = Nvl(领用id_In, 0);
  
    v_票据号 := Zl_Incstr(v_票据号);
  End Loop;
  Update 票据领用记录 Set 使用时间 = 使用时间_In Where ID = 领用id_In;

  If Nvl(领用id_In, 0) <> 0 Then
    Close c_Fact;
  End If;
Exception
  When Err_Custom Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Error || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_门诊收费记录_Reprint;
/

--77834:李南春,2014-10-15,门诊收费二次结算
Create Or Replace Procedure Zl_票据起始号_Update
(
  No_In     门诊费用记录.No%Type,
  票据号_In 票据使用明细.号码%Type,
  票种_In   票据使用明细.票种%Type
) As
  --在费用数据产生时填写开始票据号,用于医保上传 
  --如果票据号传入空将清空开始票据号,用于打印失败或取消打印时调用 
Begin
  If 票种_In = 1 Then
    Update 门诊费用记录 Set 实际票号 = 票据号_In Where 记录性质 = 1 And NO = No_In;
    If Sql%NotFound Then
      Update 费用补充记录 Set 实际票号 = 票据号_In Where 记录性质 = 1 And NO = No_In;
    End If;
  Elsif 票种_In = 3 Then
    Update 病人结帐记录 Set 实际票号 = 票据号_In Where 记录状态 = 1 And NO = No_In;
  End If;
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_票据起始号_Update;
/

--77834:冉俊明,2014-10-23,门诊收费二次结算
--77834:李南春,2014-10-15,门诊收费二次结算
Create Or Replace Procedure Zl_费用补充记录_补结算
(
  No_In          In 费用补充记录.No%Type,
  实际票号_In    In 费用补充记录.实际票号%Type,
  结算id_In      In 费用补充记录.结算id%Type,
  结算序号_In    In 病人预交记录.结算序号%Type,
  收费结帐ids_In Varchar2,
  医保结算_In    Varchar2,
  操作员编号_In  In 费用补充记录.操作员编号%Type,
  操作员姓名_In  In 费用补充记录.操作员姓名%Type,
  登记时间_In    In 费用补充记录.登记时间%Type := Null,
  备注_In        In 费用补充记录.备注%Type := Null,
  附加标志_In    In 费用补充记录.备注%Type := Null,
  费用状态_In    In 费用补充记录.费用状态%Type := 0
) Is
  -- 医保结算_IN:允许传入多个,格式为:结算方式,结算金额|..
  Err_Item Exception;

  v_Err_Msg Varchar2(255);
  n_Date    Date;
  n_病人id  门诊费用记录.病人id%Type;
  n_组id    财务缴款分组.Id%Type;
  n_冲预交  病人预交记录.冲预交%Type;
Begin
  n_组id := Zl_Get组id(操作员姓名_In);

  n_Date := 登记时间_In;
  If 登记时间_In Is Null Then
    n_Date := Sysdate;
  End If;
  Begin
    Select 病人id
    Into n_病人id
    From 门诊费用记录
    Where 结帐id In (Select Column_Value From Table(f_Num2list(收费结帐ids_In))) And Rownum < 2;
  Exception
    When Others Then
      n_病人id := Null;
  End;
  If Nvl(n_病人id, 0) = 0 Then
    v_Err_Msg := '费用记录中存在未建档病人的费用,不允许继续操作！';
    Raise Err_Item;
  End If;

  If 医保结算_In Is Not Null Then
  
    Insert Into 病人预交记录
      (ID, 记录性质, NO, 记录状态, 病人id, 主页id, 摘要, 结算方式, 收款时间, 操作员编号, 操作员姓名, 冲预交, 结帐id, 缴款组id, 结算序号, 校对标志, 卡类别id, 结算卡序号, 卡号,
       交易流水号, 交易说明, 结算号码)
    
      Select 病人预交记录_Id.Nextval, 6, No_In As NO, 1, n_病人id As 病人id, Null As 主页id, Null As 摘要, 结算方式, n_Date As 收款时间,
             操作员编号_In As 操作员编号, 操作员姓名_In As 操作员姓名, 结算金额, 结算id_In As 结帐id, n_组id As 缴款组id, 结算序号_In As 结算序号, 2 As 校对标志,
             Null As 卡类别id, Null As 结算卡序号, Null As 卡号, Null As 交易流水号, Null As 交易说明, Null As 结算号码
      From (Select C1 As 结算方式, To_Number(C2) As 结算金额 From Table(f_Str2list2(医保结算_In, '|', ',')));
  
    Select Sum(To_Number(C2)) Into n_冲预交 From Table(f_Str2list2(医保结算_In, '|', ','));
  End If;
  --增加结算方式为NULL
  Insert Into 病人预交记录
    (ID, 记录性质, NO, 记录状态, 病人id, 主页id, 摘要, 结算方式, 收款时间, 操作员编号, 操作员姓名, 冲预交, 结帐id, 缴款组id, 结算序号, 校对标志, 卡类别id, 结算卡序号, 卡号,
     交易流水号, 交易说明, 结算号码)
  Values
    (病人预交记录_Id.Nextval, 6, No_In, 1, n_病人id, Null, '', Null, n_Date, 操作员编号_In, 操作员姓名_In, -1 * n_冲预交, 结算id_In, n_组id,
     结算序号_In, 1, Null, Null, Null, Null, Null, Null);

  Insert Into 费用补充记录
    (记录性质, NO, 记录状态, 实际票号, 结算id, 收费结帐id, 费用状态, 操作员编号, 操作员姓名, 登记时间, 缴款组id, 病人id, 结算序号, 备注, 附加标志)
    Select 1 As 记录性质, No_In As NO, 1 As 记录状态, 实际票号_In As 实际票号, 结算id_In As 结算id, 结帐id, 费用状态_In As 费用状态, 操作员编号_In As 操作员编号,
           操作员姓名_In As 操作员姓名, n_Date As 登记时间, n_组id As 缴款组id, n_病人id As 病人id, 结算序号_In As 结算序号, 备注_In, 附加标志_In
    From (Select Column_Value As 结帐id From Table(f_Num2list(收费结帐ids_In)));
Exception
  When Err_Item Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Err_Msg || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_费用补充记录_补结算;
/

--79314:冉俊明,2014-11-06,费用补结算时，若医保接口返回结算方式的结算金额为零，由原在病人预交记录中不保存改为保存
--79172:刘尔旋,2014-11-06,二次结算退费卡死的问题
--79114:冉俊明,2014-10-31,结算存在误差时保存数据出错
--77834:冉俊明,2014-10-22,门诊收费二次结算
--77834:李南春,2014-10-15,门诊收费二次结算
Create Or Replace Procedure Zl_费用补充结算_Modify
(
  操作类型_In   Number,
  结算id_In     In 费用补充记录.结算id%Type,
  结算方式_In   Varchar2,
  卡类别id_In   病人预交记录.卡类别id%Type := Null,
  卡号_In       病人预交记录.卡号%Type := Null,
  交易流水号_In 病人预交记录.交易流水号%Type := Null,
  交易说明_In   病人预交记录.交易说明%Type := Null,
  误差金额_In   门诊费用记录.实收金额%Type := Null,
  完成结算_In   Number := 0
) As
  ------------------------------------------------------------------------------------------------------------------------------
  --功能:保险补充结算时,修改结算的相关信息
  --操作类型_In:
  --   0-普通结算方式:
  --     结算方式_IN:允许传入多个,格式为:结算方式|结算金额|结算号码|结算摘要||.. ;也允许传入空.
  --   1.三方卡结算:
  --     ①结算方式_IN:只能传入一个结算方式,但允许包含一些辅助信息,格式为:结算方式|结算金额|结算号码|结算摘要
  --     ②卡类别ID_IN,卡号_IN,交易流水号_IN,交易说明_In:需要传入
  --   2-医保结算(如果存在医保的结算,则要先删除原医保结算,后按新传入的更新)
  --     ①结算方式_IN:允许传入多个,格式为:结算方式|结算金额||..
  -- 误差金额_In:存在误差费时,传入
  -- 完成结算_In:1-完成补充结算;0-未完成补充结算;2-完成了异常作废
  ------------------------------------------------------------------------------------------------------------------------------

  Err_Item Exception;
  v_Err_Msg  Varchar2(255);
  v_误差费   结算方式.名称%Type;
  v_结算方式 病人预交记录.结算方式%Type;
  n_结算金额 病人预交记录.冲预交%Type;
  v_结算号码 病人预交记录.结算号码%Type;
  v_结算摘要 病人预交记录.摘要%Type;
  n_冲预交   病人预交记录.冲预交%Type;
  l_预交id   t_Numlist := t_Numlist();
  v_结算内容 Varchar2(4000);
  v_当前结算 Varchar2(4000);
  Cursor c_Balance Is
    Select 记录性质, NO, 记录状态, 实际票号, 结算id, 收费结帐id, 费用状态, 操作员编号, 操作员姓名, 登记时间, 缴款组id, 病人id, 结算序号

    
    From 费用补充记录 A
    Where 结算id = 结算id_In And Rownum < 2 And 记录性质 = 1;
  r_Balance c_Balance%RowType;

  n_Count Number(18);

Begin
  Begin
    Select 1 Into n_Count From 费用补充记录 Where 结算id = 结算id_In And Rownum < 2 And 记录性质 = 1;
  
  Exception
    When Others Then
      n_Count := 0;
  End;
  If n_Count = 0 Then
    v_Err_Msg := '未找到医保补结算数据，不能继续操作!';
    Raise Err_Item;
  End If;

  Open c_Balance;
  Fetch c_Balance
    Into r_Balance;

  If Nvl(误差金额_In, 0) <> 0 Then
    Begin
      Select 名称 Into v_误差费 From 结算方式 Where Nvl(性质, 0) = 9;
    Exception
      When Others Then
        v_误差费 := '误差费';
    End;
  
    Update 病人预交记录
    Set 冲预交 = Nvl(冲预交, 0) + Nvl(误差金额_In, 0)
    Where 结帐id = 结算id_In And 结算方式 = v_误差费;
    If Sql%NotFound Then
      Insert Into 病人预交记录
        (ID, 记录性质, NO, 记录状态, 病人id, 主页id, 摘要, 结算方式, 收款时间, 操作员编号, 操作员姓名, 冲预交, 结帐id, 缴款组id, 结算序号, 校对标志, 卡类别id, 结算卡序号, 卡号,
         交易流水号, 交易说明, 结算号码)
      Values
        (病人预交记录_Id.Nextval, 6, r_Balance.No, r_Balance.记录状态, r_Balance.病人id, Null, Null, v_误差费, r_Balance.登记时间,
         r_Balance.操作员编号, r_Balance.操作员姓名, 误差金额_In, r_Balance.结算id, r_Balance.缴款组id, r_Balance.结算序号, 2, Null, Null,
         卡号_In, 交易流水号_In, 交易说明_In, Null);
      Update 病人预交记录 Set 冲预交 = Nvl(冲预交, 0) - 误差金额_In Where 结帐id = 结算id_In And 结算方式 Is Null;
    End If;
  End If;

  --0.普通结算方式
  If Nvl(操作类型_In, 0) = 0 Then
  
    --各个收费结算 :格式为:结算方式|结算金额|结算号码|结算摘要||..
    v_结算内容 := 结算方式_In || '||';
    While v_结算内容 Is Not Null Loop
      v_当前结算 := Substr(v_结算内容, 1, Instr(v_结算内容, '||') - 1);
    
      v_结算方式 := Substr(v_当前结算, 1, Instr(v_当前结算, '|') - 1);
    
      v_当前结算 := Substr(v_当前结算, Instr(v_当前结算, '|') + 1);
      n_结算金额 := To_Number(Substr(v_当前结算, 1, Instr(v_当前结算, '|') - 1));
    
      v_当前结算 := Substr(v_当前结算, Instr(v_当前结算, '|') + 1);
      v_结算号码 := LTrim(Substr(v_当前结算, 1, Instr(v_当前结算, '|') - 1));
      v_结算摘要 := LTrim(Substr(v_当前结算, Instr(v_当前结算, '|') + 1));
    
      If Nvl(n_结算金额, 0) <> 0 Then
        Insert Into 病人预交记录
          (ID, 记录性质, NO, 记录状态, 病人id, 主页id, 摘要, 结算方式, 收款时间, 操作员编号, 操作员姓名, 冲预交, 结帐id, 缴款组id, 结算序号, 校对标志, 卡类别id, 结算卡序号, 卡号,
           交易流水号, 交易说明, 结算号码)
        Values
          (病人预交记录_Id.Nextval, 6, r_Balance.No, r_Balance.记录状态, r_Balance.病人id, Null, v_结算摘要, v_结算方式, r_Balance.登记时间,
           r_Balance.操作员编号, r_Balance.操作员姓名, n_结算金额, r_Balance.结算id, r_Balance.缴款组id, r_Balance.结算序号, 2, Null, Null,
           卡号_In, 交易流水号_In, 交易说明_In, v_结算号码);
      
        Update 病人预交记录 Set 冲预交 = Nvl(冲预交, 0) - n_结算金额 Where 结帐id = 结算id_In And 结算方式 Is Null;
      End If;
      v_结算内容 := Substr(v_结算内容, Instr(v_结算内容, '||') + 2);
    End Loop;
  
  End If;
  --1.三方卡结算
  If Nvl(操作类型_In, 0) = 1 Then
  
    v_当前结算 := 结算方式_In;
    v_结算方式 := Substr(v_当前结算, 1, Instr(v_当前结算, '|') - 1);
    v_当前结算 := Substr(v_当前结算, Instr(v_当前结算, '|') + 1);
    n_结算金额 := To_Number(Substr(v_当前结算, 1, Instr(v_当前结算, '|') - 1));
    v_当前结算 := Substr(v_当前结算, Instr(v_当前结算, '|') + 1);
    v_结算号码 := LTrim(Substr(v_当前结算, 1, Instr(v_当前结算, '|') - 1));
    v_结算摘要 := LTrim(Substr(v_当前结算, Instr(v_当前结算, '|') + 1));
  
    If Nvl(n_结算金额, 0) <> 0 Then
      Insert Into 病人预交记录
        (ID, 记录性质, NO, 记录状态, 病人id, 主页id, 摘要, 结算方式, 收款时间, 操作员编号, 操作员姓名, 冲预交, 结帐id, 缴款组id, 结算序号, 校对标志, 卡类别id, 结算卡序号, 卡号,
         交易流水号, 交易说明, 结算号码)
      Values
        (病人预交记录_Id.Nextval, 6, r_Balance.No, r_Balance.记录状态, r_Balance.病人id, Null, v_结算摘要, v_结算方式, r_Balance.登记时间,
         r_Balance.操作员编号, r_Balance.操作员姓名, n_结算金额, r_Balance.结算id, r_Balance.缴款组id, r_Balance.结算序号, 2, 卡类别id_In, Null,
         卡号_In, 交易流水号_In, 交易说明_In, v_结算号码);
      Update 病人预交记录 Set 冲预交 = Nvl(冲预交, 0) - n_结算金额 Where 结帐id = 结算id_In And 结算方式 Is Null;
    End If;
  End If;

  --2.医保结算
  If Nvl(操作类型_In, 0) = 2 Then
  
    --2.1检查是否已经存在医保结算数据,存在先删除
    n_结算金额 := 0;
    For v_医保 In (Select ID, 结算方式, 冲预交
                 From 病人预交记录 A
                 Where 结帐id = 结算id_In And Exists
                  (Select 1 From 结算方式 Where a.结算方式 = 名称 And 性质 In (3, 4)) And Mod(记录性质, 10) <> 1)
    
     Loop
      n_结算金额 := n_结算金额 + Nvl(v_医保.冲预交, 0);
      l_预交id.Extend;
      l_预交id(l_预交id.Count) := v_医保.Id;
    End Loop;
  
    If l_预交id.Count <> 0 Then
      Forall I In 1 .. l_预交id.Count
        Delete 病人预交记录 Where ID = l_预交id(I);
    End If;
  
    --先删除结算方式为空的记录
    Delete 病人预交记录 Where 结帐id = 结算id_In And 结算方式 Is Null;
    If 结算方式_In Is Not Null Then
      v_结算内容 := 结算方式_In || '||';
    End If;
    n_冲预交 := 0;
    While v_结算内容 Is Not Null Loop
      v_当前结算 := Substr(v_结算内容, 1, Instr(v_结算内容, '||') - 1);
      v_结算方式 := Substr(v_当前结算, 1, Instr(v_当前结算, '|') - 1);
      n_结算金额 := To_Number(Substr(v_当前结算, Instr(v_当前结算, '|') + 1));
      Insert Into 病人预交记录
        (ID, 记录性质, NO, 记录状态, 病人id, 主页id, 摘要, 结算方式, 收款时间, 操作员编号, 操作员姓名, 冲预交, 结帐id, 缴款组id, 结算序号, 校对标志, 卡类别id, 结算卡序号, 卡号,
         交易流水号, 交易说明, 结算号码)
      Values
        (病人预交记录_Id.Nextval, 6, r_Balance.No, r_Balance.记录状态, r_Balance.病人id, Null, '保险结算', v_结算方式, r_Balance.登记时间,
         r_Balance.操作员编号, r_Balance.操作员姓名, Nvl(n_结算金额, 0), r_Balance.结算id, r_Balance.缴款组id, r_Balance.结算序号, 2, Null,
         Null, Null, Null, Null, Null);
      n_冲预交 := n_冲预交 + Nvl(n_结算金额, 0);
    
      v_结算内容 := Substr(v_结算内容, Instr(v_结算内容, '||') + 2);
    End Loop;
  
    --处理结算方式为NULL
    Insert Into 病人预交记录
      (ID, 记录性质, NO, 记录状态, 病人id, 主页id, 摘要, 结算方式, 收款时间, 操作员编号, 操作员姓名, 冲预交, 结帐id, 缴款组id, 结算序号, 校对标志, 卡类别id, 结算卡序号, 卡号,
       交易流水号, 交易说明, 结算号码)
    Values
      (病人预交记录_Id.Nextval, 6, r_Balance.No, r_Balance.记录状态, r_Balance.病人id, Null, '', Null, r_Balance.登记时间,
       r_Balance.操作员编号, r_Balance.操作员姓名, -1 * n_冲预交, r_Balance.结算id, r_Balance.缴款组id, r_Balance.结算序号, 1, Null, Null,
       Null, Null, Null, Null);
    --医保相关表的处理
    Update 保险结算明细 Set 标志 = 2 Where 结帐id = 结算id_In;
  End If;

  If Nvl(完成结算_In, 0) = 0 Then
    Return;
  End If;

  If Nvl(完成结算_In, 0) = 2 Then
    --1.更新校对标志
    Update 病人预交记录 Set 校对标志 = 0 Where NO = r_Balance.No;
    Update 费用补充记录 Set 费用状态 = 2 Where NO = r_Balance.No;
    If Sql%NotFound Then
      v_Err_Msg := '未找到医保补结算数据，可能被他人进行了作废操作!';
      Raise Err_Item;
    End If;
    Return;
  End If;

  Select Sum(冲预交) Into n_冲预交 From 病人预交记录 Where 结帐id = 结算id_In;
  If Nvl(n_冲预交, 0) <> 0 Then
    v_Err_Msg := '医保结算数据与退未结算数据不匹配!';
    Raise Err_Item;
  End If;

  Delete 病人预交记录 Where 结帐id = 结算id_In And 结算方式 Is Null And Nvl(冲预交, 0) = 0;
  If Sql%NotFound Then
    Select Count(*) Into n_Count From 病人预交记录 A Where 结帐id = 结算id_In And 结算方式 Is Null;
    If n_Count <> 0 Then
      v_Err_Msg := '还存在未缴款的数据,不能完成结算!';
    Else
      v_Err_Msg := '结算信息错误,可能因为并发原因造成结算信息错误,请在[收费结算窗口]中重新收费！!';
    End If;
    Raise Err_Item;
  End If;

  --1.更新异常状态
  Update 费用补充记录 Set 费用状态 = 0 Where 结算序号 = r_Balance.结算序号;
  If Sql%NotFound Then
    v_Err_Msg := '未找到医保补结算数据，可能被他人进行了退费或作废操作!';
    Raise Err_Item;
  End If;

  --2.更新校对标志
  Update 病人预交记录 Set 校对标志 = 0 Where 结帐id = 结算id_In;
  --3.更新人员缴款数据
  For c_缴款 In (Select 结算方式, 操作员姓名, Sum(Nvl(a.冲预交, 0)) As 冲预交
               From 病人预交记录 A
               Where a.结算序号 = r_Balance.结算序号 And Mod(a.记录性质, 10) <> 1
               Group By 结算方式, 操作员姓名) Loop
    Update 人员缴款余额
    Set 余额 = Nvl(余额, 0) + Nvl(c_缴款.冲预交, 0)
    Where 收款员 = c_缴款.操作员姓名 And 性质 = 1 And 结算方式 = c_缴款.结算方式;
    If Sql%RowCount = 0 Then
      Insert Into 人员缴款余额
        (收款员, 结算方式, 性质, 余额)
      Values
        (c_缴款.操作员姓名, c_缴款.结算方式, 1, Nvl(c_缴款.冲预交, 0));
    End If;
  End Loop;
Exception
  When Err_Item Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Err_Msg || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_费用补充结算_Modify;
/

--77834:冉俊明,2014-10-22,门诊收费二次结算
--77834:冉俊明,2014-10-27,门诊收费二次结算
--77834:李南春,2014-10-15,门诊收费二次结算
Create Or Replace Procedure Zl_费用补充结算_完成退费
(
  结算id_In     In 费用补充记录.结算id%Type,
  结算方式_In   Varchar2,
  卡类别id_In   病人预交记录.卡类别id%Type := Null,
  卡号_In       病人预交记录.卡号%Type := Null,
  交易流水号_In 病人预交记录.交易流水号%Type := Null,
  交易说明_In   病人预交记录.交易说明%Type := Null,
  误差金额_In   门诊费用记录.实收金额%Type := Null
) As
  ------------------------------------------------------------------------------------------------------------------------------
  --功能:补结算退费
  --   结算方式_IN:格式为:"结算方式|结算金额|结算号码|结算摘要" ;也允许传入空.
  --   三方卡结算:需传入卡类别ID_IN,卡号_IN,交易流水号_IN,交易说明_In:需要传入
  -- 误差金额_In:存在误差费时,传入
  ------------------------------------------------------------------------------------------------------------------------------

  Err_Item Exception;
  v_Err_Msg  Varchar2(255);
  v_误差费   结算方式.名称%Type;
  v_结算方式 病人预交记录.结算方式%Type;
  n_结算金额 病人预交记录.冲预交%Type;
  v_结算号码 病人预交记录.结算号码%Type;
  v_结算摘要 病人预交记录.摘要%Type;
  n_冲预交   病人预交记录.冲预交%Type;
  v_当前结算 Varchar2(4000);
  Cursor c_Balance Is
    Select 记录性质, NO, 记录状态, 实际票号, 结算id, 收费结帐id, 费用状态, 操作员编号, 操作员姓名, 登记时间, 缴款组id, 病人id, 结算序号, 附加标志
    From 费用补充记录 A
    Where 结算id = 结算id_In And Rownum < 2 And 记录性质 = 1;
  r_Balance  c_Balance%RowType;
  n_未退合计 病人预交记录.冲预交%Type;
  n_Count    Number(18);
Begin
  Begin
    Select 1 Into n_Count From 费用补充记录 Where 结算id = 结算id_In And Rownum < 2 And 记录性质 = 1;
  Exception
    When Others Then
      n_Count := 0;
  End;
  If n_Count = 0 Then
    v_Err_Msg := '未找到医保补结算数据，不能继续操作!';
    Raise Err_Item;
  End If;

  Open c_Balance;
  Fetch c_Balance
    Into r_Balance;

  If Nvl(误差金额_In, 0) <> 0 Then
    Begin
      Select 名称 Into v_误差费 From 结算方式 Where Nvl(性质, 0) = 9;
    Exception
      When Others Then
        v_误差费 := '误差费';
    End;
  
    Update 病人预交记录
    Set 冲预交 = Nvl(冲预交, 0) + Nvl(误差金额_In, 0)
    Where 结帐id = 结算id_In And 结算方式 = v_误差费;
  
    If Sql%NotFound Then
      Insert Into 病人预交记录
        (ID, 记录性质, NO, 记录状态, 病人id, 主页id, 摘要, 结算方式, 收款时间, 操作员编号, 操作员姓名, 冲预交, 结帐id, 缴款组id, 结算序号, 校对标志, 卡类别id, 结算卡序号, 卡号,
         交易流水号, 交易说明, 结算号码)
      Values
        (病人预交记录_Id.Nextval, 6, r_Balance.No, r_Balance.记录状态, r_Balance.病人id, Null, Null, v_误差费, r_Balance.登记时间,
         r_Balance.操作员编号, r_Balance.操作员姓名, 误差金额_In, r_Balance.结算id, r_Balance.缴款组id, r_Balance.结算序号, 2, Null, Null,
         卡号_In, 交易流水号_In, 交易说明_In, Null);
    End If;
  
    Update 病人预交记录
    Set 冲预交 = Nvl(冲预交, 0) - Nvl(误差金额_In, 0)
    Where 结算方式 Is Null And 结帐id = 结算id_In;
    If Sql%NotFound Then
    
      Insert Into 病人预交记录
        (ID, 记录性质, NO, 记录状态, 病人id, 主页id, 摘要, 结算方式, 收款时间, 操作员编号, 操作员姓名, 冲预交, 结帐id, 缴款组id, 结算序号, 校对标志, 卡类别id, 结算卡序号, 卡号,
         交易流水号, 交易说明, 结算号码)
      Values
        (病人预交记录_Id.Nextval, 6, r_Balance.No, r_Balance.记录状态, r_Balance.病人id, Null, Null, Null, r_Balance.登记时间,
         r_Balance.操作员编号, r_Balance.操作员姓名, -1 * 误差金额_In, r_Balance.结算id, r_Balance.缴款组id, r_Balance.结算序号, 2, Null, Null,
         Null, Null, Null, Null);
    End If;
  End If;

  Select Sum(Nvl(冲预交, 0))
  Into n_未退合计
  From 病人预交记录
  Where 结算序号 = r_Balance.结算序号 And 结算方式 Is Null And Mod(记录性质, 10) <> 1;
  If Not 结算方式_In Is Null Then
    --各个收费结算 :格式为:"结算方式|结算金额|结算号码|结算摘要||.."
    v_当前结算 := 结算方式_In;
    v_结算方式 := Substr(v_当前结算, 1, Instr(v_当前结算, '|') - 1);
  
    v_当前结算 := Substr(v_当前结算, Instr(v_当前结算, '|') + 1);
    n_结算金额 := To_Number(Substr(v_当前结算, 1, Instr(v_当前结算, '|') - 1));
  
    v_当前结算 := Substr(v_当前结算, Instr(v_当前结算, '|') + 1);
    v_结算号码 := LTrim(Substr(v_当前结算, 1, Instr(v_当前结算, '|') - 1));
    v_结算摘要 := LTrim(Substr(v_当前结算, Instr(v_当前结算, '|') + 1));
  
    If Nvl(n_未退合计, 0) <> -1 * Nvl(n_结算金额, 0) Then
      v_Err_Msg := '当前退款合计(' || Nvl(n_结算金额, 0) || ')与未退合计(' || Nvl(n_未退合计, 0) || ')不区配!';
      Raise Err_Item;
    End If;
  
    For c_结算 In (Select 结帐id, 记录性质, 记录状态, NO, Sum(Nvl(冲预交, 0)) As 结算金额
                 From 病人预交记录
                 Where 结算序号 = r_Balance.结算序号 And 结算方式 Is Null And Mod(记录性质, 10) <> 1
                 Group By 结帐id, 记录性质, NO, 记录状态) Loop
      Insert Into 病人预交记录
        (ID, 记录性质, NO, 记录状态, 病人id, 主页id, 摘要, 结算方式, 收款时间, 操作员编号, 操作员姓名, 冲预交, 结帐id, 缴款组id, 结算序号, 校对标志, 卡类别id, 结算卡序号, 卡号,
         交易流水号, 交易说明, 结算号码)
      Values
        (病人预交记录_Id.Nextval, c_结算.记录性质, c_结算.No, c_结算.记录状态, r_Balance.病人id, Null, v_结算摘要, v_结算方式, r_Balance.登记时间,
         r_Balance.操作员编号, r_Balance.操作员姓名, c_结算.结算金额, c_结算.结帐id, r_Balance.缴款组id, r_Balance.结算序号, 2, 卡类别id_In, Null,
         卡号_In, 交易流水号_In, 交易说明_In, v_结算号码);
    
      Update 病人预交记录
      Set 冲预交 = Nvl(冲预交, 0) - Nvl(c_结算.结算金额, 0)
      Where 结算方式 Is Null And 结帐id = c_结算.结帐id;
    End Loop;
  End If;

  Select Sum(冲预交) Into n_冲预交 From 病人预交记录 Where 结帐id = r_Balance.结算序号;
  If Nvl(n_冲预交, 0) <> 0 Then
    v_Err_Msg := '医保结算数据与未退结算数据不匹配!';
    Raise Err_Item;
  End If;

  Delete From 病人预交记录
  Where 结算序号 = r_Balance.结算序号 And 结算方式 Is Null And Mod(记录性质, 10) <> 1 And Nvl(冲预交, 0) = 0;

  --1.更新异常状态
  Update 门诊费用记录
  Set 费用状态 = 0
  Where Nvl(费用状态, 0) = 1 And 结帐id In (Select Distinct 结帐id From 病人预交记录 Where 结算序号 = r_Balance.结算序号);

  Update 费用补充记录 Set 费用状态 = 0 Where 结算序号 = r_Balance.结算序号;
  If Sql%NotFound Then
    v_Err_Msg := '未找到医保补结算数据，可能补他人进行了退费或作废操作!';
    Raise Err_Item;
  End If;

  --2.更新校对标志
  Update 病人预交记录 Set 校对标志 = 0 Where 结算序号 = r_Balance.结算序号;

  --3.更新人员缴款数据
  For c_缴款 In (Select 结算方式, 操作员姓名, Sum(Nvl(a.冲预交, 0)) As 冲预交
               From 病人预交记录 A
               Where a.结算序号 = r_Balance.结算序号 And Mod(a.记录性质, 10) <> 1
               Group By 结算方式, 操作员姓名) Loop
    Update 人员缴款余额
    Set 余额 = Nvl(余额, 0) + Nvl(c_缴款.冲预交, 0)
    Where 收款员 = c_缴款.操作员姓名 And 性质 = 1 And 结算方式 = c_缴款.结算方式;
    If Sql%RowCount = 0 Then
      Insert Into 人员缴款余额
        (收款员, 结算方式, 性质, 余额)
      Values
        (c_缴款.操作员姓名, c_缴款.结算方式, 1, Nvl(c_缴款.冲预交, 0));
    End If;
  End Loop;

Exception
  When Err_Item Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Err_Msg || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_费用补充结算_完成退费;
/

--77834:李南春,2014-10-15,门诊收费二次结算
Create Or Replace Procedure Zl_费用补充记录_补结算作废
(
  No_In         In 费用补充记录.No%Type,
  冲销id_In     In 费用补充记录.结算id%Type,
  结算序号_In   In 费用补充记录.结算序号%Type,
  操作员编号_In In 费用补充记录.操作员编号%Type,
  操作员姓名_In In 费用补充记录.操作员姓名%Type,
  登记时间_In   In 费用补充记录.登记时间%Type := Null
) Is
  Err_Item Exception;
  v_Err_Msg  Varchar2(255);
  n_Date     Date;
  n_组id     财务缴款分组.Id%Type;
  n_结算id   费用补充记录.结算id%Type;
  n_费用状态 费用补充记录.费用状态%Type;
Begin
  n_组id := Zl_Get组id(操作员姓名_In);
  n_Date := 登记时间_In;
  If 登记时间_In Is Null Then
    n_Date := Sysdate;
  End If;

  v_Err_Msg := Null;
  Begin
    Select 结算id, 费用状态
    Into n_结算id, n_费用状态
    From 费用补充记录
    Where 记录性质 = 1 And NO = No_In And 记录状态 = 1 And Rownum < 2;
  Exception
    When Others Then
      v_Err_Msg := '未找到结算单号为' || No_In || '的结算记录,可能已经被他人作废或重新结算,不允许再继续操作！';
  End;

  If v_Err_Msg Is Not Null Then
    Raise Err_Item;
  End If;

  Update 费用补充记录 Set 记录状态 = 3 Where 结算id = n_结算id;

  Insert Into 费用补充记录
    (记录性质, NO, 记录状态, 实际票号, 结算id, 收费结帐id, 费用状态, 操作员编号, 操作员姓名, 登记时间, 缴款组id, 病人id, 结算序号, 附加标志)
    Select 记录性质, NO, 2, 实际票号, 冲销id_In As 结算id, 收费结帐id, 1 As 费用状态, 操作员编号_In As 操作员编号, 操作员姓名_In As 操作员姓名, n_Date As 登记时间,
           n_组id As 缴款组id, 病人id, 结算序号_In As 结算序号, 附加标志
    From 费用补充记录
    Where 结算id = n_结算id;

  Insert Into 病人预交记录
    (ID, 记录性质, NO, 记录状态, 病人id, 主页id, 摘要, 结算方式, 收款时间, 操作员编号, 操作员姓名, 冲预交, 结帐id, 缴款组id, 结算序号, 校对标志, 卡类别id, 结算卡序号, 卡号,
     交易流水号, 交易说明, 结算号码)
    Select 病人预交记录_Id.Nextval, 6, NO, 2, 病人id, 主页id, 摘要, 结算方式, n_Date As 收款时间, 操作员编号_In As 操作员编号, 操作员姓名_In As 操作员姓名,
           -1 * 冲预交, 冲销id_In As 结帐id, n_组id As 缴款组id, -1 * 冲销id_In As 结算序号, 2 As 校对标志, 卡类别id, 结算卡序号, 卡号, 交易流水号, 交易说明,
           结算号码
    From 病人预交记录
    Where 结帐id = n_结算id;

  Update 病人预交记录 Set 记录状态 = 3 Where 结帐id = n_结算id;

Exception
  When Err_Item Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Err_Msg || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_费用补充记录_补结算作废;
/

--79482:冉俊明,2014-11-11,医保参数“support门诊结算作废”=0时,挂号二次结算退费还可以退回个人账户

--77834:冉俊明,2014-10-23,门诊收费二次结算
--77834:李南春,2014-10-15,门诊收费二次结算
Create Or Replace Procedure Zl_费用补充记录_Delete
(
  No_In           In 费用补充记录.No%Type,
  冲销id_In       In 费用补充记录.结算id%Type,
  重结id_In       In 费用补充记录.结算id%Type,
  结算序号_In     In 费用补充记录.结算序号%Type,
  退费结帐ids_In  In Varchar2,
  操作员编号_In   In 费用补充记录.操作员编号%Type,
  操作员姓名_In   In 费用补充记录.操作员姓名%Type,
  登记时间_In     In 费用补充记录.登记时间%Type := Null,
  非原样退结算_In In Varchar2 := Null
) Is
  --退费结帐id_In:传入本次退费的结帐ID,多个用逗号分离(123,111...)
  Err_Item Exception;
  v_Err_Msg  Varchar2(255);
  d_Date     Date;
  n_组id     财务缴款分组.Id%Type;
  n_结算id   费用补充记录.结算id%Type;
  n_费用状态 费用补充记录.费用状态%Type;
  v_实际票号 费用补充记录.实际票号%Type;
  n_病人id   费用补充记录.病人id%Type;
  n_附加标志 费用补充记录.附加标志%Type;
  n_Count    Number(18);
  n_打印id   票据使用明细.Id%Type;
Begin
  n_组id := Zl_Get组id(操作员姓名_In);
  d_Date := 登记时间_In;
  If 登记时间_In Is Null Then
    d_Date := Sysdate;
  End If;

  v_Err_Msg := Null;
  Begin
    Select 结算id, 费用状态, 实际票号, 病人id, 附加标志
    Into n_结算id, n_费用状态, v_实际票号, n_病人id, n_附加标志
    From 费用补充记录
    Where 记录性质 = 1 And NO = No_In And 记录状态 = 1 And Rownum < 2 And Nvl(费用状态, 0) = 0;
  Exception
    When Others Then
      v_Err_Msg := '未找到结算单号为' || No_In || '的结算记录,可能已经被他人作废或重新结算,不允许再继续操作！';
  End;

  If v_Err_Msg Is Not Null Then
    Raise Err_Item;
  End If;

  Update 费用补充记录 Set 记录状态 = 3 Where 结算id = n_结算id;

  Insert Into 费用补充记录
    (记录性质, NO, 记录状态, 实际票号, 结算id, 收费结帐id, 费用状态, 操作员编号, 操作员姓名, 登记时间, 缴款组id, 病人id, 结算序号, 附加标志)
    Select 记录性质, NO, 2, 实际票号, 冲销id_In As 结算id, 收费结帐id, 1 As 费用状态, 操作员编号_In As 操作员编号, 操作员姓名_In As 操作员姓名, d_Date As 登记时间,
           n_组id As 缴款组id, 病人id, 结算序号_In As 结算序号, 附加标志
    From 费用补充记录
    Where 结算id = n_结算id;

  --先将医保退回,剩下部分,直接退款(退款界面)
  Insert Into 病人预交记录
    (ID, 记录性质, NO, 记录状态, 病人id, 主页id, 摘要, 结算方式, 收款时间, 操作员编号, 操作员姓名, 冲预交, 结帐id, 缴款组id, 结算序号, 校对标志, 卡类别id, 结算卡序号, 卡号,
     交易流水号, 交易说明, 结算号码)
    Select 病人预交记录_Id.Nextval, 6, NO, 2, 病人id, 主页id, 摘要, 结算方式, d_Date As 收款时间, 操作员编号_In As 操作员编号, 操作员姓名_In As 操作员姓名,
           -1 * 冲预交, 冲销id_In As 结帐id, n_组id As 缴款组id, 结算序号_In As 结算序号, 2 As 校对标志, 卡类别id, 结算卡序号, 卡号, 交易流水号, 交易说明, 结算号码
    From 病人预交记录 A
    Where 结帐id = n_结算id And Exists
     (Select 1
           From 结算方式
           Where a.结算方式 = 名称 And 性质 In (3, 4) And Instr(',' || 非原样退结算_In || ',', ',' || 名称 || ',') = 0) And
          Mod(a.记录性质, 10) <> 1;

  --插入病人预交记录.结算方式为空的记录
  Insert Into 病人预交记录
    (ID, 记录性质, NO, 记录状态, 病人id, 主页id, 摘要, 结算方式, 收款时间, 操作员编号, 操作员姓名, 冲预交, 结帐id, 缴款组id, 结算序号, 校对标志, 卡类别id, 结算卡序号, 卡号,
     交易流水号, 交易说明, 结算号码)
    Select 病人预交记录_Id.Nextval, 6, NO, 2, 病人id, 主页id, 摘要, Null, d_Date As 收款时间, 操作员编号_In As 操作员编号, 操作员姓名_In As 操作员姓名,
           -1 * 退款额, 冲销id_In As 结帐id, n_组id As 缴款组id, 结算序号_In As 结算序号, 1 As 校对标志, Null, Null, Null, Null, Null, Null
    From (Select NO, Max(病人id) As 病人id, Max(主页id) As 主页id, Max(摘要) As 摘要, Sum(冲预交) As 退款额
           From 病人预交记录
           Where 结帐id = 冲销id_In
           Group By NO) A;

  Update 病人预交记录 Set 记录状态 = 3 Where 结帐id = n_结算id;

  Select Count(*)
  Into n_Count
  From (Select a.No, a.记录性质, a.序号, a.收费细目id, Sum(Nvl(a.付数, 1) * a.数次) As 数量
         From 门诊费用记录 A,
              (Select Distinct m.记录性质, m.No
                From 费用补充记录 J, 门诊费用记录 M
                Where j.收费结帐id = m.结帐id And j.No = No_In And Mod(j.记录性质, 0) = 1) B
         Where a.记录性质 = b.记录性质 And a.No = b.No And a.价格父号 Is Null
         Group By a.No, a.记录性质, a.序号, a.收费细目id
         Having Sum(Nvl(a.付数, 1) * a.数次) <> 0);

  If Nvl(n_Count, 0) <> 0 Then
    --部分冲销,需要产生重收记录
    Insert Into 费用补充记录
      (记录性质, NO, 记录状态, 实际票号, 结算id, 收费结帐id, 费用状态, 操作员编号, 操作员姓名, 登记时间, 缴款组id, 病人id, 结算序号, 附加标志)
      Select 1 As 记录性质, No_In As NO, 1, v_实际票号 As 实际票号, 重结id_In As 结算id, 收费结帐id, 1 As 费用状态, 操作员编号_In As 操作员编号,
             操作员姓名_In As 操作员姓名, d_Date As 登记时间, n_组id As 缴款组id, n_病人id As 病人id, 结算序号_In As 结算序号, n_附加标志 As 附加标志
      From (Select Distinct 收费结帐id As 收费结帐id
             From 费用补充记录 J
             Where j.No = No_In And Mod(j.记录性质, 10) = 1
             Union
             Select Column_Value As 结帐id From Table(f_Num2list(退费结帐ids_In)));
  End If;
  --收回票据
  --获取单据最后一次的打印ID(可能是多张单据收费打印)
  Begin
    Select Max(ID) Into n_打印id From 票据打印内容 Where 数据性质 = 1 And NO = No_In;
  Exception
    When Others Then
      Null;
  End;

  --可能以前没有打印,无收回
  If n_打印id Is Not Null Then
    --a.多张单据循环调用时只能收回一次
    Select Count(*) Into n_Count From 票据使用明细 Where 票种 = 1 And 性质 = 2 And 打印id = n_打印id;
    If n_Count = 0 Then
      Insert Into 票据使用明细
        (ID, 票种, 号码, 性质, 原因, 领用id, 打印id, 使用时间, 使用人)
        Select 票据使用明细_Id.Nextval, 票种, 号码, 2, 2, 领用id, 打印id, d_Date, 操作员姓名_In
        From 票据使用明细
        Where 打印id = n_打印id And 票种 = 1 And 性质 = 1;
    Else
      --b.部分退费多次收回时,最后一次全退收回要排开已收回的
      Insert Into 票据使用明细
        (ID, 票种, 号码, 性质, 原因, 领用id, 打印id, 使用时间, 使用人)
        Select 票据使用明细_Id.Nextval, 票种, 号码, 2, 2, 领用id, 打印id, d_Date, 操作员姓名_In
        From 票据使用明细 A
        Where 打印id = n_打印id And 票种 = 1 And 性质 = 1 And Not Exists
         (Select 1 From 票据使用明细 B Where a.号码 = b.号码 And 打印id = n_打印id And 票种 = 1 And 性质 = 2);
    End If;
  End If;
Exception
  When Err_Item Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Err_Msg || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_费用补充记录_Delete;
/

--77834:李南春,2014-10-15,门诊收费二次结算
Create Or Replace Procedure Zl_补充结算票据_Insert
(
  No_In       Varchar2,
  票据号_In   票据使用明细.号码%Type,
  领用id_In   票据使用明细.领用id%Type,
  使用人_In   票据使用明细.使用人%Type,
  使用时间_In 票据使用明细.使用时间%Type,
  打印id_In   票据打印内容.Id%Type := 0,
  票据张数_In Number
) As
  --功能：处理门诊收费票据的发出 
  --参数： 
  --      NO_IN       =     收费的单据号,可能是多张单据同时收费。格式为：A0000001,A0000002,.... 
  --      票据号_IN   =     要使用的开始票据号。该票据号应该不为空，否则不用处理票据，也不能区分多张一起收费的单据。 
  --      领用ID_IN   =     严格控制票据时，为使用票据的领用批次。非严格控制时，为NULL。 
  --      打印ID_IN   =     当修改多单据中的一张时,为了便于整体重打,将该单据的打印内容填写为与退费单据相同,不单独新发出票据,由退费重打发出 
  --      票据张数_In =     实际所需的票据打印张数 

  --该游标用于票据范围判断 
  Cursor c_Fact Is
    Select * From 票据领用记录 Where ID = Nvl(领用id_In, 0);
  r_Factrow c_Fact%RowType;

  v_票据号     票据使用明细.号码%Type;
  v_当前票据号 票据使用明细.号码%Type;
  v_打印id     票据打印内容.Id%Type;

  v_当前号 门诊费用记录.No%Type;
  v_单据号 Varchar2(1000);

  v_Error Varchar2(255);
  Err_Custom Exception;
Begin
  If Nvl(打印id_In, 0) = 0 Then
    --无票据号时,不用处理票据 
    If 票据号_In Is Null Then
      Return;
    End If;
    Select 票据打印内容_Id.Nextval Into v_打印id From Dual;
  
    --生成单据的票据打印内容 
    v_单据号 := No_In || ',';
    While v_单据号 Is Not Null Loop
      v_当前号 := Substr(v_单据号, 1, Instr(v_单据号, ',') - 1);
      --票据打印内容 
      Insert Into 票据打印内容 (ID, 数据性质, NO) Values (v_打印id, 1, v_当前号);
      --门诊费用记录中填写开始票据号以便显示 
      Update 费用补充记录 Set 实际票号 = 票据号_In Where 记录性质 = 1 And NO = v_当前号 ;
      v_单据号 := Substr(v_单据号, Instr(v_单据号, ',') + 1);
    End Loop;
  
    --并发出票据 
    v_票据号 := 票据号_In;
    If Nvl(领用id_In, 0) <> 0 Then
      Open c_Fact;
      Fetch c_Fact
        Into r_Factrow;
      If c_Fact%RowCount = 0 Then
        v_Error := '无效的票据领用批次，无法完成补充结算票据分配操作。';
        Close c_Fact;
        Raise Err_Custom;
      Elsif Nvl(r_Factrow.剩余数量, 0) < 票据张数_In Then
        v_Error := '当前批次的剩余数量不足' || 票据张数_In || '张，无法完成补充结算票据分配操作。';
        Close c_Fact;
        Raise Err_Custom;
      End If;
    End If;
    For I In 1 .. 票据张数_In Loop
      --检查票据范围是否正确 
      If Nvl(领用id_In, 0) <> 0 Then
        If Not (Upper(v_票据号) >= Upper(r_Factrow.开始号码) And Upper(v_票据号) <= Upper(r_Factrow.终止号码) And
            Length(v_票据号) = Length(r_Factrow.终止号码)) Then
          v_Error := '该单据需要打印多张票据,但票据号"' || v_票据号 || '"超出票据领用的号码范围！';
          Close c_Fact;
          Raise Err_Custom;
        End If;
      End If;
    
      --发出票据 
      Insert Into 票据使用明细
        (ID, 票种, 号码, 性质, 原因, 领用id, 打印id, 使用人, 使用时间)
      Values
        (票据使用明细_Id.Nextval, 1, v_票据号, 1, 1, 领用id_In, v_打印id, 使用人_In, 使用时间_In);
    
      v_当前票据号 := v_票据号;
      --下一个票据号 
      v_票据号 := Zl_Incstr(v_票据号);
    End Loop;
  
    If Nvl(领用id_In, 0) <> 0 Then
      Update 票据领用记录
      Set 使用时间 = 使用时间_In, 当前号码 = v_当前票据号, 剩余数量 = Nvl(剩余数量, 0) - 票据张数_In
      Where ID = 领用id_In;
    
      Close c_Fact;
    End If;
  Else
    Insert Into 票据打印内容 (ID, 数据性质, NO) Values (打印id_In, 1, No_In);
  End If;
Exception
  When Err_Custom Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Error || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_补充结算票据_Insert;
/

--77834:李南春,2014-10-15,门诊收费二次结算
Create Or Replace Procedure Zl_补充结算票据_Reprint
(
  No_In         门诊费用记录.No%Type,
  票据号_In     票据使用明细.号码%Type,
  领用id_In     票据使用明细.领用id%Type,
  使用人_In     票据使用明细.使用人%Type,
  使用时间_In   票据使用明细.使用时间%Type,
  退费_In       Number := 0,
  票据张数_In   Number := 0,
  收回票据号_In Varchar2 := Null
) As
  --功能：用于重打收费票据 
  --参数： 
  --      NO_IN         =   要重打的单据号，可能是一起收费的多张单据中的一张。 
  --      票据号        =   用于重打的起始票据号.而该张收费单据可能使用多张票据。 
  --      领用ID        =   如果为0或NULL,表示不严格控制票据。 
  --      退费_IN       =   是否部份退费时调用该过程 
  --      收回票据号_In =   部分退费时,界面选择要收回的票据号,这些票据只收回,不重新发出,传入空表示全部收回并重打 
  --该游标用于范围判断 
  Cursor c_Fact Is
    Select 开始号码, 终止号码 From 票据领用记录 Where ID = Nvl(领用id_In, 0);
  r_Factrow c_Fact%RowType;

  v_收回id 票据打印内容.Id%Type;
  v_打印id 票据打印内容.Id%Type;
  v_票据号 票据使用明细.号码%Type;

  v_Error Varchar2(255);
  Err_Custom Exception;
Begin
  --获取该张单据的最后一次的的打印ID(可能没有) 
  Begin
    Select Max(ID) Into v_收回id From 票据打印内容 Where 数据性质 = 1 And NO = No_In;
  Exception
    When Others Then
      Null;
  End;

  --收回票据(可能以前未控制票据,无法收回) 
  If v_收回id Is Not Null Then
    If 收回票据号_In Is Null Then
      If Nvl(退费_In, 0) = 0 Then
        --a.重打 
        Insert Into 票据使用明细
          (ID, 票种, 号码, 性质, 原因, 领用id, 打印id, 使用人, 使用时间)
          Select 票据使用明细_Id.Nextval, 票种, 号码, 2, 4, 领用id, 打印id, 使用人_In, 使用时间_In
          From 票据使用明细
          Where 打印id = v_收回id And 票种 = 1 And 性质 = 1;
      Else
        --b.部分退费重打先收回 
        Insert Into 票据使用明细
          (ID, 票种, 号码, 性质, 原因, 领用id, 打印id, 使用人, 使用时间)
          Select 票据使用明细_Id.Nextval, 票种, 号码, 2, 2, 领用id, 打印id, 使用人_In, 使用时间_In
          From 票据使用明细 A
          Where 打印id = v_收回id And 票种 = 1 And 性质 = 1 And Not Exists
           (Select 1 From 票据使用明细 B Where a.号码 = b.号码 And 打印id = v_收回id And 票种 = 1 And 性质 = 2);
      End If;
    Else
      --c.部分退费收回指定的票据号 
      Insert Into 票据使用明细
        (ID, 票种, 号码, 性质, 原因, 领用id, 打印id, 使用人, 使用时间)
        Select 票据使用明细_Id.Nextval, 票种, 号码, 2, 2, 领用id, 打印id, 使用人_In, 使用时间_In
        From 票据使用明细
        Where 打印id = v_收回id And 票种 = 1 And 性质 = 1 And Instr(收回票据号_In, 号码) > 0;
      Return;
    End If;
  End If;

  --无票据号时,不用处理票据 
  If 票据号_In Is Null Then
    Return;
  End If;

  --重新发出票据并填写票据打印内容 
  Select 票据打印内容_Id.Nextval Into v_打印id From Dual;
  Insert Into 票据打印内容
    (ID, 数据性质, NO)
    Select Distinct v_打印id, 1, NO
    From (Select NO
           From 票据打印内容
           Where ID = v_收回id And 数据性质 = 1
           Union All
           Select No_In From Dual);

  --门诊费用记录中记录票据号以便显示 
  Update 费用补充记录
  Set 实际票号 = 票据号_In
  Where 记录性质 = 1 And NO In (Select NO
                            From 票据打印内容
                            Where ID = v_收回id And 数据性质 = 1
                            Union
                            Select No_In From Dual);

  v_票据号 := 票据号_In;
  If Nvl(领用id_In, 0) <> 0 Then
    Open c_Fact;
    Fetch c_Fact
      Into r_Factrow;
    If c_Fact%RowCount = 0 Then
      v_Error := '无效的票据领用批次,无法完成重打操作！';
      Close c_Fact;
      Raise Err_Custom;
    End If;
  End If;
  For I In 1 .. 票据张数_In Loop
    If Nvl(领用id_In, 0) <> 0 Then
      --检查票据范围 
      If Not (Upper(v_票据号) >= Upper(r_Factrow.开始号码) And Upper(v_票据号) <= Upper(r_Factrow.终止号码) And
          Length(v_票据号) = Length(r_Factrow.终止号码)) Then
        v_Error := '单据需要打印多张票据,但票据号"' || v_票据号 || '"超出票据领用的号码范围！';
        Close c_Fact;
        Raise Err_Custom;
      End If;
    End If;
  
    --发出票据:重打时,如果是部份退费重打,则处理为新发生；如果以前没有收回，也处理为新发出。 
    Insert Into 票据使用明细
      (ID, 票种, 号码, 性质, 原因, 领用id, 打印id, 使用人, 使用时间)
    Values
      (票据使用明细_Id.Nextval, 1, v_票据号, 1, Decode(Nvl(退费_In, 0), 0, Decode(v_收回id, Null, 1, 3), 1), 领用id_In, v_打印id, 使用人_In,
       使用时间_In);
  
    Update 票据领用记录
    Set 当前号码 = v_票据号, 剩余数量 = Decode(Sign(Nvl(剩余数量, 0) - 1), -1, 0, Nvl(剩余数量, 0) - 1)
    Where ID = Nvl(领用id_In, 0);
  
    v_票据号 := Zl_Incstr(v_票据号);
  End Loop;
  Update 票据领用记录 Set 使用时间 = 使用时间_In Where ID = 领用id_In;

  If Nvl(领用id_In, 0) <> 0 Then
    Close c_Fact;
  End If;
Exception
  When Err_Custom Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Error || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_补充结算票据_Reprint;
/

--79264:刘尔旋,2014-11-26,挂号记录新增医疗付款方式字段
--77834:冉俊明,2014-10-27,门诊收费二次结算
--77834:冉俊明,2014-10-27,门诊收费二次结算
--77834:冉俊明,2014-11-07,门诊收费二次结
--77834:李南春,2014-10-15,门诊收费二次结算
Create Or Replace Procedure Zl_病人挂号补结算_Delete
(
  单据号_In     门诊费用记录.No%Type,
  操作员编号_In 门诊费用记录.操作员编号%Type,
  操作员姓名_In 门诊费用记录.操作员姓名%Type,
  冲销id_In     门诊费用记录.结帐id%Type := Null,
  结算序号_In   病人预交记录.结算序号%Type := Null,
  退号时间_In   门诊费用记录.登记时间%Type := Null,
  删除门诊号_In Number := 0
) As
  --该游标用于判断是否单独收病历费,及挂号汇总表处理

  v_Err_Msg Varchar(255);
  Err_Item Exception;

  n_结帐id   病人预交记录.结帐id%Type;
  n_冲销id   门诊费用记录.结帐id%Type;
  n_结算序号 病人预交记录.结算序号%Type;

  n_病人id   病人信息.病人id%Type;
  n_退费金额 病人预交记录.冲预交%Type;
  n_挂号id   病人挂号记录.Id%Type;
  n_组id     财务缴款分组.Id%Type;

  n_分诊台签到排队 Number;
  n_挂号生成队列   Number;
  d_Date           Date;
  n_病人id1        病人信息.病人id%Type;
  d_Temp           Date;
Begin
  n_组id := Zl_Get组id(操作员姓名_In);
  --首先判断要退号/取消预约的记录是否存在

  Begin
    Select a.结帐id, a.病人id
    Into n_结帐id, n_病人id
    From 门诊费用记录 A
    Where a.记录性质 = 4 And a.No = 单据号_In And a.记录状态 = 1 And Rownum < 2;
  Exception
  
    When Others Then
      n_病人id := -1;
  End;
  If Nvl(n_病人id, 0) = -1 Then
    v_Err_Msg := '未找到指定的挂号单:' || 单据号_In || ',可能已经被人退号,不允许再次退号。';
    Raise Err_Item;
  End If;

  --2.挂号处理

  d_Date     := 退号时间_In;
  n_冲销id   := 冲销id_In;
  n_结算序号 := 结算序号_In;

  If d_Date Is Null Then
    d_Date := Sysdate;
  End If;
  If n_冲销id Is Null Then
    Select 病人结帐记录_Id.Nextval Into n_冲销id From Dual;
  End If;

  If n_结算序号 Is Null Then
    n_结算序号 := -1 * n_冲销id;
  End If;
  --更新挂号序号状态
  If Zl_To_Number(zl_GetSysParameter('已退序号允许挂号', 1111)) = 1 Then
    Delete 挂号序号状态
    Where 状态 = 1 And
          (号码, 序号, 日期) = (Select 号别, 号序, Trunc(发生时间) From 病人挂号记录 Where NO = 单据号_In And Rownum = 1) Or
          (号码, 序号, 日期) = (Select 号别, 号序, 发生时间 From 病人挂号记录 Where NO = 单据号_In And Rownum = 1);
  Else
    Update 挂号序号状态
    Set 状态 = 4
    Where 状态 = 1 And
          (号码, 序号, 日期) = (Select 号别, 号序, Trunc(发生时间) From 病人挂号记录 Where NO = 单据号_In And Rownum = 1) Or
          (号码, 序号, 日期) = (Select 号别, 号序, 发生时间 From 病人挂号记录 Where NO = 单据号_In And Rownum = 1);
  End If;

  --病人就诊状态
  If n_病人id Is Not Null Then
    Update 病人信息 Set 就诊状态 = 0, 就诊诊室 = Null Where 病人id = n_病人id;
    --删除门诊号相关处理,只有当只有一条挂号记录并且病人建档日期与挂号日期近似时才会处理(界面要根据提示来删除)
    If 删除门诊号_In = 1 Then
      Delete 门诊病案记录 Where 病人id = n_病人id;
      Update 病人信息 Set 门诊号 = Null Where 病人id = n_病人id;
      --费用记录包括挂号及病案、就诊卡费用,以及病人交费后退费或销帐的费用,挂号记录在最后处理
      Update 门诊费用记录 Set 标识号 = Null Where 门诊标志 = 1 And 病人id = n_病人id;
    End If;
  End If;

  --如果挂时收了就诊卡费,退费时清除就诊卡号,在非光退病历费时
  n_病人id1 := Null;
  Begin
    Select 病人id
    Into n_病人id1
    From 门诊费用记录
    Where 记录性质 = 4 And 记录状态 = 1 And NO = 单据号_In And 附加标志 = 2 And Rownum < 2;
  Exception
    When Others Then
      Null;
  End;
  If n_病人id1 Is Not Null Then
    Update 病人信息
    Set 就诊卡号 = Null, 卡验证码 = Null, Ic卡号 = Decode(Ic卡号, 就诊卡号, Null, Ic卡号)
    Where 病人id = n_病人id1;
  End If;

  --门诊费用记录
  --冲销记录
  Insert Into 门诊费用记录
    (ID, NO, 实际票号, 记录性质, 记录状态, 序号, 价格父号, 从属父号, 病人id, 病人科室id, 门诊标志, 标识号, 付款方式, 姓名, 性别, 年龄, 费别, 收费类别, 收费细目id, 计算单位, 付数,
     数次, 加班标志, 附加标志, 发药窗口, 收入项目id, 收据费目, 记帐费用, 标准单价, 应收金额, 实收金额, 开单部门id, 开单人, 执行部门id, 执行人, 操作员编号, 操作员姓名, 发生时间, 登记时间,
     结帐id, 结帐金额, 保险项目否, 保险大类id, 统筹金额, 摘要, 是否上传, 保险编码, 费用类型, 缴款组id, 费用状态, 执行状态)
    Select 病人费用记录_Id.Nextval, NO, 实际票号, 记录性质, 2, 序号, 价格父号, 从属父号, 病人id, 病人科室id, 门诊标志, 标识号, 付款方式, 姓名, 性别, 年龄, 费别, 收费类别,
           收费细目id, 计算单位, 付数, -数次, 加班标志, 附加标志, 发药窗口, 收入项目id, 收据费目, 记帐费用, 标准单价, -应收金额, -实收金额, 开单部门id, 开单人, 执行部门id, 执行人,
           操作员编号_In, 操作员姓名_In, 发生时间, d_Date, n_冲销id, -1 * 结帐金额, 保险项目否, 保险大类id, -1 * 统筹金额, 摘要 As 摘要, 附加标志, 保险编码, 费用类型,
           n_组id, 1, 1
    From 门诊费用记录
    Where 记录性质 = 4 And 记录状态 = 1 And NO = 单据号_In;

  Update 门诊费用记录 Set 记录状态 = 3 Where 记录性质 = 4 And 记录状态 = 1 And NO = 单据号_In;
  Select Sum(实收金额) Into n_退费金额 From 门诊费用记录 Where 结帐id = n_冲销id;
  Insert Into 病人预交记录
    (ID, NO, 实际票号, 记录性质, 记录状态, 病人id, 主页id, 科室id, 摘要, 结算方式, 收款时间, 操作员编号, 操作员姓名, 冲预交, 结帐id, 结算序号, 缴款组id, 预交类别, 卡类别id,
     结算卡序号, 卡号, 交易流水号, 交易说明, 合作单位, 校对标志)
    Select 病人预交记录_Id.Nextval, a.No, a.实际票号, a.记录性质, 2, a.病人id, a.主页id, a.科室id, a.摘要, Null, d_Date, 操作员编号_In, 操作员姓名_In,
           n_退费金额, n_冲销id, n_结算序号, n_组id, 预交类别, Null, Null, Null, Null, Null, Null, 1
    From 病人预交记录 A
    Where a.记录性质 = 4 And a.结帐id = n_结帐id And Rownum = 1;
  If Sql%NotFound Then
    v_Err_Msg := '未找到挂号单为【' || 单据号_In || '】的原始挂号记录!';
    Raise Err_Item;
  End If;
  Update 病人预交记录 Set 记录状态 = 3 Where 记录性质 = 4 And 记录状态 = 1 And 结帐id = n_结帐id;

  --病人挂号汇总
  For c_挂号 In (Select a.收费细目id, a.发生时间, a.登记时间, c.接收时间, c.执行部门id, c.执行人, m.Id As 医生id, Nvl(c.号别, b.号码) As 号码, a.结帐id,
                      a.病人id, Decode(c.预约, Null, 0, 0, 0, 1) As 预约
               From 门诊费用记录 A, 病人挂号记录 C, 挂号安排 B, 人员表 M
               Where a.记录性质 = 4 And a.结帐id = n_冲销id And a.从属父号 Is Null And c.执行人 = m.姓名(+) And a.No = c.No And
                     Nvl(c.号别, Nvl(a.计算单位, '-')) = b.号码 And Nvl(a.附加标志, 0) = 0 And Rownum < 2) Loop
    --退非挂号费用,则不处理汇总表数据
  
    If Nvl(c_挂号.预约, 0) <> 0 Then
      d_Temp := Trunc(c_挂号.接收时间);
    Else
      d_Temp := Trunc(c_挂号.发生时间);
    End If;
  
    Update 病人挂号汇总
    Set 已挂数 = Nvl(已挂数, 0) - 1, 其中已接收 = Nvl(其中已接收, 0) - Nvl(c_挂号.预约, 0), 已约数 = Nvl(已约数, 0) - Nvl(c_挂号.预约, 0)
    Where 日期 = d_Temp And 科室id = c_挂号.执行部门id And 项目id = c_挂号.收费细目id And Nvl(医生姓名, '医生') = Nvl(c_挂号.执行人, '医生') And
          Nvl(医生id, 0) = Nvl(c_挂号.医生id, 0) And (号码 = c_挂号.号码 Or 号码 Is Null);
  
    If Sql%RowCount = 0 Then
      Insert Into 病人挂号汇总
        (日期, 科室id, 项目id, 医生姓名, 医生id, 号码, 已挂数, 其中已接收, 已约数)
      Values
        (d_Temp, c_挂号.执行部门id, c_挂号.收费细目id, c_挂号.执行人, Decode(c_挂号.医生id, 0, Null, c_挂号.医生id), c_挂号.号码, -1,
         -1 * Nvl(c_挂号.预约, 0), -1 * Nvl(c_挂号.预约, 0));
    End If;
  End Loop;

  n_挂号生成队列 := Zl_To_Number(zl_GetSysParameter('排队叫号模式', 1113));
  If n_挂号生成队列 <> 0 Then
    n_分诊台签到排队 := Zl_To_Number(zl_GetSysParameter('分诊台签到排队', 1113));
    If Nvl(n_分诊台签到排队, 0) = 0 Then
      --要删除队列
      For v_挂号 In (Select ID, 诊室, 执行部门id, 执行人 From 病人挂号记录 Where NO = 单据号_In) Loop
        Zl_排队叫号队列_Delete(v_挂号.执行部门id, v_挂号.Id);
      End Loop;
    End If;
  End If;

  --医保产生的就诊登记记录
  Delete From 就诊登记记录
  Where (病人id, 主页id, 就诊时间) In (Select 病人id, 主页id, 发生时间 From 病人挂号记录 Where NO = 单据号_In);
  --病人挂号记录
  Select 病人挂号记录_Id.Nextval Into n_挂号id From Dual;

  Update 病人挂号记录 Set 记录状态 = 3 Where NO = 单据号_In And 记录性质 = 1 And 记录状态 = 1;
  If Sql%NotFound Then
    v_Err_Msg := '挂号单【' || 单据号_In || '】不存在或由于并发原因已经被退号';
    Raise Err_Item;
  End If;

  Insert Into 病人挂号记录
    (ID, NO, 记录性质, 记录状态, 病人id, 门诊号, 姓名, 性别, 年龄, 号别, 急诊, 诊室, 附加标志, 执行部门id, 执行人, 执行状态, 执行时间, 登记时间, 发生时间, 操作员编号, 操作员姓名, 复诊,
     号序, 社区, 预约, 摘要, 预约方式, 接收人, 接收时间, 交易流水号, 交易说明, 合作单位, 险类, 医疗付款方式)
    Select n_挂号id, NO, 记录性质, 2, 病人id, 门诊号, 姓名, 性别, 年龄, 号别, 急诊, 诊室, 附加标志, 执行部门id, 执行人, 执行状态, 执行时间, d_Date, 发生时间, 操作员编号_In,
           操作员姓名_In, 复诊, 号序, 社区, 预约, 摘要 As 摘要, 预约方式, 接收人, 接收时间, 交易流水号, 交易说明, 合作单位, 险类, 医疗付款方式
    From 病人挂号记录
    Where NO = 单据号_In And 记录状态 = 3;

Exception
  When Err_Item Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Err_Msg || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_病人挂号补结算_Delete;
/

--80176:刘尔旋,2014-12-04,收费轧帐暂存金
--77834:刘尔旋,2014-10-15,门诊二次结算修改
Create Or Replace Procedure Zl_收费员轧帐记录_Insert
(
  Id_In         In 人员收缴记录.Id%Type,
  No_In         In 人员收缴记录.No%Type,
  收款员_In     In 人员收缴记录.收款员%Type,
  收款部门id_In In 人员收缴记录.收款部门id%Type,
  冲预交款_In   In 人员收缴记录.冲预交款%Type,
  借入合计_In   In 人员收缴记录.借入合计%Type,
  借出合计_In   In 人员收缴记录.借出合计%Type,
  摘要_In       In 人员收缴记录.摘要%Type,
  开始时间_In   In 人员收缴记录.开始时间%Type,
  终止时间_In   In 人员收缴记录.终止时间%Type,
  登记人_In     In 人员收缴记录.登记人%Type,
  登记时间_In   In 人员收缴记录.登记时间%Type,
  收缴标志_In   In 人员收缴记录.收缴标志%Type,
  收费对照_In   In Varchar2,
  操作类别_In   In Integer := 0,
  暂存金_In     In 人员暂存记录.金额%Type := 0
) Is
  --------------------------------------------------------------------------------------------
  --功能:收费员轧帐记录写入
  --参数:记录性质_IN:
  --     收费对照_IN:性质1,记录ID1|性质2,记录ID2|...|性质n,记录IDn
  --     操作类别_In:0-保存轧帐记录和对照;1-只保存对照
  --------------------------------------------------------------------------------------------
  v_Err_Msg Varchar2(500);
  Err_Item Exception;

  n_组id     人员收缴记录.缴款组id%Type;
  n_Count    Number(18);
  n_暂存id   人员暂存记录.Id%Type;
  v_暂存单号 人员暂存记录.No%Type;
  v_现金     结算方式.名称%Type;

Begin
  --并发检查,是否当前时间范围内已经有轧帐记录
  If 操作类别_In = 0 Then
    Select Count(1)
    Into n_Count
    From 人员收缴记录
    Where 收款员 = 收款员_In And 开始时间 > 开始时间_In And 作废时间 Is Null And Rownum < 2 And 记录性质 = 1;
    If n_Count <> 0 Then
      v_Err_Msg := '收费员:"' || 收款员_In || '"在当前轧帐范围内已经被他人进行轧帐,不能再进行轧帐！';
      Raise Err_Item;
    End If;
    If 收费对照_In Is Null Then
      v_Err_Msg := '不存在什何轧帐记录,不能进行轧帐操作！';
      Raise Err_Item;
    End If;
  End If;
  --检查是否已存在轧帐明细
  For c_检查 In (Select /*+ rule*/
                a.性质, a.记录id
               From 人员收缴对照 A, Table(f_Str2list2(收费对照_In, '|', ',')) B, 人员收缴记录 M
               Where a.性质 = b.C1 And a.记录id = b.C2 And a.收缴id = m.Id And m.作废时间 Is Null And Rownum < 2) Loop
    --1-收费(含挂号),2-结帐,3-预交,4-借款;5-消费卡充值;6--消费卡面值;7-暂存金(本次增加)；８－收款或轧帐作废对照（本次增加）
    If c_检查.性质 = 1 Then
      Select Decode(记录性质, 1, '收费', 4, '挂号', '') || '单据为:' || NO || '的' || Decode(记录性质, 1, '收费', 4, '挂号', '') ||
              '记录已经被轧帐，不能再进行轧帐处理'
      Into v_Err_Msg
      From 门诊费用记录
      Where 结帐id = c_检查.记录id And Rownum < 2;
    End If;
    If c_检查.性质 = 2 Then
      Select '结帐单据为:' || NO || '的结帐记录已经被轧帐，不能再进行轧帐处理'
      Into v_Err_Msg
      From 病人结帐记录
      Where ID = c_检查.记录id And Rownum < 2;
    End If;
    If c_检查.性质 = 3 Then
      Select '预交单据为:' || NO || '的预交记录已经被轧帐，不能再进行轧帐处理'
      Into v_Err_Msg
      From 病人预交记录
      Where ID = c_检查.记录id And Rownum < 2;
    End If;
    If c_检查.性质 = 4 Then
      Select '收费员在' || To_Char(借出时间, 'yyyy-mm-dd hh24:mi:ss') || '的' || Decode(借款人, Null, '借出款', '借入款') ||
              '已经被轧帐，不能再进行轧帐处理'
      Into v_Err_Msg
      From 人员借款记录
      Where ID = c_检查.记录id And Rownum < 2;
    End If;
  
    If c_检查.性质 = 5 Then
      Select '收费员在' || To_Char(充值时间, 'yyyy-mm-dd hh24:mi:ss') || '的消费卡充值记录已经被轧帐，不能再进行轧帐处理'
      Into v_Err_Msg
      From 消费卡充值记录
      Where ID = c_检查.记录id And Rownum < 2;
    End If;
    If c_检查.性质 = 6 Then
      Select '收费员在发卡号为:' || 卡号 || '的且发卡时间为:' || To_Char(发卡时间, 'yyyy-mm-dd hh24:mi:ss') || '发卡记录已经被轧帐，不能再进行轧帐处理'
      Into v_Err_Msg
      From 消费卡目录
      Where ID = c_检查.记录id And Rownum < 2;
    End If;
    If c_检查.性质 = 7 Then
      Select '暂存单号为:' || NO || '的暂存记录已经被轧帐，不能再进行轧帐处理'
      Into v_Err_Msg
      From 人员暂存记录
      Where ID = c_检查.记录id And Rownum < 2;
    End If;
    If c_检查.性质 = 9 Then
      Select '单号为:' || NO || '的补充结算记录已经被轧帐，不能再进行轧帐处理'
      Into v_Err_Msg
      From 费用补充记录
      Where 结算id = c_检查.记录id And Rownum < 2;
    End If;
    Raise Err_Item;
  End Loop;
  If 操作类别_In = 0 Then
    n_组id := Zl_Get组id(收款员_In);
    Insert Into 人员收缴记录
      (ID, 记录性质, NO, 收款员, 收款部门id, 冲预交款, 借入合计, 借出合计, 摘要, 开始时间, 终止时间, 缴款组id, 登记人, 登记时间, 收缴标志)
    Values
      (Id_In, 1, No_In, 收款员_In, 收款部门id_In, 冲预交款_In, 借入合计_In, 借出合计_In, 摘要_In, 开始时间_In, 终止时间_In, n_组id, 登记人_In, 登记时间_In,
       收缴标志_In);
  
    Update 人员缴款余额 Set 上次轧帐时间 = 终止时间_In Where 收款员 = 收款员_In;
  End If;
  --插入收费对照
  Insert Into 人员收缴对照
    (收缴id, 性质, 记录id)
    Select Id_In, C1, C2 From Table(f_Str2list2(收费对照_In, '|', ','));
  If 暂存金_In <> 0 Then
    Begin
      Select 名称 Into v_现金 From 结算方式 Where 性质 = 1 And Rownum < 2;
    Exception
      When Others Then
        v_现金 := '现金';
    End;
    Select 人员暂存记录_Id.Nextval Into n_暂存id From Dual;
    v_暂存单号 := Nextno(141);
    Insert Into 人员暂存记录
      (ID, 收缴id, 记录性质, NO, 结算方式, 金额, 收款员, 登记人, 登记时间)
    Values
      (n_暂存id, Id_In, 2, v_暂存单号, v_现金, -1 * 暂存金_In, 收款员_In, 登记人_In, 登记时间_In);
  
    Insert Into 人员收缴对照 (收缴id, 性质, 记录id) Values (Id_In, 7, n_暂存id);
    Select 人员暂存记录_Id.Nextval Into n_暂存id From Dual;
    v_暂存单号 := Nextno(141);
    Insert Into 人员暂存记录
      (ID, 收缴id, 记录性质, NO, 结算方式, 金额, 收款员, 登记人, 登记时间)
    Values
      (n_暂存id, Id_In, 2, v_暂存单号, v_现金, 暂存金_In, 收款员_In, 登记人_In, 登记时间_In + 1 / 24 / 60 / 60);
  End If;
Exception
  When Err_Item Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Err_Msg || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_收费员轧帐记录_Insert;
/

--77834:刘兴洪,2014-10-15,增加保险补充结算
Create Or Replace Procedure Zl1_Datamove_Tag
(
  d_End    In Date,
  n_批次   In Number,
  n_System In Number
) As
  --功能：标记待转出的数据
  --说明：为避免Undo表空间膨胀过大，分段提交
Begin
  --1.经济核算（费用,药品,收款和票据等）

  --*****特殊处理遵义医院特殊数据:
  --病人ID为1的"医保病人",为2的"旧病人":不检查是否结清，不限制预交款未冲完的，强制转出
  Update /*+ rule*/ 病人预交记录 L
  Set 待转出 = n_批次
  Where 结帐id In
        (Select Distinct a.结帐id --1.门诊收费和挂号的收费结算记录(排除之后退号和退费的,一张单据中只要其中一行退了)
         From 门诊费用记录 A
         Where a.待转出 Is Null And a.登记时间 < d_End And a.记录性质 In (1, 4) And
               (a.记录状态 In (1, 2) Or a.记录状态 = 3 And Not Exists
                (Select 1
                 From 门诊费用记录 B
                 Where a.No = b.No And a.记录性质 = b.记录性质 And b.记录状态 = 2 And b.登记时间 >= d_End))
         Union All
         Select Distinct a.结算id --2.医保补结算
         From 费用补充记录 A
         Where a.待转出 Is Null And a.登记时间 < d_End And a.记录性质 = 1 And
               (a.记录状态 In (1, 2) Or a.记录状态 = 3 And Not Exists
                (Select 1
                 From 费用补充记录 B
                 Where a.No = b.No And a.记录性质 = b.记录性质 And b.记录状态 In (1, 2) And b.登记时间 >= d_End))
         Union All
         Select Distinct a.结帐id --3.就诊卡的收费结算记录(排除之后退卡费的,一张单据中只要其中一行退了)
         From 住院费用记录 A
         Where a.待转出 Is Null And a.登记时间 < d_End And a.记录性质 = 5 And a.记帐费用 = 0 And
               (a.记录状态 In (1, 2) Or a.记录状态 = 3 And Not Exists
                (Select 1
                 From 住院费用记录 B
                 Where a.No = b.No And a.记录性质 = b.记录性质 And b.记录状态 = 2 And b.登记时间 >= d_End))
         Union All --4.门诊(记帐单)和住院的结帐结算记录
         Select 结帐id
         From (With Settle As (Select Distinct a.Id As 结帐id, a.病人id --3.门诊(记帐单)和住院的结帐结算记录(排除之后结帐作废的)
                               From 病人结帐记录 A
                               Where a.待转出 Is Null And a.收费时间 < d_End And
                                     (a.记录状态 In (1, 2) Or a.记录状态 = 3 And Not Exists
                                      (Select 1
                                       From 病人结帐记录 B
                                       Where a.No = b.No And b.记录状态 = 2 And b.收费时间 >= d_End)))
                Select 结帐id
                From Settle
                Minus
                --1.一张预交款被多笔结帐冲完（结帐ID不同），这些结帐ID要整体排除,避免部分被转出后影响后续的计算是否冲完 
                --2.这些费用单据的结帐ID对应的可能还有其他NO的其他结帐ID(结帐作废后分多次结帐结清，可能部分在转出时间之后)，这些结帐ID要整体排除,避免部分被转出后影响后续的计算是否结清
                --考虑到这情况的复杂性，为简化逻辑，提升查询性能，按病人ID来排除
                Select Distinct d.Id
                From 病人结帐记录 D,
                     (Select Distinct c.病人id --多次住院可以一起结，以及门诊记帐和住院记帐可以一起结且冲同一笔预交，所以这里不加主页ID
                       From 住院费用记录 C,
                            (Select Distinct d.No, d.序号, Mod(d.记录性质, 10) As 记录性质
                              From 住院费用记录 D,
                                   (Select s.结帐id
                                     From Settle S, 病人结帐记录 E
                                     Where s.病人id = e.病人id And
                                           (e.收费时间 > d_End Or Exists (Select 1 From 在院病人 F Where s.病人id = f.病人id))) S --没有结清且之后没有再结过就成了呆帐，这种就不排除
                              Where d.结帐id = s.结帐id) D
                       Where c.No = d.No And Mod(c.记录性质, 10) = d.记录性质 And c.序号 = d.序号 --结帐后作废后，再对包含记帐单销帐的结帐ID为空的记录,一起汇总计算是否结清,这种结帐ID为空的数据转出在后面单独转出                                        
                       Group By c.No, Mod(c.记录性质, 10), c.病人id --一张单据中的一行可部分结帐，以单据为对象来判断，避免一张单据的其中一部分被转出
                       Having Nvl(Sum(c.实收金额), 0) <> Nvl(Sum(c.结帐金额), 0) Or Exists (Select 1 --排除转出时间之后再次结帐的(作废后再次结帐)，避免原始单据转走后，后续结帐时无法正确判断
                                                                                   From 住院费用记录 E, 病人结帐记录 S
                                                                                   Where e.No = c.No And
                                                                                         Mod(e.记录性质, 10) = Mod(c.记录性质, 10) And
                                                                                         e.记录性质 In (12, 13, 15) And
                                                                                         e.结帐id = s.Id And s.收费时间 >= d_End)
                       Union All
                       Select Distinct c.病人id
                       From 门诊费用记录 C,
                            (Select Distinct d.No, d.序号, Mod(d.记录性质, 10) As 记录性质
                              From 门诊费用记录 D, Settle S
                              Where d.结帐id = s.结帐id) D --因为是门诊病人，所以，只要没有结清,该病人的都不转出
                       Where c.No = d.No And Mod(c.记录性质, 10) = d.记录性质 And c.序号 = d.序号
                       Group By c.No, Mod(c.记录性质, 10), c.病人id
                       Having Nvl(Sum(c.实收金额), 0) <> Nvl(Sum(c.结帐金额), 0) Or Exists (Select 1
                                                                                    From 门诊费用记录 E, 病人结帐记录 S
                                                                                    Where e.No = c.No And
                                                                                          Mod(e.记录性质, 10) = Mod(c.记录性质, 10) And
                                                                                          e.记录性质 In (12, 13, 15) And
                                                                                          e.结帐id = s.Id And s.收费时间 >= d_End)) N
                Where d.病人id = n.病人id)
                
         
         );

  --排除预交款未冲完的和转出时间之后发药的记录
  --因为前面的SQL查出的结帐ID可能不全是冲预交的(门诊收费和住院结帐补费等)，所以，需要单独一个SQL来排除
  --由于可能存在数据异常(住院费用结帐冲预交类别为1的门诊预交)，所以没有加预交类别条件限定
  Update /*+ rule*/ 病人预交记录
  Set 待转出 = Null
  Where 待转出 = n_批次 And
        结帐id In
        (Select Distinct d.结帐id
         From 病人预交记录 D,
              --连接D表是为了查冲同一预交单据的其他结帐ID（退预交款，冲预交作废的，再次冲同一预交单据）
              --该病人的所有结帐ID的都不转出，避免部分冲预交的结帐ID被排除后，原始预交单被转走，或者其他结帐ID将费用单据的一部分(原始结帐、结帐作废、再次结一部分、再次结全部)转走
              (Select Distinct l.病人id
                From 病人预交记录 L, 病人预交记录 P --可能本次结帐冲的只是剩余款，所以需要连接L表，查原始交预交的单据，以及记录性质为11的可能还有转出时间之后其他冲剩余款的结帐ID
                Where l.记录性质 In (1, 11) And l.No = p.No And p.记录性质 In (1, 11) And p.待转出 = n_批次
                Group By l.No, l.病人id
                Having Nvl(Sum(l.金额), 0) <> Nvl(Sum(l.冲预交), 0) And (Exists (Select 1
                                                                           From 病人预交记录 E
                                                                           Where l.病人id = e.病人id And e.收款时间 > d_End) Or Exists (Select 1
                                                                                                                               From 在院病人 E
                                                                                                                               Where l.病人id =
                                                                                                                                     e.病人id)) --没有冲完且之后没有再冲过或结算过就成了呆帐（存在用负的结帐补款来表示冲预交当成冲完的清况），这种就不排除 
                Or Nvl(Sum(l.金额), 0) = Nvl(Sum(l.冲预交), 0) And (Exists (Select 1
                                                                      From 病人预交记录 E, 病人结帐记录 F --排除转出时间之后的其他结帐ID冲的
                                                                      Where e.No = l.No And e.记录性质 = 11 And e.结帐id = f.Id And --冲预交时的收款时间填的是交预交款的时间，所以这里需要用其他表的时间
                                                                            f.收费时间 >= d_End) Or Exists (Select 1
                                                                                                       From 病人预交记录 E,
                                                                                                            门诊费用记录 F
                                                                                                       Where e.No = l.No And
                                                                                                             e.记录性质 = 11 And
                                                                                                             e.结帐id =
                                                                                                             f.结帐id And
                                                                                                             f.登记时间 >=
                                                                                                             d_End And
                                                                                                             f.记录性质 In
                                                                                                             (1, 4) And
                                                                                                             Nvl(f.记帐费用, 0) <> 1) Or Exists (Select 1
                                                                                                                                            From 病人预交记录 E,
                                                                                                                                                 住院费用记录 F
                                                                                                                                            Where e.No = l.No And
                                                                                                                                                  e.记录性质 = 11 And
                                                                                                                                                  e.结帐id =
                                                                                                                                                  f.结帐id And
                                                                                                                                                  f.登记时间 >=
                                                                                                                                                  d_End And
                                                                                                                                                  f.记录性质 In (5,
                                                                                                                                                             15) And
                                                                                                                                                  Nvl(f.记帐费用,
                                                                                                                                                      0) <> 1))) N
         
         Where d.病人id = n.病人id);

  --为了降低逻辑的复杂性，不排除在转出时间之后发药或未发药的费用记录对应的结帐ID，将这种情况的结算数据和费用数据强制转走 

  --预交款没有使用就直接退了的记录(结帐ID为空)
  Update /*+ rule*/ 病人预交记录
  Set 待转出 = n_批次
  Where 记录性质 = 1 And
        NO In (Select a.No
               From 病人预交记录 A
               Where a.结帐id Is Null And a.记录性质 = 1 And a.记录状态 In (2, 3) And a.待转出 Is Null And a.收款时间 < d_End
               Group By a.No
               Having Sum(a.金额) = 0);

  --冲预交款作废的记录（记录性质为2），没有结帐ID
  Update /*+ rule*/ 病人预交记录
  Set 待转出 = n_批次
  Where 结帐id Is Null And 记录性质 = 2 And NO In (Select a.No From 病人预交记录 A Where a.待转出 = n_批次 And a.记录性质 = 3);

  Update Zldatamovelog
  Set 当前进度 = '(1/10)结算数据标记完成，正在标记费用数据'
  Where 系统 = n_System And 批次 = n_批次;
  Commit;

  Update /*+ rule*/ 病人结帐记录
  Set 待转出 = n_批次
  Where ID In (Select 结帐id From 病人预交记录 Where 待转出 = n_批次);

  --结帐无结算的记录(为了提升性能，不判断费用，只要结了帐且无预交记录就当成是零费用结帐)
  Update /*+ rule*/ 病人结帐记录 L
  Set 待转出 = n_批次
  Where 收费时间 < d_End And 待转出 Is Null And Not Exists (Select 1 From 病人预交记录 P Where l.Id = p.结帐id);

  Update /*+ rule*/ 病人卡结算对照
  Set 待转出 = n_批次
  Where 预交id In (Select a.Id From 病人预交记录 A Where 待转出 = n_批次);

  Update /*+ rule*/ 病人卡结算记录
  Set 待转出 = n_批次
  Where ID In (Select 卡结算id From 病人卡结算对照 Where 待转出 = n_批次);

  Update /*+ rule*/ 三方结算交易
  Set 待转出 = n_批次
  Where 交易id In (Select a.Id From 病人预交记录 A Where 待转出 = n_批次);

  --挂号打折后实收金额为0的(没有对应的预交记录),即使之后有退号费用也不管，因为金额为零不影响计算),而卡费即使为零也有预交记录                 
  --根据挂号记录再找门诊费用，比直接按时间查门诊费用要快
  Update /*+ rule*/ 门诊费用记录
  Set 待转出 = n_批次
  Where NO In (Select NO From 病人挂号记录 Where 待转出 Is Null And 登记时间 < d_End) And 记录性质 = 4 And 实收金额 = 0;

  --没有结帐的已冲销的记帐单或打折后实收金额为零的，且没有其他记帐单的强制转出
  Update /*+ rule*/ 门诊费用记录
  Set 待转出 = n_批次
  Where 记录性质 = 2 And
        NO In
        (Select NO
         From (Select b.挂号单, a.No, a.序号, Sum(a.实收金额)
                From 门诊费用记录 A, 病人医嘱记录 B
                Where a.医嘱序号 = b.Id And a.结帐id Is Null And a.记录性质 = 2 And b.病人来源 <> 4 And a.待转出 Is Null And a.登记时间 < d_End
                Group By a.No, a.序号, b.挂号单
                Having Sum(a.实收金额) = 0 And Not Exists (Select 1
                                                      From 门诊费用记录 C, 病人医嘱记录 D
                                                      Where b.挂号单 = d.挂号单 And d.Id = c.医嘱序号 And d.病人来源 <> 4 And c.记录性质 = 2 And
                                                            c.待转出 Is Null
                                                      Group By c.No, c.序号
                                                      Having Sum(a.实收金额) > 0)));

  --直接收费的和结帐无结算（预交）记录的，Union不加all去掉重复以减少in的数量
  Update /*+ rule*/ 门诊费用记录
  Set 待转出 = n_批次
  Where 结帐id In (Select 结帐id
                 From 病人预交记录
                 Where 待转出 = n_批次
                 Union
                 Select ID From 病人结帐记录 Where 待转出 = n_批次);

  Update /*+ rule*/ 费用补充记录
  Set 待转出 = n_批次
  Where 结算id In (Select 结帐id From 病人预交记录 Where 待转出 = n_批次);

  Update /*+ rule*/ 凭条打印记录
  Set 待转出 = n_批次
  Where (NO, 记录性质) In (Select NO, 记录性质 From 门诊费用记录 Where 待转出 = n_批次);

  --从预交记录读是为了取就诊卡直接收费的（无结帐ID）,再加结帐记录是为了取结帐无结算（预交）记录的
  Update /*+ rule*/ 住院费用记录
  Set 待转出 = n_批次
  Where 结帐id In (Select 结帐id
                 From 病人预交记录
                 Where 待转出 = n_批次
                 Union
                 Select ID From 病人结帐记录 Where 待转出 = n_批次);

  --1.转出结帐作废后，记帐单销帐的记录（记帐状态为2且没有结帐ID且(记录状态为3的有结帐ID的)在最前面已转出）
  --2.未结帐的零费用(已冲销的记帐单)
  --3.没有结帐ID的划价记录处理为转出
  --4.不收费也没有冲预交的零费用处理为转出
  --加条件"待转出 Is Null"是为了处理连续多次标记转出的情况
  Update /*+ rule*/ 门诊费用记录 A
  Set 待转出 = n_批次
  Where ((Exists (Select 1
                  From 门诊费用记录 B
                  Where a.No = b.No And a.记录性质 = b.记录性质 And a.序号 = b.序号 And b.记录状态 = 3 And b.结帐id Is Not Null And
                        b.待转出 + 0 = n_批次) And 记录状态 = 2 Or Exists
         (Select 1
           From 门诊费用记录 B
           Where a.No = b.No And a.记录性质 = b.记录性质 And a.序号 = b.序号
           Group By b.No, b.记录性质, b.序号
           Having Nvl(Sum(b.实收金额), 0) = 0)) And 记录性质 = 2 Or 记录状态 = 0 Or 记录性质 = 1 And 实收金额 = 0 And 结帐金额 = 0) And
        结帐id Is Null And 待转出 Is Null And 登记时间 < d_End;

  --1.转出结帐作废后，记帐单销帐的记录（记帐状态为2且没有结帐ID且(记录状态为3的有结帐ID的)在最前面已转出）
  --2.未结帐的零费用(已冲销的记帐单)
  --3.没有结帐ID的划价记录处理为转出
  Update /*+ rule*/ 住院费用记录 A
  Set 待转出 = n_批次
  Where ((Exists (Select 1
                  From 住院费用记录 B
                  Where a.No = b.No And a.记录性质 = b.记录性质 And a.序号 = b.序号 And b.记录状态 = 3 And b.结帐id Is Not Null And
                        b.待转出 + 0 = n_批次) And 记录状态 = 2 Or Exists
         (Select 1
           From 住院费用记录 B
           Where a.No = b.No And a.记录性质 = b.记录性质 And a.序号 = b.序号
           Group By b.No, b.记录性质, b.序号
           Having Nvl(Sum(b.实收金额), 0) = 0)) And 记录性质 = 2 Or 记录状态 = 0) And 结帐id Is Null And 待转出 Is Null And 登记时间 < d_End;

  --由于存在赖帐病人离院未结的情况，对于很久以前的这些数据，如果预交已冲完，则处理为要转出
  Update /*+ rule*/ 住院费用记录 A
  Set 待转出 = n_批次
  Where 待转出 Is Null And 结帐id Is Null And
        (病人id, 主页id) In (Select 病人id, 主页id
                         From 病案主页 C
                         Where 出院日期 < d_End And 待转出 Is Null And 数据转出 Is Null And Not Exists
                          (Select 1
                                From 病人预交记录 B
                                Where b.病人id = c.病人id And b.预交类别 = 2 And b.记录性质 In (1, 11) Having
                                 Nvl(Sum(b.金额), 0) - Nvl(Sum(b.冲预交), 0) <> 0));

  Update Zldatamovelog
  Set 当前进度 = '(2/10)费用数据标记完成，正在标记药品数据'
  Where 系统 = n_System And 批次 = n_批次;
  Commit;

  Update /*+ Rule*/ 药品收发记录 A
  Set 待转出 = n_批次
  Where Rowid In (Select m.Rowid
                  From 药品收发记录 M, 门诊费用记录 E
                  Where m.费用id = e.Id And (e.记录性质 = 1 And m.单据 In (8, 24) Or e.记录性质 = 2 And m.单据 In (9, 25)) And
                        e.收费类别 In ('4', '5', '6', '7') And e.待转出 = n_批次
                  Union All
                  Select m.Rowid
                  From 药品收发记录 M, 住院费用记录 E
                  Where m.费用id = e.Id And m.单据 In (9, 10, 25, 26) And e.记录性质 = 2 And e.收费类别 In ('4', '5', '6', '7') And
                        e.待转出 = n_批次);

  Update /*+ rule*/ 收发记录补充信息
  Set 待转出 = n_批次
  Where 收发id In (Select ID From 药品收发记录 Where 待转出 = n_批次);
  Update /*+ rule*/ 输液配药内容
  Set 待转出 = n_批次
  Where 收发id In (Select ID From 药品收发记录 Where 待转出 = n_批次);

  Update /*+ rule*/ 输液配药记录
  Set 待转出 = n_批次
  Where ID In (Select 记录id From 输液配药内容 Where 待转出 = n_批次);

  Update /*+ rule*/ 输液配药附费
  Set 待转出 = n_批次
  Where 配药id In (Select ID From 输液配药记录 Where 待转出 = n_批次);

  Update /*+ rule*/ 输液配药状态
  Set 待转出 = n_批次
  Where 配药id In (Select ID From 输液配药记录 Where 待转出 = n_批次);

  Update /*+ rule*/ 药品留存计划
  Set 待转出 = n_批次
  Where 留存id In (Select ID From 药品收发记录 Where 待转出 = n_批次);

  Update /*+ rule*/ 药品签名明细
  Set 待转出 = n_批次
  Where 收发id In (Select ID From 药品收发记录 Where 待转出 = n_批次);

  Update /*+ rule*/ 药品签名记录
  Set 待转出 = n_批次
  Where ID In (Select 签名id From 药品签名明细 Where 待转出 = n_批次);

  Update Zldatamovelog
  Set 当前进度 = '(3/10)药品数据标记完成，正在标记缴款与票据数据'
  Where 系统 = n_System And 批次 = n_批次;
  Commit;

  Update /*+ rule*/ 人员借款记录 Set 待转出 = n_批次 Where 待转出 Is Null And 借出时间 < d_End;

  Update /*+ rule*/ 人员收缴记录 Set 待转出 = n_批次 Where 待转出 Is Null And 登记时间 < d_End;

  Update /*+ rule*/ 人员收缴对照
  Set 待转出 = n_批次
  Where 收缴id In (Select ID From 人员收缴记录 Where 待转出 = n_批次);

  Update /*+ rule*/ 人员收缴明细
  Set 待转出 = n_批次
  Where 收缴id In (Select ID From 人员收缴记录 Where 待转出 = n_批次);

  Update /*+ rule*/ 人员收缴票据
  Set 待转出 = n_批次
  Where 收缴id In (Select ID From 人员收缴记录 Where 待转出 = n_批次);

  Update /*+ rule*/ 人员暂存记录
  Set 待转出 = n_批次
  Where 收缴id In (Select ID From 人员收缴记录 Where 待转出 = n_批次);

  Update /*+ rule*/ 人员暂存记录 Set 待转出 = n_批次 Where 待转出 Is Null And 记录性质 = 1 And 登记时间 < d_End;

  Update /*+ rule*/ 票据领用记录 A
  Set 待转出 = n_批次
  Where Not Exists
   (Select 1 From 票据使用明细 B Where b.领用id = a.Id And b.使用时间 >= d_End) And 待转出 Is Null And 剩余数量 = 0 And 登记时间 < d_End;

  Update /*+ rule*/ 票据使用明细
  Set 待转出 = n_批次
  Where 领用id In (Select ID From 票据领用记录 Where 待转出 = n_批次);
  Update /*+ rule*/ 票据打印内容
  Set 待转出 = n_批次
  Where ID In (Select 打印id From 票据使用明细 Where 待转出 = n_批次);

  Update /*+ rule*/ 票据打印明细
  Set 待转出 = n_批次
  Where 使用id In (Select ID From 票据使用明细 Where 待转出 = n_批次);

  Update Zldatamovelog
  Set 当前进度 = '(4/10)缴款与票据数据标记完成，正在标记就诊及诊治数据'
  Where 系统 = n_System And 批次 = n_批次;
  Commit;

  --2.就诊及诊治数据
  --不转出的条件：挂号费用未转出的，转出时间之后存在医嘱，医嘱对应的费用未转出的
  --即使正在就诊(r.执行状态 <> 2 )的也强制转出
  Update /*+ rule*/ 病人挂号记录 T
  Set 待转出 = n_批次
  Where Rowid In
        (Select Rowid
         From 病人挂号记录 R
         Where Not Exists (Select 1
                From 门诊费用记录 A
                Where r.No = a.No And a.登记时间 < d_End And a.记录性质 = 4 And a.待转出 Is Null) And Not Exists
          (Select 1
                From 病人医嘱记录 A
                Where a.挂号单 = r.No And a.病人来源 <> 4 And Nvl(a.停嘱时间, a.开嘱时间) >= d_End) And Not Exists
          (Select 1
                From 门诊费用记录 E, 病人医嘱记录 A
                Where r.No = a.挂号单 And a.Id = e.医嘱序号 And a.病人来源 <> 4 And e.待转出 Is Null) And r.待转出 Is Null And
               r.登记时间 < d_End);

  --由于有一部分挂号数据未转出，所以，汇总表的数据可能与挂号数据不匹配
  Update 病人挂号汇总 Set 待转出 = n_批次 Where 待转出 Is Null And 日期 < d_End;
  Update /*+ rule*/ 病人转诊记录 Set 待转出 = n_批次 Where NO In (Select NO From 病人挂号记录 Where 待转出 = n_批次);

  --通过"住院费用记录"来查询，而不是"病人结帐记录",因为离院未结的赖帐病人也转出了费用
  --出院日期条件仍然需要，因为可能某次结帐转出了，但病人当时并未出院(一次住院多次结帐)。
  --通过指定索引方式进行特殊优化（缺省采用"病案主页IX_出院日期"索引的效率太低）
  Update /*+ rule*/ 病案主页 P
  Set 待转出 = n_批次
  Where Not Exists (Select 1 From 住院费用记录 A Where a.病人id = p.病人id And a.主页id = p.主页id And a.待转出 Is Null) And 待转出 Is Null And
