  --11、更新RIS分科室启用参数
  Procedure RIS启用控制_Update(检查类型_In RIS启用控制.检查类型%Type,
                           场合_In     RIS启用控制.场合%Type,
                           部门ids_In  Varchar2,
                           启用类型_In Number) Is
  
    l_部门id   t_Numlist := t_Numlist();
    v_启用RIS  RIS启用控制.是否启用RIS%Type;
    v_启用预约 RIS启用控制.是否启用预约%Type;
  
    Cursor c_Dept(Dept_In Varchar2) Is
      Select Column_Value From Table(f_Num2list(Dept_In));
  Begin
  
    if 启用类型_In = 1 then
      v_启用RIS  := 1;
      v_启用预约 := null;
      Delete From RIS启用控制
       Where 检查类型 = 检查类型_In
         and 场合 = 场合_In
         and 是否启用RIS = 1;
    else
      v_启用RIS  := null;
      v_启用预约 := 1;
      Delete From RIS启用控制
       Where 检查类型 = 检查类型_In
         and 场合 = 场合_In
         and 是否启用预约 = 1;
    end if;
  
    if 部门ids_In is null then
      Insert Into RIS启用控制
        (ID, 检查类型, 场合, 部门id, 是否启用RIS, 是否启用预约)
      Values
        (ris启用控制_id.nextval,
         检查类型_In,
         场合_In,
         null,
         v_启用RIS,
         v_启用预约);
    else
      Open c_Dept(部门ids_In);
      Fetch c_Dept Bulk Collect
        Into l_部门id;
      Close c_Dept;
    
      Forall I In 1 .. l_部门id.Count
        Insert Into RIS启用控制
          (ID, 检查类型, 场合, 部门id, 是否启用RIS, 是否启用预约)
        Values
          (ris启用控制_id.nextval,
           检查类型_In,
           场合_In,
           l_部门id(I),
           v_启用RIS,
           v_启用预约);
    end if;
  
  Exception
    When Others Then
      Zl_Errorcenter(Sqlcode, Sqlerrm);
  End RIS启用控制_Update;

