--110594:余伟节,2017-06-27,病案主页新增入科时间
Begin
  For r_Info In (Select a.病人id, a.主页id, Max(a.开始时间) As 入科时间
		From 病人变动记录 A, 病人信息 B
		Where b.病人id = a.病人id And b.主页id = a.主页id And a.开始原因 In (1, 2) And b.在院 = 1
		Group By a.病人id, a.主页id
		) Loop
    Update 病案主页 Set 入科时间 = r_Info.入科时间 Where 病人id = r_Info.病人id And 主页id = r_Info.主页id;
  End Loop;
End;
/


