Insert Into zlRPTSQLs(源ID,行号,内容)  Select zlRPTDatas_ID.CurrVal,a.* From (
  Select 1,'Select 标志, 材料id, 编码, 名称, 规格, 单位, 期初数量, 期初金额, 期初金额-期初差价 as 期初成本, ' From Dual Union All
  Select 2,'       入库数量, 入库金额, 入库金额 / Decode(入库数量, Null, 1, 0, 1, 入库数量) as 入库售价, (入库金额-入库差价)/ Decode(入库数量, Null, 1, 0, 1, 入库数量) as 入库成本价, 入库金额-入库差价 as 入库成本, ' From Dual Union All
  Select 3,'       调价金额, 调价金额-调价差价 as 调价成本, ' From Dual Union All
  Select 4,'       出库数量, 出库金额, 出库金额 / Decode(出库数量, Null, 1, 0, 1, 出库数量) as 出库售价, (出库金额-出库差价)/ Decode(出库数量, Null, 1, 0, 1, 出库数量) as 出库成本价, 出库金额-出库差价 as 出库成本, ' From Dual Union All
  Select 5,'       期末数量, 期末金额, 期末金额-期末差价 as 期末成本,''0'' 药品id' From Dual Union All
  Select 6,'From (Select Distinct Decode(To_Char(b.撤档时间, ''yyyy-MM-dd''), Null, '''', ''3000-01-01'', '''', ''√'') 标志, b.Id As 材料id, b.编码 As 编码,' From Dual Union All
  Select 7,'                       x.名称 As 名称, b.规格 As 规格, Decode([1], 2, d.包装单位, b.计算单位) As 单位,' From Dual Union All
  Select 8,'                       c.期初数量 / Decode([1], 2, d.换算系数, 1) As 期初数量, c.期初金额, c.期初差价,' From Dual Union All
  Select 9,'                       c.入库数量 / Decode([1], 2, d.换算系数, 1) As 入库数量, c.入库金额, c.入库差价,' From Dual Union All
  Select 10,'                       c.调价金额, c.调价差价, c.出库数量 / Decode([1], 2, d.换算系数, 1) As 出库数量,' From Dual Union All
  Select 11,'                       c.出库金额, c.出库差价, c.期末数量 / Decode([1], 2, d.换算系数, 1) As 期末数量,' From Dual Union All
  Select 12,'                       c.期末金额, c.期末差价' From Dual Union All
  Select 13,'       From (Select 材料id, Sum(期初数量) As 期初数量, Sum(期初金额) As 期初金额, Sum(期初差价) As 期初差价, Sum(入库数量) As 入库数量, Sum(入库金额) As 入库金额,' From Dual Union All
  Select 14,'                     Sum(入库差价) As 入库差价, Sum(调价金额) As 调价金额, Sum(调价差价) As 调价差价, Sum(出库数量) As 出库数量, Sum(出库金额) As 出库金额,' From Dual Union All
  Select 15,'                     Sum(出库差价) As 出库差价, Sum(期末数量) As 期末数量, Sum(期末金额) As 期末金额, Sum(期末差价) As 期末差价' From Dual Union All
  Select 16,'              From (Select 材料id, 期初数量, 期初金额, 期初差价, 0 As 入库数量, 0 As 入库金额, 0 As 入库差价, 0 As 调价金额, 0 As 调价差价, 0 As 出库数量,' From Dual Union All
  Select 17,'                            0 As 出库金额, 0 As 出库差价, 期末数量, 期末金额, 期末差价' From Dual Union All
  Select 18,'                     From 材料结存明细' From Dual Union All
