  Select 1,'Select A.业务大类, A.业务分类, A.库房id, A.材料id, ' From Dual Union All
  Select 2,'       Sum(A.金额) As 零售金额, Sum(A.金额)-Sum(A.差价) As 成本金额' From Dual Union All
  Select 3,'From (Select A.库房id, ''1-期初'' As 业务大类, '''' As 业务分类, A.材料id As 材料id,  Sum(A.期初金额) As 金额, Sum(A.期初差价) As 差价' From Dual Union All
  Select 4,'       From 材料结存明细 A' From Dual Union All
  Select 5,'       Where 结存id = [0]' From Dual Union All
  Select 6,'       Group By A.库房id, A.材料id' From Dual Union All
  Select 7,'     Union All' From Dual Union All
