    Else
      Delete From 病案主页从表 Where 病人id = 病人id_In And 主页id = v_主页id And 信息名 = '医保号';
    End If;
  End If;
Exception
  When Err_Custom Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Error || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_病人信息_Update;
