    End If;
  
    Select Count(1)
    Into v_Count
    From 病人护理文件 a, 病人护理数据 b
    Where a.Id = b.文件id And 病人id = 病人id_In And 主页id = 主页id_In And 科室id = r_Curlogrow.科室id And Rownum < 2;
    If v_Count > 0 Then
