                    Where 病人id = 病人id_In And 主页id = 主页id_In And Mod(记录性质, 10) = 3 And 登记时间 >= v_开始时间
                    Group By No, 序号, Mod(记录性质, 10)
                    Having Sum(结帐金额) <> 0) Loop
